/**
 * Helper function to parse MM-DD or YYYY-MM-DD into a Date object for a given year.
 * If dateString is MM-DD, it uses the provided year or current year.
 * If dateString is YYYY-MM-DD, it uses that year.
 * Returns null if parsing fails or date is invalid.
 */
function parseDate(dateString, yearToUse) {
    let dateParts;
    if (dateString.includes('-')) {
        dateParts = dateString.split('-');
    } else if (dateString.includes('/')) {
        dateParts = dateString.split('/');
    } else {
        return null; // Invalid format
    }

    let year, month, day;

    if (dateParts.length === 2) { // MM-DD
        year = yearToUse || new Date().getFullYear();
        month = parseInt(dateParts[0]) - 1; // Month is 0-indexed
        day = parseInt(dateParts[1]);
    } else if (dateParts.length === 3) { // YYYY-MM-DD
        year = parseInt(dateParts[0]);
        month = parseInt(dateParts[1]) - 1;
        day = parseInt(dateParts[2]);
    } else {
        return null; // Invalid format
    }

    const date = new Date(year, month, day);
    // Validate date to prevent invalid dates like Feb 30th converting to Mar 2nd
    if (date.getMonth() !== month || date.getDate() !== day) {
        return null; // The parsed date doesn't match the input components, indicating an invalid date
    }
    return date;
}

/**
 * Helper function to add/subtract days from a Date object.
 * Returns a new Date object.
 */
function addDays(date, days) {
    const result = new Date(date);
    result.setDate(result.getDate() + days);
    return result;
}

/**
 * Helper function to format a Date object as MM-DD-YYYY.
 * Returns 'Invalid Date' for invalid Date objects.
 */
function formatDate(date) {
    if (!date || isNaN(date.getTime())) return 'Invalid Date';
    const year = date.getFullYear();
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const day = date.getDate().toString().padStart(2, '0');
    return `${month}-${day}-${year}`;
}

// Get today's date for year context and comparisons
const today = new Date();
const currentYear = today.getFullYear();

// Parse Last Frost Date, initially assuming current year
let effectiveLastFrost = parseDate(inputs.lastFrostDate, currentYear);

// Handle invalid date input early
if (!effectiveLastFrost) {
    return {
        seedStartingDate: "N/A",
        targetTransplantDate: "N/A",
        _error: "Please enter a valid Last Frost Date in MM-DD format (e.g., 05-15)."
    };
}

// Adjust Last Frost Date year if it's already passed in the current year (and input was MM-DD)
const todayNoTime = new Date(today.getFullYear(), today.getMonth(), today.getDate());
const effectiveLastFrostNoTime = new Date(effectiveLastFrost.getFullYear(), effectiveLastFrost.getMonth(), effectiveLastFrost.getDate());

// If the input was MM-DD and that date has already passed this year, assume it's for next year.
if (inputs.lastFrostDate.length <= 5 && effectiveLastFrostNoTime < todayNoTime) {
    effectiveLastFrost = parseDate(inputs.lastFrostDate, currentYear + 1);
    // Re-check for validity after year adjustment
    if (!effectiveLastFrost) {
        return {
            seedStartingDate: "N/A",
            targetTransplantDate: "N/A",
            _error: "Failed to adjust Last Frost Date for next year. Please check format."
        };
    }
}

// Convert string inputs to numbers
const weeksIndoor = parseFloat(inputs.weeksIndoorStart);
const weeksHardenOff = parseFloat(inputs.weeksHardeningOff);
// Positive offsetWeeks means transplant *before* last frost, negative means *after*.
const transplantOffsetWeeks = parseFloat(inputs.targetTransplantOffsetWeeks);

// Calculate Target Transplant Date
// We subtract transplantOffsetDays from lastFrost because a positive offsetWeeks means earlier (before frost).
const transplantOffsetDays = transplantOffsetWeeks * 7;
let targetTransplant = addDays(effectiveLastFrost, -transplantOffsetDays);

// Calculate Seed Starting Date
// This is the total time seeds need to be indoors before they are ready to be transplanted.
const totalIndoorPrepWeeks = weeksIndoor + weeksHardenOff;
const totalIndoorPrepDays = totalIndoorPrepWeeks * 7;
let seedStarting = addDays(targetTransplant, -totalIndoorPrepDays);

// Return formatted results
return {
    seedStartingDate: formatDate(seedStarting),
    targetTransplantDate: formatDate(targetTransplant)
};
