function calculate({ W, Sds, S1, R, Ie, Ct, x, hn }) {
  const R_eff = R / Ie;

  // 1. Calculate Approximate Fundamental Period (Ta)
  const Ta = Ct * Math.pow(hn, x);

  // 2. Calculate preliminary Seismic Response Coefficient (Cs_prelim)
  const T_approx_transition = S1 / Sds; // Simplified T_S for conceptual transition
  let Cs_prelim;

  if (Ta <= T_approx_transition) {
    Cs_prelim = Sds / R_eff;
  } else {
    Cs_prelim = S1 / (Ta * R_eff);
  }

  // 3. Apply upper and lower bounds for Cs (based on ASCE 7-16 conceptual logic)
  const Cs_max_limit = Sds / R_eff; // Generally the upper limit for short period structures

  const Cs_min_limit1 = 0.044 * Ie * Sds;
  const Cs_min_limit2 = (S1 >= 0.6) ? (0.5 * S1 / R_eff) : 0; // Applies only if S1 >= 0.6g
  const Cs_min_final = Math.max(Cs_min_limit1, Cs_min_limit2);

  // Apply bounds: Cs must be between min_final and min(Cs_prelim, Cs_max_limit)
  const Cs = Math.max(Cs_min_final, Math.min(Cs_prelim, Cs_max_limit));

  // 4. Calculate Design Base Shear (V)
  const V = Cs * W;

  return {
    Ta: Ta,
    Cs: Cs,
    V: V
  };
}
