function calculateImpact(inputs) {
    const {
        eventDurationHours,
        personnelAffected,
        avgDailyRevenueOrOutput,
        avgPersonnelDailyWage,
        directInfrastructureDamage,
        emergencyResponseAndCleanup,
        supplyChainDisruptionCost,
        recoveryPhaseDays,
        insuranceCoverageRatio
    } = inputs;

    // Ensure insurance coverage ratio is within bounds
    const actualInsuranceRatio = Math.min(100, Math.max(0, insuranceCoverageRatio)) / 100;

    // 1. Calculate Total Explicit Direct Costs (for insurance calculation)
    const totalExplicitDirectCosts = directInfrastructureDamage + emergencyResponseAndCleanup + supplyChainDisruptionCost;

    // 2. Calculate Lost Productivity & Wages (during core disruption)
    // Assumes avgPersonnelDailyWage is the cost per day per employee.
    // eventDurationHours / 24 converts hours to equivalent days.
    const lostProductivityWages = personnelAffected > 0 && eventDurationHours > 0
        ? personnelAffected * avgPersonnelDailyWage * (eventDurationHours / 24)
        : 0;

    // 3. Calculate Lost Revenue (during core disruption)
    const lostRevenueEstimate = eventDurationHours > 0
        ? (eventDurationHours / 24) * avgDailyRevenueOrOutput
        : 0;

    // 4. Calculate Indirect Costs during Extended Recovery Phase
    // This accounts for ongoing operational inefficiencies, reduced sales, or increased costs
    // even after the initial disruption, assuming a percentage of normal daily output is still lost.
    const recoveryDailyImpactFactor = 0.20; // 20% of daily revenue/output lost/cost during recovery
    const recoveryIndirectCosts = recoveryPhaseDays > 0
        ? recoveryPhaseDays * avgDailyRevenueOrOutput * recoveryDailyImpactFactor
        : 0;

    // 5. Aggregate all costs into Direct and Indirect categories for reporting
    const totalDirectCosts = totalExplicitDirectCosts;
    const totalIndirectCosts = lostProductivityWages + lostRevenueEstimate + recoveryIndirectCosts;

    const grossEconomicImpact = totalDirectCosts + totalIndirectCosts;

    // 6. Calculate Net Economic Impact after Insurance
    // For simplicity, we assume insurance primarily covers a percentage of `totalExplicitDirectCosts`.
    // Real insurance policies can be more complex, with deductibles, caps, and specific coverages for BI.
    const insuredAmount = totalExplicitDirectCosts * actualInsuranceRatio;
    const netEconomicImpact = grossEconomicImpact - insuredAmount;

    return {
        totalDirectCosts: parseFloat(totalDirectCosts.toFixed(2)),
        lostRevenueEstimate: parseFloat(lostRevenueEstimate.toFixed(2)),
        totalIndirectCosts: parseFloat(totalIndirectCosts.toFixed(2)),
        netEconomicImpact: parseFloat(netEconomicImpact.toFixed(2))
    };
}
