/**
 * Calculates a home's severe weather resilience score based on structural factors, energy backup, and insulation.
 * The score ranges from 0-100.
 * @param {object} inputs - An object containing all input values.
 * @returns {object} An object containing the resilience_score, score_category, and vulnerability_notes.
 */
function calculateResilience(inputs) {
    let score = 0;
    const recommendations = [];

    // Base score contribution for each category, out of a total of 100
    // Structural: ~45 points
    // Energy: ~25 points
    // Water Intrusion: ~20 points
    // Age/Codes: ~10 points

    // 1. Foundation Type (Max 15 points)
    switch (inputs.foundation_type) {
        case 'slab_on_grade': score += 5; recommendations.push('Slab-on-grade foundations offer some stability but can be vulnerable to hydrostatic pressure without proper drainage.'); break;
        case 'crawl_space_unsealed': score += 3; recommendations.push('Unsealed crawl spaces are highly vulnerable to moisture, pests, and air quality issues. Consider sealing and insulation.'); break;
        case 'crawl_space_sealed': score += 8; break;
        case 'concrete_block_basement': score += 10; recommendations.push('Concrete block basements may require additional waterproofing and reinforcement for optimal resilience.'); break;
        case 'poured_concrete_basement': score += 12; break;
        case 'elevated_pier_piling': score += 15; break; // Best for flood-prone areas
    }

    // 2. Roof Material (Max 15 points)
    switch (inputs.roof_material) {
        case 'asphalt_shingle_standard': score += 5; recommendations.push('Standard asphalt shingles may have lower wind resistance. Consider upgrading to architectural or impact-resistant options.'); break;
        case 'asphalt_shingle_architectural': score += 8; break;
        case 'wood_shake': score += 4; recommendations.push('Wood shake roofs are highly flammable and require regular maintenance; they are generally less resilient to high winds than other options.'); break;
        case 'flat_membrane_tpo': score += 7; recommendations.push('Flat membrane roofs require diligent maintenance to prevent leaks and address ponding water.'); break;
        case 'metal_standing_seam': score += 12; break;
        case 'tile_concrete_clay': score += 15; break; // Durable, but heavy and can break
    }

    // 3. Window Type (Max 10 points)
    switch (inputs.window_type) {
        case 'single_pane': score += 2; recommendations.push('Single pane windows offer minimal thermal insulation and are highly vulnerable to impact. Consider upgrading to double or triple pane, or impact-resistant windows.'); break;
        case 'double_pane_standard': score += 5; break;
        case 'double_pane_lowe': score += 7; break;
        case 'triple_pane': score += 8; break;
        case 'impact_resistant': score += 10; break;
    }

    // 4. Wall Construction (Max 10 points)
    switch (inputs.wall_construction) {
        case 'wood_frame_siding': score += 4; recommendations.push('Wood frame with siding is common but may require additional reinforcement for high wind zones or specific weather events.'); break;
        case 'brick_veneer': score += 7; break;
        case 'solid_masonry': score += 9; break;
        case 'icf_sips': score += 10; break; // Superior strength and insulation
    }

    // 5. Attic Insulation R-Value (Max 15 points)
    const rValue = parseFloat(inputs.attic_insulation_r_value);
    if (isNaN(rValue) || rValue < 0) {
        recommendations.push('Please provide a valid R-value for attic insulation.');
    } else {
        if (rValue < 20) { score += 3; recommendations.push('Attic insulation is very low. Significant energy losses will occur during extreme temperatures. Aim for R-38 to R-60.'); }
        else if (rValue < 30) { score += 6; recommendations.push('Attic insulation is low. Consider adding more insulation to improve thermal resilience. Aim for R-38 to R-60.'); }
        else if (rValue < 38) { score += 9; recommendations.push('Attic insulation is fair. Adding more insulation could further improve energy efficiency and thermal resilience.'); }
        else if (rValue < 50) { score += 12; } // Good
        else { score += 15; } // Excellent
    }

    // 6. Backup Power System (Max 15 points)
    switch (inputs.backup_power_system) {
        case 'none': score += 0; recommendations.push('Lack of a backup power system makes your home highly vulnerable to power outages during severe weather. Consider a generator or solar+battery.'); break;
        case 'portable_generator': score += 5; recommendations.push('A portable generator offers some backup, but requires manual operation and fueling. Consider a standby system for greater convenience and resilience.'); break;
        case 'standby_generator': score += 10; break;
        case 'solar_battery_storage': score += 15; break; // Best for sustained power during grid outages
    }

    // 7. Water Intrusion Prevention Measures (Max 10 points)
    switch (inputs.water_intrusion_measures) {
        case 'none_basic_gutters': score += 2; recommendations.push('Basic or absent water intrusion measures leave your home susceptible to flooding and moisture damage. Evaluate your drainage, sump pump needs, and foundation sealing.'); break;
        case 'sump_pump_basic': score += 5; recommendations.push('A basic sump pump is a good start, but consider a battery backup or secondary pump for reliability during power outages.'); break;
        case 'sump_pump_battery_backup': score += 8; break;
        case 'advanced_drainage_system': score += 9; break;
        case 'elevated_utilities': score += 10; break;
    }

    // 8. Year Built (Max 10 points - adjusted based on modern building codes)
    const yearBuilt = parseInt(inputs.year_built);
    if (isNaN(yearBuilt) || yearBuilt < 1800 || yearBuilt > new Date().getFullYear()) {
        recommendations.push('Please provide a valid year for when your home was built.');
        score += 0; // Don't penalize for invalid input, just warn.
    } else {
        if (yearBuilt < 1960) { score += 2; recommendations.push('Homes built before 1960 may predate many modern building codes for severe weather. Consider a professional structural assessment.'); }
        else if (yearBuilt < 1980) { score += 4; recommendations.push('Homes built between 1960-1980 often lack modern energy efficiency and some structural resilience features. Upgrades may be beneficial.'); }
        else if (yearBuilt < 2000) { score += 6; }
        else if (yearBuilt < 2010) { score += 8; }
        else { score += 10; } // Post-2010 generally benefit from more robust codes
    }

    // Final Score Calculation & Capping
    score = Math.max(0, Math.min(100, score)); // Ensure score is between 0 and 100

    let scoreCategory;
    if (score < 30) {
        scoreCategory = 'Critical Vulnerability';
        recommendations.unshift('Your home exhibits critical vulnerabilities to severe weather. Immediate assessment and improvements are highly recommended.');
    } else if (score < 50) {
        scoreCategory = 'High Vulnerability';
        recommendations.unshift('Your home has high vulnerabilities. Prioritize key improvements for safety and protection.');
    } else if (score < 70) {
        scoreCategory = 'Moderate Resilience';
        recommendations.unshift('Your home has moderate resilience. Targeted upgrades can significantly improve preparedness.');
    } else if (score < 85) {
        scoreCategory = 'Good Resilience';
        recommendations.unshift('Your home is well-prepared. Consider advanced measures for optimal resilience.');
    } else {
        scoreCategory = 'Excellent Resilience';
        recommendations.unshift('Your home demonstrates excellent resilience against severe weather events. Maintain these features regularly.');
    }

    let vulnerabilityNotes = recommendations.length > 0 ? recommendations.join('\n') : 'Your home exhibits strong resilience characteristics based on the provided information. Regular maintenance is always recommended.';

    return {
        resilience_score: score,
        score_category: scoreCategory,
        vulnerability_notes: vulnerabilityNotes
    };
}
