function calculate({ utility_transformer_kva, transformer_impedance_percent, secondary_voltage_ll, transformer_x_r_ratio, conductor_length_ft, conductor_material, conductor_size, conductors_per_phase }) {

    // Simplified conductor data (R_AC and X_AC per 1000ft @ 75°C, 600V, 60Hz)
    // These values are illustrative and approximate, based on common NEC tables.
    const conductorData = {
        copper: {
            "4_awg": { r_per_1000ft: 0.31, x_per_1000ft: 0.040 },
            "2_awg": { r_per_1000ft: 0.19, x_per_1000ft: 0.040 },
            "1_0_awg": { r_per_1000ft: 0.12, x_per_1000ft: 0.045 },
            "2_0_awg": { r_per_1000ft: 0.096, x_per_1000ft: 0.045 },
            "3_0_awg": { r_per_1000ft: 0.076, x_per_1000ft: 0.045 },
            "4_0_awg": { r_per_1000ft: 0.060, x_per_1000ft: 0.045 },
            "250_kcmil": { r_per_1000ft: 0.051, x_per_1000ft: 0.040 },
            "350_kcmil": { r_per_1000ft: 0.036, x_per_1000ft: 0.040 },
            "500_kcmil": { r_per_1000ft: 0.025, x_per_1000ft: 0.040 }
        },
        aluminum: {
            "4_awg": { r_per_1000ft: 0.51, x_per_1000ft: 0.040 },
            "2_awg": { r_per_1000ft: 0.31, x_per_1000ft: 0.040 },
            "1_0_awg": { r_per_1000ft: 0.20, x_per_1000ft: 0.045 },
            "2_0_awg": { r_per_1000ft: 0.15, x_per_1000ft: 0.045 },
            "3_0_awg": { r_per_1000ft: 0.12, x_per_1000ft: 0.045 },
            "4_0_awg": { r_per_1000ft: 0.095, x_per_1000ft: 0.045 },
            "250_kcmil": { r_per_1000ft: 0.082, x_per_1000ft: 0.040 },
            "350_kcmil": { r_per_1000ft: 0.058, x_per_1000ft: 0.040 },
            "500_kcmil": { r_per_1000ft: 0.041, x_per_1000ft: 0.040 }
        }
    };

    // Input Validation
    if (utility_transformer_kva <= 0 || transformer_impedance_percent <= 0 || secondary_voltage_ll <= 0 || conductor_length_ft <= 0 || conductors_per_phase <= 0 || transformer_x_r_ratio < 0) {
        return { short_circuit_current_amps: 0, available_fault_k_amps: 0, total_impedance_ohms: 0 };
    }

    // 1. Calculate Transformer Per-Phase Impedance (Z_xfmr)
    // Base impedance for 3-phase system per phase
    const z_base_per_phase = Math.pow(secondary_voltage_ll, 2) / (utility_transformer_kva * 1000);

    // Transformer impedance magnitude per phase in ohms
    const z_xfmr_magnitude_per_phase = (transformer_impedance_percent / 100) * z_base_per_phase;

    // Deconstruct transformer impedance into R and X components using X/R ratio
    const tan_theta_xfmr = transformer_x_r_ratio;
    const r_xfmr = z_xfmr_magnitude_per_phase / Math.sqrt(1 + Math.pow(tan_theta_xfmr, 2));
    const x_xfmr = r_xfmr * tan_theta_xfmr;

    // 2. Calculate Conductor Impedance (Z_cond) per phase
    const selectedConductor = conductorData[conductor_material][conductor_size];
    if (!selectedConductor) {
        // This case should not be hit with proper select options, but for robustness
        return { short_circuit_current_amps: 0, available_fault_k_amps: 0, total_impedance_ohms: 0 };
    }

    const r_per_1000ft = selectedConductor.r_per_1000ft;
    const x_per_1000ft = selectedConductor.x_per_1000ft;

    // Per-phase resistance and reactance for the entire run, adjusted for parallel conductors
    const r_conductor_ohms = (r_per_1000ft / 1000) * conductor_length_ft / conductors_per_phase;
    const x_conductor_ohms = (x_per_1000ft / 1000) * conductor_length_ft / conductors_per_phase;

    // 3. Calculate Total System Impedance (Z_total) per phase
    // Sum the resistive and reactive components separately (vector addition)
    const r_total_ohms = r_xfmr + r_conductor_ohms;
    const x_total_ohms = x_xfmr + x_conductor_ohms;

    // Calculate the magnitude of the total impedance per phase
    const total_impedance_ohms_per_phase = Math.sqrt(Math.pow(r_total_ohms, 2) + Math.pow(x_total_ohms, 2));

    // 4. Calculate Short-circuit Current (I_sc) - for a 3-phase symmetrical fault
    // I_sc (3-phase) = V_LL / (sqrt(3) * Z_total_per_phase)
    const short_circuit_current_amps = secondary_voltage_ll / (Math.sqrt(3) * total_impedance_ohms_per_phase);

    const available_fault_k_amps = short_circuit_current_amps / 1000;

    return {
        short_circuit_current_amps: short_circuit_current_amps,
        available_fault_k_amps: available_fault_k_amps,
        total_impedance_ohms: total_impedance_ohms_per_phase
    };
}
