function calculate() {
    const shutdownDurationDays = parseFloat(inputs.shutdownDurationDays);
    const numAffectedFederalEmployees = parseFloat(inputs.numAffectedFederalEmployees);
    const avgDailyFederalEmployeeWage = parseFloat(inputs.avgDailyFederalEmployeeWage);
    const numAffectedContractorCompanies = parseFloat(inputs.numAffectedContractorCompanies);
    const avgDailyContractorRevenueLoss = parseFloat(inputs.avgDailyContractorRevenueLoss);
    const localEconomicMultiplier = parseFloat(inputs.localEconomicMultiplier);
    const federalEmployeeBackPayLikelihoodPercent = parseFloat(inputs.federalEmployeeBackPayLikelihoodPercent);

    // Input validation and edge cases
    if (isNaN(shutdownDurationDays) || shutdownDurationDays < 0) return { error: "Shutdown duration must be a non-negative number of days." };
    if (isNaN(numAffectedFederalEmployees) || numAffectedFederalEmployees < 0) return { error: "Number of federal employees must be non-negative." };
    if (isNaN(avgDailyFederalEmployeeWage) || avgDailyFederalEmployeeWage < 0) return { error: "Average daily employee wage must be non-negative." };
    if (isNaN(numAffectedContractorCompanies) || numAffectedContractorCompanies < 0) return { error: "Number of contractor companies must be non-negative." };
    if (isNaN(avgDailyContractorRevenueLoss) || avgDailyContractorRevenueLoss < 0) return { error: "Average daily contractor revenue loss must be non-negative." };
    if (isNaN(localEconomicMultiplier) || localEconomicMultiplier < 1) return { error: "Local economic multiplier must be 1 or greater." };
    if (isNaN(federalEmployeeBackPayLikelihoodPercent) || federalEmployeeBackPayLikelihoodPercent < 0 || federalEmployeeBackPayLikelihoodPercent > 100) return { error: "Back pay likelihood must be between 0 and 100 percent." };

    // Step 1: Calculate direct federal employee wage loss
    const directFederalEmployeeWageLoss = numAffectedFederalEmployees * avgDailyFederalEmployeeWage * shutdownDurationDays;

    // Step 2: Calculate direct contractor company revenue loss
    const directContractorRevenueLoss = numAffectedContractorCompanies * avgDailyContractorRevenueLoss * shutdownDurationDays;

    // Step 3: Sum the direct losses for Gross Direct Economic Impact
    const totalGrossDirectEconomicImpact = directFederalEmployeeWageLoss + directContractorRevenueLoss;

    // Step 4: Apply the Local Economic Multiplier for Total Local Economic Impact (Pre-Back Pay)
    const totalLocalEconomicImpact = totalGrossDirectEconomicImpact * localEconomicMultiplier;

    // Step 5: Adjust federal employee loss for back pay likelihood
    const backPayAmount = directFederalEmployeeWageLoss * (federalEmployeeBackPayLikelihoodPercent / 100);
    const netFederalEmployeeLossAfterBackPay = directFederalEmployeeWageLoss - backPayAmount;

    // Step 6: Calculate Total Net Economic Impact Considering Back Pay
    const totalNetEconomicImpactConsideringBackPay = (netFederalEmployeeLossAfterBackPay + directContractorRevenueLoss) * localEconomicMultiplier;

    return {
        directFederalEmployeeWageLoss,
        directContractorRevenueLoss,
        totalGrossDirectEconomicImpact,
        totalLocalEconomicImpact,
        netFederalEmployeeLossAfterBackPay,
        totalNetEconomicImpactConsideringBackPay
    };
}
