function calculateRemediationCost(inputs) {
    let baseCostPerSqM = 0;
    let methodFactor = 1;
    let typeFactor = 1;
    let groundwaterFactor = 1;
    let regulatoryFactor = 1;
    let monitoringCost = 0;
    const fixedPlanningAndReportingCost = 35000; // Base cost for preliminary assessments, reports, permits, project management

    // Input validation and default values
    const totalArea = Math.max(0, inputs.siteTotalAreaSqM || 0);
    const contaminatedArea = Math.max(0, inputs.contaminatedAreaSqM || 0);
    const severity = Math.max(1, Math.min(5, inputs.contaminationConcentrationSeverity || 1));
    const monitoringYears = Math.max(0, inputs.postRemediationMonitoringYears || 0);

    // Ensure contaminated area does not exceed total area
    if (contaminatedArea > totalArea) {
        return {
            estimatedRemediationCostUSD: 0,
            costBreakdownSummary: "Error: Contaminated area cannot exceed total site area."
        };
    }

    if (contaminatedArea === 0) {
        return {
            estimatedRemediationCostUSD: totalArea > 0 ? fixedPlanningAndReportingCost : 0,
            costBreakdownSummary: totalArea > 0 ? "Minimal or no contamination, primarily planning and assessment costs." : "No site area or contamination specified."
        };
    }

    // Determine base cost per sq meter and method factor (illustrative market-based values)
    switch (inputs.chosenRemediationMethod.toLowerCase()) {
        case 'excavation & disposal':
            methodFactor = 1.8; // High cost due to transport and landfill/treatment fees
            baseCostPerSqM = 120;
            break;
        case 'in-situ treatment':
            methodFactor = 1.2;
            baseCostPerSqM = 80;
            break;
        case 'ex-situ treatment (on-site)':
            methodFactor = 1.5;
            baseCostPerSqM = 100;
            break;
        case 'capping/containment':
            methodFactor = 0.9; // Lower initial cost, but implies long-term management
            baseCostPerSqM = 70;
            break;
        default:
            // Default to a moderately invasive on-site method if not specified
            methodFactor = 1.3;
            baseCostPerSqM = 90;
    }

    // Adjust for contamination type (reflects treatment complexity and specific technologies)
    switch (inputs.contaminationType.toLowerCase()) {
        case 'hydrocarbons':
            typeFactor = 1.0; // Relatively common, established methods
            break;
        case 'heavy metals':
            typeFactor = 1.3; // Often requires stabilization or immobilization
            break;
        case 'pfas':
            typeFactor = 3.0; // Highly persistent, complex, and very expensive to treat
            break;
        case 'asbestos':
            typeFactor = 1.1; // Requires specialized handling and disposal
            break;
        case 'mixed organic':
            typeFactor = 1.5; // Multiple contaminants often require combined strategies
            break;
        case 'pcbs':
            typeFactor = 2.0; // Persistent Organic Pollutants, often incineration or chemical treatment
            break;
        default:
            typeFactor = 1.0;
    }

    // Adjust for groundwater involvement (significant cost driver)
    switch (inputs.groundwaterInvolvement.toLowerCase()) {
        case 'none':
            groundwaterFactor = 1.0;
            break;
        case 'minor':
            groundwaterFactor = 1.3; // Monitoring wells, minor pump-and-treat or barriers
            break;
        case 'moderate':
            groundwaterFactor = 1.8; // More extensive pump-and-treat, permeable reactive barriers, advanced monitoring
            break;
        case 'severe':
            groundwaterFactor = 3.0; // Extensive hydrogeological investigations, deep well injection, multi-phase extraction, long-term complex systems
            break;
        default:
            groundwaterFactor = 1.0;
    }

    // Adjust for regulatory oversight (influences documentation, sampling, agency interaction)
    switch (inputs.regulatoryOversightLevel.toLowerCase()) {
        case 'standard':
            regulatoryFactor = 1.0;
            break;
        case 'enhanced':
            regulatoryFactor = 1.15; // More frequent reporting, additional agency reviews, public notification requirements
            break;
        case 'high scrutiny':
            regulatoryFactor = 1.40; // Frequent audits, extensive community engagement, stricter clean-up levels, potential for legal challenges
            break;
        default:
            regulatoryFactor = 1.0;
    }

    // Base cost for remediation area, combining method, type, and groundwater factors
    let remediationAreaCost = contaminatedArea * baseCostPerSqM * methodFactor * typeFactor * groundwaterFactor;

    // Severity multiplier: +15% per severity point above 1
    remediationAreaCost *= (1 + (severity - 1) * 0.15);

    // Site complexity/mobilization factor for the entire site (even clean areas influence logistics/project management)
    // This factor accounts for general site preparation, security, project management, and basic infrastructure.
    let siteLogisticsAndPMCost = totalArea * 0.8; // e.g., $0.80 per sq meter for general overhead
    if (totalArea > 20000) siteLogisticsAndPMCost *= 0.7; // Economies of scale for larger sites
    if (totalArea < 500) siteLogisticsAndPMCost *= 1.5; // Higher relative overhead for very small sites

    // Post-remediation monitoring cost (e.g., $20,000 per year per 1000 sqM of contaminated area, minimum $10k/year)
    let annualMonitoringBase = (contaminatedArea / 1000) * 20000;
    let annualMonitoringCost = Math.max(10000, annualMonitoringBase);
    monitoringCost = annualMonitoringCost * monitoringYears;
    monitoringCost *= regulatoryFactor; // Monitoring also subject to regulatory rigor

    // Total estimated cost
    let totalEstimatedCost = fixedPlanningAndReportingCost + remediationAreaCost + siteLogisticsAndPMCost + monitoringCost;

    // Apply regulatory factor as a final multiplier for overall project complexity and compliance
    totalEstimatedCost *= regulatoryFactor;

    // Cap the estimated cost to a realistic max for a single project (e.g., $500M) and ensure a minimum ($75K for any serious assessment)
    totalEstimatedCost = Math.min(500000000, Math.max(75000, totalEstimatedCost));

    let breakdown = `Base remediation for ${contaminatedArea.toFixed(0)} sqM: $${remediationAreaCost.toFixed(0)}. `;
    breakdown += `Planning, reporting & general project management: $${fixedPlanningAndReportingCost.toFixed(0)}. `;
    breakdown += `Site logistics & overhead: $${siteLogisticsAndPMCost.toFixed(0)}. `;
    if (monitoringYears > 0) {
        breakdown += `Post-remediation monitoring (${monitoringYears} years): $${monitoringCost.toFixed(0)}. `;
    }
    breakdown += `Adjustments applied for contamination severity, type, groundwater involvement, and regulatory oversight levels.`;

    return {
        estimatedRemediationCostUSD: Math.round(totalEstimatedCost),
        costBreakdownSummary: breakdown
    };
}
