function calculateROI(inputs) {
    const {
        initialSafetyInvestment,
        annualMaintenanceCost,
        projectLifespanYears,
        averageDirectAccidentCost,
        averageIndirectAccidentCostFactor,
        averageAccidentFrequencyBefore,
        expectedAccidentReductionPercentage,
        annualProjectRevenue,
        productivityImprovementPercentage,
        liabilityReductionPercentage
    } = inputs;

    // Ensure numeric inputs are treated as numbers and handle potential non-numeric inputs gracefully
    const numInitialSafetyInvestment = parseFloat(initialSafetyInvestment) || 0;
    const numAnnualMaintenanceCost = parseFloat(annualMaintenanceCost) || 0;
    const numProjectLifespanYears = parseFloat(projectLifespanYears) || 0;
    const numAverageDirectAccidentCost = parseFloat(averageDirectAccidentCost) || 0;
    const numAverageIndirectAccidentCostFactor = parseFloat(averageIndirectAccidentCostFactor) || 1; 
    const numAverageAccidentFrequencyBefore = parseFloat(averageAccidentFrequencyBefore) || 0;
    const numExpectedAccidentReductionPercentage = parseFloat(expectedAccidentReductionPercentage) || 0;
    const numAnnualProjectRevenue = parseFloat(annualProjectRevenue) || 0;
    const numProductivityImprovementPercentage = parseFloat(productivityImprovementPercentage) || 0;
    const numLiabilityReductionPercentage = parseFloat(liabilityReductionPercentage) || 0;

    // Cap percentages to 0-100%
    const expectedAccidentReductionFactor = Math.min(Math.max(numExpectedAccidentReductionPercentage, 0), 100) / 100;
    const productivityImprovementFactor = Math.min(Math.max(numProductivityImprovementPercentage, 0), 100) / 100;
    const liabilityReductionFactor = Math.min(Math.max(numLiabilityReductionPercentage, 0), 100) / 100;

    // 1. Calculate Total Safety Investment
    const totalSafetyInvestment = numInitialSafetyInvestment + (numAnnualMaintenanceCost * numProjectLifespanYears);

    // 2. Calculate Avoided Accident Costs
    const averageTotalAccidentCostPerIncident = numAverageDirectAccidentCost * (1 + numAverageIndirectAccidentCostFactor);
    const avoidedAccidentFrequencyPerYear = numAverageAccidentFrequencyBefore * expectedAccidentReductionFactor;
    const totalAvoidedAccidentCosts = avoidedAccidentFrequencyPerYear * averageTotalAccidentCostPerIncident * numProjectLifespanYears;

    // 3. Calculate Productivity Gains
    const totalProductivityGains = (numAnnualProjectRevenue * productivityImprovementFactor) * numProjectLifespanYears;

    // 4. Calculate Liability Savings
    // Assume baseline potential liability costs are proportional to existing accident costs before mitigation.
    const baselinePotentialAccidentLiabilityCost = numAverageAccidentFrequencyBefore * averageTotalAccidentCostPerIncident * numProjectLifespanYears;
    const totalLiabilitySavings = baselinePotentialAccidentLiabilityCost * liabilityReductionFactor;

    // 5. Calculate Total Monetary Benefits
    const totalMonetaryBenefits = totalAvoidedAccidentCosts + totalProductivityGains + totalLiabilitySavings;

    // 6. Calculate Net ROI
    let netROI = 0;
    if (totalSafetyInvestment > 0) {
        netROI = ((totalMonetaryBenefits - totalSafetyInvestment) / totalSafetyInvestment) * 100;
    } else if (totalMonetaryBenefits > 0) { // If investment is 0 but benefits exist, ROI is effectively infinite.
        netROI = Infinity; // Represent as a very large number for practical display if needed
    }

    // 7. Calculate Payback Period
    let paybackPeriodYears = Infinity;
    const annualNetBenefit = (totalMonetaryBenefits / numProjectLifespanYears) - numAnnualMaintenanceCost; // Average annual benefit after maintenance

    if (numInitialSafetyInvestment === 0 && totalMonetaryBenefits > 0) {
        paybackPeriodYears = 0; // Immediate payback if no initial investment and benefits exist
    } else if (annualNetBenefit > 0) {
        paybackPeriodYears = numInitialSafetyInvestment / annualNetBenefit;
    } 

    // If total benefits do not cover total investment, or annual net benefit is negative, no payback within project lifespan.
    if (totalMonetaryBenefits < totalSafetyInvestment || annualNetBenefit <= 0) {
        paybackPeriodYears = Infinity;
    }
    // Cap payback period to project lifespan if it exceeds it, for practical interpretation
    if (paybackPeriodYears > numProjectLifespanYears) {
        paybackPeriodYears = Infinity;
    }
    // If payback period is calculated as negative (e.g., due to annual net benefit calculation, should be handled by above), set to infinity
    if (paybackPeriodYears < 0) {
        paybackPeriodYears = Infinity;
    }

    return {
        totalSafetyInvestment: totalSafetyInvestment,
        totalAvoidedAccidentCosts: totalAvoidedAccidentCosts,
        totalProductivityGains: totalProductivityGains,
        totalLiabilitySavings: totalLiabilitySavings,
        totalMonetaryBenefits: totalMonetaryBenefits,
        netROI: netROI,
        paybackPeriodYears: paybackPeriodYears
    };
}
