function calculate({ initialBuildingValue, upgradeCost, expectedDamageReduction, currentAnnualInsurancePremium, insurancePremiumReduction, estimatedAnnualFatalitiesBaseline, fatalityReductionPercentage, valueHumanLife, projectLifeYears, discountRate }) {
    // Input validation and edge case handling
    if ([initialBuildingValue, upgradeCost, currentAnnualInsurancePremium, estimatedAnnualFatalitiesBaseline, valueHumanLife, projectLifeYears].some(v => typeof v !== 'number' || v < 0)) {
        throw new Error('All monetary, fatality, and time inputs must be non-negative numbers.');
    }
    if (projectLifeYears === 0) {
        return {
            annualPropertyDamageSavings: 0,
            annualInsuranceSavings: 0,
            annualHumanLifePreservationValue: 0,
            totalAnnualBenefits: 0,
            netPresentValue: -upgradeCost,
            paybackPeriod: upgradeCost > 0 ? Infinity : 0,
            returnOnInvestment: upgradeCost > 0 ? -100 : 0,
            costBenefitRatio: 0
        };
    }
    if ([expectedDamageReduction, insurancePremiumReduction, fatalityReductionPercentage].some(v => typeof v !== 'number' || v < 0 || v > 100)) {
        throw new Error('All percentage inputs must be between 0 and 100.');
    }

    const effectiveDiscountRate = (typeof discountRate === 'number' && discountRate >= 0 && discountRate <= 100) ? discountRate / 100 : 0.05; // Default to 5% if invalid or not provided

    // 1. Calculate Annual Property Damage Reduction Savings
    const annualPropertyDamageSavings = initialBuildingValue * (expectedDamageReduction / 100);

    // 2. Calculate Annual Insurance Premium Savings
    const annualInsuranceSavings = currentAnnualInsurancePremium * (insurancePremiumReduction / 100);

    // 3. Calculate Annual Human Life Preservation Value
    const reducedFatalitiesAnnually = estimatedAnnualFatalitiesBaseline * (fatalityReductionPercentage / 100);
    const annualHumanLifePreservationValue = reducedFatalitiesAnnually * valueHumanLife;

    // 4. Calculate Total Estimated Annual Benefits
    const totalAnnualBenefits = annualPropertyDamageSavings + annualInsuranceSavings + annualHumanLifePreservationValue;

    // 5. Calculate Net Present Value (NPV)
    let npv = -upgradeCost;
    if (totalAnnualBenefits > 0 && effectiveDiscountRate >= 0) {
        for (let i = 1; i <= projectLifeYears; i++) {
            npv += totalAnnualBenefits / Math.pow(1 + effectiveDiscountRate, i);
        }
    }

    // 6. Calculate Payback Period
    const paybackPeriod = totalAnnualBenefits > 0 ? upgradeCost / totalAnnualBenefits : (upgradeCost > 0 ? Infinity : 0);

    // 7. Calculate Return on Investment (ROI)
    const totalBenefitsOverLife = totalAnnualBenefits * projectLifeYears;
    const returnOnInvestment = upgradeCost > 0 ? ((totalBenefitsOverLife - upgradeCost) / upgradeCost) * 100 : (totalBenefitsOverLife > 0 ? Infinity : 0);

    // 8. Calculate Cost-Benefit Ratio
    const costBenefitRatio = upgradeCost > 0 ? totalBenefitsOverLife / upgradeCost : (totalBenefitsOverLife > 0 ? Infinity : 0);

    return {
        annualPropertyDamageSavings: annualPropertyDamageSavings,
        annualInsuranceSavings: annualInsuranceSavings,
        annualHumanLifePreservationValue: annualHumanLifePreservationValue,
        totalAnnualBenefits: totalAnnualBenefits,
        netPresentValue: npv,
        paybackPeriod: paybackPeriod,
        returnOnInvestment: returnOnInvestment,
        costBenefitRatio: costBenefitRatio
    };
}
