const annualRevenue = parseFloat(inputs.annualRevenue);
const numEmployees = parseInt(inputs.numEmployees);
const industrySector = inputs.industrySector;
const dataSensitivityLevel = inputs.dataSensitivityLevel;
const currentSecurityPosture = inputs.currentSecurityPosture;
const averageDowntimeHours = parseFloat(inputs.averageDowntimeHours);
const avgCostPerRecord = parseFloat(inputs.avgCostPerRecord);
const existingCyberInsuranceCoverage = parseFloat(inputs.existingCyberInsuranceCoverage);

// --- Constants & Lookups --- 
const industryRiskFactors = {
    "Retail": 1.2, // Higher risk due to payment data, PII
    "Healthcare": 1.5, // PHI, high regulatory fines
    "Finance": 1.8, // Financial data, very high regulatory scrutiny
    "Professional Services": 1.3, // Client confidential data, IP
    "Manufacturing": 1.1, // IP, operational technology risks
    "Technology": 1.4, // IP, software vulnerabilities, supply chain risk
    "Other": 1.0
};
const dataSensitivityMultipliers = {
    "Low": 0.5, // Fewer records, lower impact per record
    "Moderate": 1.0,
    "High": 1.5, // More records or higher impact per record
    "Critical": 2.0 // Very high number of records or severe impact per record
};
const postureReductionFactors = {
    "Minimal": 0.1, // Provides only 10% reduction from baseline risk
    "Basic": 0.3,
    "Moderate": 0.5,
    "Strong": 0.7 // Provides 70% reduction from baseline risk
};
const baseAttackLikelihood = 0.15; // 15% annual chance for an average SMB to experience a significant attack
const recommendedSecurityEffectiveness = 0.75; // Assumed effectiveness of implementing recommended measures in reducing future losses (75%)

const baseCostPerEmployeeSecurity = 500; // Base annual cost for recommended security per employee (e.g., software, training, services)
const workingHoursPerYear = 2080; // Standard full-time working hours in a year
const maxRevenueDowntimeCap = 0.10; // Max downtime cost capped at 10% of annual revenue for a single incident
const maxRecommendedInvestmentCap = 0.20; // Max recommended investment capped at 20% of annual revenue for an SMB's first year

// --- Input Validation & Defaults --- 
if (isNaN(annualRevenue) || annualRevenue < 0) return { error: "Invalid Annual Revenue. Please enter a positive number." };
if (isNaN(numEmployees) || numEmployees <= 0) return { error: "Invalid Number of Employees. Please enter at least 1." };
if (isNaN(averageDowntimeHours) || averageDowntimeHours < 0) return { error: "Invalid Average Downtime Hours. Please enter a non-negative number." };
if (isNaN(avgCostPerRecord) || avgCostPerRecord < 0) return { error: "Invalid Average Cost Per Record. Please enter a non-negative number." };
if (isNaN(existingCyberInsuranceCoverage) || existingCyberInsuranceCoverage < 0) return { error: "Invalid Cyber Insurance Coverage. Please enter a non-negative number." };

// --- Step 1: Calculate Current Risk Profile Factors --- 
const currentIndustryRiskFactor = industryRiskFactors[industrySector] || industryRiskFactors["Other"];
const currentDataSensitivityMultiplier = dataSensitivityMultipliers[dataSensitivityLevel] || dataSensitivityMultipliers["Moderate"];
const currentPostureReductionFactor = postureReductionFactors[currentSecurityPosture] || postureReductionFactors["Minimal"];

// Adjust attack likelihood based on industry and current posture, capped between 0 and 1
let effectiveAttackLikelihood = baseAttackLikelihood * currentIndustryRiskFactor * (1 - currentPostureReductionFactor);
effectiveAttackLikelihood = Math.min(1, Math.max(0, effectiveAttackLikelihood)); 

// Estimate number of sensitive records based on employees and data sensitivity (heuristic)
const estimatedSensitiveRecords = numEmployees * 100 * currentDataSensitivityMultiplier; // Avg 100 records/employee, scaled by sensitivity

// Calculate cost of potential downtime per incident, capped to prevent unrealistically high values
const costOfDowntimePerIncident = (annualRevenue / workingHoursPerYear) * averageDowntimeHours;
const actualDowntimeCost = Math.min(costOfDowntimePerIncident, annualRevenue * maxRevenueDowntimeCap);

// --- Step 2: Calculate Single Loss Expectancy (SLE) --- 
const singleLossExpectancy = (estimatedSensitiveRecords * avgCostPerRecord) + actualDowntimeCost;

// --- Step 3: Calculate Annual Loss Expectancy (ALE) without new investment --- 
const aleBeforeSecurity = singleLossExpectancy * effectiveAttackLikelihood;

// --- Step 4: Estimate Recommended Annual Security Investment --- 
// Base investment scales with employees, then adjusted by data sensitivity and industry risk
let recommendedSecurityInvestment = numEmployees * baseCostPerEmployeeSecurity;
recommendedSecurityInvestment *= (1 + (currentDataSensitivityMultiplier * 0.2)); // Higher sensitivity = higher investment
recommendedSecurityInvestment *= (1 + (currentIndustryRiskFactor * 0.1)); // Higher industry risk = higher investment

// Cap recommended investment to be realistic for an SMB's first year
recommendedSecurityInvestment = Math.min(recommendedSecurityInvestment, annualRevenue * maxRecommendedInvestmentCap);

// --- Step 5: Calculate Potential Loss After Investment (Reduced ALE) --- 
// Assume recommended measures reduce the effective risk that leads to ALE
const aleAfterInvestment = aleBeforeSecurity * (1 - recommendedSecurityEffectiveness);

// --- Step 6: Calculate Return on Security Investment (ROSI) --- 
const avoidedLosses = aleBeforeSecurity - aleAfterInvestment;
let returnOnSecurityInvestment = 0;
if (recommendedSecurityInvestment > 0) {
    returnOnSecurityInvestment = ((avoidedLosses - recommendedSecurityInvestment) / recommendedSecurityInvestment) * 100;
} else {
    // If no investment is recommended, ROSI is effectively infinite if losses are avoided, else 0
    returnOnSecurityInvestment = avoidedLosses > 0 ? Infinity : 0;
}

// --- Step 7: Calculate Net Financial Impact --- 
// This considers the initial ALE (reduced by avoided losses), the cost of investment, and insurance payout
let netFinancialImpact = (aleBeforeSecurity - avoidedLosses) + recommendedSecurityInvestment - existingCyberInsuranceCoverage;
// Ensure net financial impact does not go below zero, as that implies a profit from risk/security, which is unrealistic for 'impact'
netFinancialImpact = Math.max(0, netFinancialImpact);

return {
    estimatedAnnualLossExpectancy: aleBeforeSecurity,
    recommendedSecurityInvestment: recommendedSecurityInvestment,
    potentialLossAfterInvestment: aleAfterInvestment,
    returnOnSecurityInvestment: returnOnSecurityInvestment,
    netFinancialImpact: netFinancialImpact
};
