function calculate({ monthlyRevenue, monthlyOperatingExpenses, cashReserves, totalDebt, lineOfCreditAvailable, daysReceivables, daysPayables, supplyChainDiversificationScore, operationalFlexibilityScore }) {

  // Input validation and default to 0 for non-numeric or negative values where appropriate
  monthlyRevenue = Math.max(0, Number(monthlyRevenue) || 0);
  monthlyOperatingExpenses = Math.max(0, Number(monthlyOperatingExpenses) || 0);
  cashReserves = Math.max(0, Number(cashReserves) || 0);
  totalDebt = Math.max(0, Number(totalDebt) || 0);
  lineOfCreditAvailable = Math.max(0, Number(lineOfCreditAvailable) || 0);
  daysReceivables = Math.max(0, Number(daysReceivables) || 0);
  daysPayables = Math.max(0, Number(daysPayables) || 0);
  supplyChainDiversificationScore = Math.min(5, Math.max(1, Number(supplyChainDiversificationScore) || 1));
  operationalFlexibilityScore = Math.min(5, Math.max(1, Number(operationalFlexibilityScore) || 1));

  // 1. Calculate Cash Runway
  const effectiveCash = cashReserves + lineOfCreditAvailable;
  const netMonthlyBurn = monthlyOperatingExpenses - monthlyRevenue;
  let cashRunwayMonths;
  if (netMonthlyBurn <= 0) {
    cashRunwayMonths = 999; // Represents effectively infinite runway or growing cash
  } else {
    cashRunwayMonths = effectiveCash / netMonthlyBurn;
    cashRunwayMonths = Math.min(cashRunwayMonths, 999); // Cap for very long runways
  }

  // 2. Calculate Debt-to-Available Cash Ratio
  let debtToCashRatio;
  if (effectiveCash === 0) {
    debtToCashRatio = totalDebt > 0 ? 999 : 0; // Infinite if debt exists but no cash, else 0
  } else {
    debtToCashRatio = totalDebt / effectiveCash;
  }

  // 3. Calculate Short-Term Shock Buffer (Days)
  const averageDailyOperatingExpenses = monthlyOperatingExpenses / 30.437; // Average days in a month
  let shortTermShockBufferDays = 0;
  if (averageDailyOperatingExpenses > 0) {
    shortTermShockBufferDays = effectiveCash / averageDailyOperatingExpenses;
  }
  shortTermShockBufferDays = Math.min(shortTermShockBufferDays, 730); // Cap at approx. 2 years

  // 4. Calculate Overall Economic Resilience Score (0-100%)
  // Component 1: Cash Runway (30% weight)
  // A runway of 6 months is considered good, 12+ excellent.
  const runwayScoreComponent = Math.min(1, cashRunwayMonths / 9); // Max score at 9 months

  // Component 2: Debt-to-Available Cash Ratio (25% weight)
  // A ratio of 0-0.5 is excellent, 0.5-1.0 good, 1.0-2.0 acceptable, >2.0 concerning
  const debtToCashScoreComponent = Math.max(0, 1 - Math.min(1, debtToCashRatio / 1.5)); // 1.5 is the point where score drops to 0

  // Component 3: Supply Chain Diversification (15% weight)
  const supplyChainScoreComponent = supplyChainDiversificationScore / 5;

  // Component 4: Operational Flexibility (15% weight)
  const operationalFlexibilityScoreComponent = operationalFlexibilityScore / 5;

  // Component 5: Working Capital Efficiency (15% weight)
  // A positive difference (payables > receivables) is good. Neutral around 0-15 days. Max good around 30 days.
  const workingCapitalDifference = daysPayables - daysReceivables;
  // Normalize: A difference of +30 days (payables > receivables) is ideal (score 1). -30 days is poor (score 0).
  const workingCapitalScoreComponent = Math.max(0, Math.min(1, (workingCapitalDifference + 30) / 60)); // Range -30 to +30 maps to 0 to 1

  const economicResilienceScore = (
    runwayScoreComponent * 0.30 +
    debtToCashScoreComponent * 0.25 +
    supplyChainScoreComponent * 0.15 +
    operationalFlexibilityScoreComponent * 0.15 +
    workingCapitalScoreComponent * 0.15
  ) * 100;

  return {
    cashRunwayMonths: parseFloat(cashRunwayMonths.toFixed(1)),
    debtToCashRatio: parseFloat(debtToCashRatio.toFixed(2)),
    economicResilienceScore: Math.round(economicResilienceScore),
    shortTermShockBufferDays: Math.round(shortTermShockBufferDays)
  };
}
