function calculate(inputs) {
  const sellerDiscretionaryEarnings = inputs.seller_discretionary_earnings;
  const sdeMultiple = inputs.sde_multiple;
  const cashAssets = inputs.cash_assets;
  const debtLiabilities = inputs.debt_liabilities;
  const inventoryValue = inputs.inventory_value;
  const fixedAssetValuation = inputs.fixed_asset_valuation;

  // Ensure SDE multiple is within a reasonable range for calculation purposes (e.g., 0.5 to 10)
  const cappedSdeMultiple = Math.min(10, Math.max(0.5, sdeMultiple));

  // Calculate the SDE-based valuation component.
  // If SDE is zero or negative, the multiple method isn't typically applicable for this component.
  const sdeBasedValue = sellerDiscretionaryEarnings > 0 
    ? sellerDiscretionaryEarnings * cappedSdeMultiple 
    : 0;

  // Calculate net tangible asset adjustment (liquid assets + physical assets - debts)
  const netTangibleAssetAdjustment = cashAssets + inventoryValue + fixedAssetValuation - debtLiabilities;

  // The 'estimated_business_value' primarily represents the SDE-based component.
  const estimatedBusinessValue = sdeBasedValue;

  // The 'adjusted_business_value' incorporates both the SDE-based value and the net tangible assets.
  // Ensure the adjusted value is not negative, as a business generally has some inherent value or liquidation value.
  const adjustedBusinessValue = Math.max(0, sdeBasedValue + netTangibleAssetAdjustment);

  return {
    estimated_business_value: estimatedBusinessValue,
    adjusted_business_value: adjustedBusinessValue
  };
}
