const { smr_capacity_mw, smr_capex_per_mw, smr_om_fixed_per_mw_year, smr_construction_years, tnp_capacity_mw, tnp_capex_per_mw, tnp_om_fixed_per_mw_year, tnp_construction_years } = inputs; 

// --- Assumed Constants for LCOE Calculation --- 
const plant_lifespan_years = 60; // Common assumed operational lifespan for nuclear plants 
const capacity_factor_percent = 90; // Typical high capacity factor for nuclear 
const discount_rate_percent = 5; // Assumed real discount rate for economic analysis 

const capacity_factor = capacity_factor_percent / 100; 
const discount_rate = discount_rate_percent / 100; 
const hours_per_year = 8760; 

// --- Capital Recovery Factor (CRF) --- 
// CRF = [r * (1 + r)^n] / [(1 + r)^n - 1] 
const crf_numerator = discount_rate * Math.pow(1 + discount_rate, plant_lifespan_years); 
const crf_denominator = Math.pow(1 + discount_rate, plant_lifespan_years) - 1; 
const capital_recovery_factor = crf_denominator === 0 ? 0 : crf_numerator / crf_denominator; // Handle zero denominator edge case 

// --- SMR Calculations --- 
const smr_total_project_cost = smr_capacity_mw * smr_capex_per_mw; 
const smr_annual_operating_cost = smr_capacity_mw * smr_om_fixed_per_mw_year; 
const smr_annual_energy_production_mwh = smr_capacity_mw * hours_per_year * capacity_factor; 

// Prevent division by zero for LCOE if production is zero 
let smr_lcoe_usd_mwh = 0; 
if (smr_annual_energy_production_mwh > 0) { 
  const smr_annualized_capex_cost = smr_total_project_cost * capital_recovery_factor; 
  smr_lcoe_usd_mwh = (smr_annualized_capex_cost + smr_annual_operating_cost) / smr_annual_energy_production_mwh; 
} 

// --- Traditional Plant Calculations --- 
const tnp_total_project_cost = tnp_capacity_mw * tnp_capex_per_mw; 
const tnp_annual_operating_cost = tnp_capacity_mw * tnp_om_fixed_per_mw_year; 
const tnp_annual_energy_production_mwh = tnp_capacity_mw * hours_per_year * capacity_factor; 

// Prevent division by zero for LCOE if production is zero 
let tnp_lcoe_usd_mwh = 0; 
if (tnp_annual_energy_production_mwh > 0) { 
  const tnp_annualized_capex_cost = tnp_total_project_cost * capital_recovery_factor; 
  tnp_lcoe_usd_mwh = (tnp_annualized_capex_cost + tnp_annual_operating_cost) / tnp_annual_energy_production_mwh; 
} 

// --- Comparison Outputs --- 
const construction_time_advantage = tnp_construction_years - smr_construction_years; 
const lcoe_difference = smr_lcoe_usd_mwh - tnp_lcoe_usd_mwh; 

return { 
  smr_total_project_cost: smr_total_project_cost, 
  tnp_total_project_cost: tnp_total_project_cost, 
  smr_annual_operating_cost: smr_annual_operating_cost, 
  tnp_annual_operating_cost: tnp_annual_operating_cost, 
  smr_lcoe_usd_mwh: smr_lcoe_usd_mwh, 
  tnp_lcoe_usd_mwh: tnp_lcoe_usd_mwh, 
  construction_time_advantage: construction_time_advantage, 
  lcoe_difference: lcoe_difference 
};
