function calculate({ expectedSnowDepth, snowDensity, roofArea, structuralCapacity, roofSlope, userSafetyFactor }) {
  // Input validation and defaults
  const depth = Math.max(0, parseFloat(expectedSnowDepth) || 0);
  const density = Math.max(0, parseFloat(snowDensity) || 0);
  const area = Math.max(0, parseFloat(roofArea) || 0);
  const capacity = Math.max(1, parseFloat(structuralCapacity) || 1); // Capacity must be > 0
  const slope = Math.min(90, Math.max(0, parseFloat(roofSlope) || 0)); // Slope between 0 and 90
  const safetyFactor = Math.max(1, parseFloat(userSafetyFactor) || 1);

  // Edge case: if no snow depth or density, no load
  if (depth === 0 || density === 0 || area === 0) {
    return {
      totalSnowWeight: 0,
      calculatedSnowLoad: 0,
      stressPercentage: 0,
      actionThreshold: 'No significant snow load detected.'
    };
  }

  // Step 1: Convert snow depth from inches to feet
  const depth_ft = depth / 12;

  // Step 2: Calculate raw snow load per square foot (ground equivalent)
  const rawSnowLoad_psf = depth_ft * density;

  // Step 3: Adjust snow load for roof slope
  // This uses a simplified linear reduction. Actual codes (like ASCE 7) use more complex factors.
  // For slopes > 70 degrees, assume nearly all snow sheds.
  let slopeReductionFactor = 1;
  if (slope <= 70) {
    // Linear reduction: 0% reduction at 0 degrees, ~70% reduction at 70 degrees.
    // (1 - slope/70) means 1 at 0 degrees, 0 at 70 degrees
    slopeReductionFactor = Math.max(0, 1 - (slope / 70)); 
  } else { // slope > 70
    slopeReductionFactor = 0; // Virtually no snow remains on very steep roofs
  }
  const effectiveSnowLoad_psf = rawSnowLoad_psf * slopeReductionFactor;

  // Step 4: Calculate total estimated snow weight on the roof
  const totalSnowWeight_lbs = effectiveSnowLoad_psf * area;

  // Step 5: Calculate structural stress percentage (factored for safety)
  // The safety factor effectively increases the 'felt' load for calculation purposes,
  // making the threshold more conservative.
  const factoredSnowLoad = effectiveSnowLoad_psf * safetyFactor;
  let stressPercentage = (factoredSnowLoad / capacity) * 100;

  // Cap stress percentage at a very high number if it exceeds reasonable bounds
  stressPercentage = Math.min(1000, stressPercentage); 

  // Step 6: Determine recommended action threshold
  let actionThreshold = 'Uncertain or Invalid Input';
  if (stressPercentage < 0) {
    actionThreshold = 'Error: Negative stress calculated.';
  } else if (stressPercentage < 50) {
    actionThreshold = 'Low Risk: Monitor conditions, no immediate action required.';
  } else if (stressPercentage >= 50 && stressPercentage < 75) {
    actionThreshold = 'Moderate Risk: Prepare for potential action, conduct visual inspection.';
  } else if (stressPercentage >= 75 && stressPercentage < 95) {
    actionThreshold = 'High Risk: Consider mitigation strategies, prepare for snow removal.';
  } else if (stressPercentage >= 95 && stressPercentage < 100) {
    actionThreshold = 'Critical Risk: Immediate action advised, consider professional snow removal.';
  } else {
    actionThreshold = 'Extreme Danger: Load likely exceeds structural capacity. Immediate evacuation and professional intervention required.';
  }

  return {
    totalSnowWeight: totalSnowWeight_lbs,
    calculatedSnowLoad: effectiveSnowLoad_psf,
    stressPercentage: stressPercentage,
    actionThreshold: actionThreshold
  };
}
