function calculate() {
  const protestParticipants = parseFloat(inputs.protestParticipants) || 0;
  const majorMediaOutletsCoverage = parseFloat(inputs.majorMediaOutletsCoverage) || 0;
  const socialMediaInitialShares = parseFloat(inputs.socialMediaInitialShares) || 0;
  const averageResharesPerShare = parseFloat(inputs.averageResharesPerShare) || 0.1;
  const policyAlignmentScore = parseFloat(inputs.policyAlignmentScore) || 0;
  const organizerNetworkReach = parseFloat(inputs.organizerNetworkReach) || 0;
  const campaignDurationDays = parseFloat(inputs.campaignDurationDays) || 1;
  const celebrityInfluencerEndorsements = parseFloat(inputs.celebrityInfluencerEndorsements) || 0;

  // Constants and Multipliers
  const BASE_PROTEST_MULTIPLIER = 5; // Each participant informs X others
  const MEDIA_OUTLET_BASE_REACH = 500000; // Average reach per major outlet
  const MEDIA_PROTEST_SIZE_BOOST_FACTOR = 0.000005; // Larger protests get disproportionately more media attention
  const SOCIAL_MEDIA_AUDIENCE_PER_SHARE = 25; // Average audience per social media share/impression
  const VIRALITY_DIMINISHING_RETURNS = 0.7; // Power factor for social media cascade
  const INFLUENCER_BASE_REACH = 2000000; // Average reach per celebrity influencer
  const DURATION_MULTIPLIER_FACTOR = 0.05; // Each day adds 5% more reach

  const ENGAGEMENT_RATE_BASE = 0.005; // 0.5% of total reach might engage
  const ENGAGEMENT_VIRALITY_BOOST = 0.05; // Virality increases engagement % by this much per reshare factor
  const ENGAGEMENT_POLICY_BOOST = 0.0005; // Policy alignment % increases engagement % by this much

  const POLICY_ENGAGEMENT_CONVERSION = 0.00001; // How much engaged audience converts to policy pressure

  // Step 1: Calculate Base Reach (Protest + Organizer)
  let baseReach = protestParticipants * BASE_PROTEST_MULTIPLIER;
  baseReach += organizerNetworkReach;

  // Step 2: Calculate Media Reach
  let mediaReach = majorMediaOutletsCoverage * MEDIA_OUTLET_BASE_REACH;
  // Boost media reach based on protest size, capped to prevent absurd numbers
  mediaReach *= (1 + Math.min(1, protestParticipants * MEDIA_PROTEST_SIZE_BOOST_FACTOR)); 

  // Step 3: Calculate Social Media Reach (with diminishing returns)
  let socialMediaReach = socialMediaInitialShares * SOCIAL_MEDIA_AUDIENCE_PER_SHARE;
  if (averageResharesPerShare > 1) {
    socialMediaReach *= Math.pow(averageResharesPerShare, VIRALITY_DIMINISHING_RETURNS);
  } else {
    socialMediaReach *= averageResharesPerShare; // Linear if less than 1 reshare
  }

  // Step 4: Calculate Influencer Reach
  let influencerReach = celebrityInfluencerEndorsements * INFLUENCER_BASE_REACH;

  // Step 5: Sum initial reach components
  let potentialTotalReach = baseReach + mediaReach + socialMediaReach + influencerReach;

  // Step 6: Apply Campaign Duration Multiplier
  potentialTotalReach *= (1 + (campaignDurationDays * DURATION_MULTIPLIER_FACTOR));

  // Ensure minimum reach even with low inputs
  potentialTotalReach = Math.max(potentialTotalReach, protestParticipants + organizerNetworkReach + socialMediaInitialShares);

  // Step 7: Calculate Estimated Engaged Audience
  let engagementRate = ENGAGEMENT_RATE_BASE;
  engagementRate += (averageResharesPerShare - 1) * ENGAGEMENT_VIRALITY_BOOST; // Boost for virality beyond 1
  engagementRate += (policyAlignmentScore / 100) * ENGAGEMENT_POLICY_BOOST; // Boost for policy alignment
  engagementRate = Math.min(0.2, Math.max(0.001, engagementRate)); // Cap engagement rate between 0.1% and 20%

  const estimatedEngagedAudience = potentialTotalReach * engagementRate;

  // Step 8: Calculate Policy Influence Score
  let policyInfluenceScore = policyAlignmentScore * 0.5; // Base on alignment
  policyInfluenceScore += Math.min(50, (estimatedEngagedAudience * POLICY_ENGAGEMENT_CONVERSION)); // Add boost from engaged audience, capped
  policyInfluenceScore = Math.min(100, Math.max(0, policyInfluenceScore)); // Cap score between 0 and 100

  return {
    potentialTotalReach: Math.round(potentialTotalReach),
    estimatedEngagedAudience: Math.round(estimatedEngagedAudience),
    policyInfluenceScore: parseFloat(policyInfluenceScore.toFixed(1))
  };
}
