function calculateSIB(inputs) {
    const initialInvestment = inputs.initialInvestment;
    const interventionCostPerBeneficiary = inputs.interventionCostPerBeneficiary;
    const numberOfBeneficiaries = inputs.numberOfBeneficiaries;
    const baselineOutcomeRate = inputs.baselineOutcomeRate; // %
    const targetOutcomeReduction = inputs.targetOutcomeReduction; // % points reduction from baseline
    const outcomeMonetaryValuePerUnit = inputs.outcomeMonetaryValuePerUnit;
    const investorPaymentRate = inputs.investorPaymentRate; // %
    const paymentTriggerThreshold = inputs.paymentTriggerThreshold; // % points reduction
    const maxInvestorReturnCap = inputs.maxInvestorReturnCap; // % of initial investment

    // Input Validations
    if ([initialInvestment, interventionCostPerBeneficiary, numberOfBeneficiaries, baselineOutcomeRate, targetOutcomeReduction, outcomeMonetaryValuePerUnit, investorPaymentRate, paymentTriggerThreshold, maxInvestorReturnCap].some(val => val < 0)) {
        return {
            error: "All monetary and percentage inputs must be non-negative. Number of beneficiaries must be non-negative."
        };
    }
    if (baselineOutcomeRate < targetOutcomeReduction) {
        return {
            error: "Target Outcome Reduction cannot exceed the Baseline Negative Outcome Rate. You cannot reduce more than exists."
        };
    }
    if (baselineOutcomeRate < 0 || baselineOutcomeRate > 100) {
        return {
            error: "Baseline Outcome Rate must be between 0 and 100 percent."
        };
    }
    if (investorPaymentRate < 0 || investorPaymentRate > 1000) { // Allowing >100% for very high-return scenarios, but capping at 1000% seems reasonable
        return {
            error: "Investor Payment Rate typically ranges from 0-100% of generated value, but should not be excessively high."
        };
    }
    if (maxInvestorReturnCap < 0) {
         return {
            error: "Maximum Investor Return Cap cannot be negative."
        };
    }

    // Step 1: Calculate Total Intervention Cost
    const totalInterventionCost = interventionCostPerBeneficiary * numberOfBeneficiaries;

    // Step 2: Calculate Achieved Outcome Reduction (absolute percentage points) and Avoided Negative Outcomes
    // The achieved reduction cannot exceed the baseline rate (you can't reduce more than 100% of an issue).
    const achievedOutcomeReductionAbsolute = Math.max(0, Math.min(targetOutcomeReduction, baselineOutcomeRate));
    const avoidedNegativeOutcomes = numberOfBeneficiaries * (achievedOutcomeReductionAbsolute / 100);

    // Step 3: Calculate Total Monetary Value Generated from Outcomes (Social Benefit)
    const totalMonetaryValueGenerated = avoidedNegativeOutcomes * outcomeMonetaryValuePerUnit;

    // Step 4: Determine Payment to Investors, applying trigger and cap
    let paymentToInvestors = 0;
    if (achievedOutcomeReductionAbsolute >= paymentTriggerThreshold) {
        const rawInvestorPayment = totalMonetaryValueGenerated * (investorPaymentRate / 100);
        const maxPaymentAmount = initialInvestment * (maxInvestorReturnCap / 100);
        paymentToInvestors = Math.min(rawInvestorPayment, maxPaymentAmount);
    }

    // Step 5: Calculate Investor Return on Investment (ROI)
    let investorReturnOnInvestment = 0;
    if (initialInvestment > 0) {
        investorReturnOnInvestment = ((paymentToInvestors - initialInvestment) / initialInvestment) * 100;
    } else if (paymentToInvestors > 0) {
        investorReturnOnInvestment = Infinity; // Infinite return if no initial investment but payment received
    } // Else, if initialInvestment is 0 and paymentToInvestors is 0, ROI is 0.

    // Step 6: Calculate Net Social Benefit
    // This represents the total value generated for society, net of the cost of delivering the intervention.
    const netSocialBenefit = totalMonetaryValueGenerated - totalInterventionCost;

    // Step 7: Calculate Breakeven Outcome Reduction Required for Investors
    let breakevenReductionRequired = 0;
    if (initialInvestment === 0) {
        breakevenReductionRequired = 0; // If no initial investment, breakeven is technically 0 reduction (or already met)
    } else if (numberOfBeneficiaries === 0 || outcomeMonetaryValuePerUnit === 0 || investorPaymentRate === 0) {
        breakevenReductionRequired = Infinity; // Cannot achieve breakeven if these critical factors are zero
    } else {
        // Calculate the total monetary value that needs to be generated to provide 'initialInvestment' back to investors
        // based on the investorPaymentRate.
        const monetaryValueForBreakeven = initialInvestment / (investorPaymentRate / 100);

        // Calculate the number of avoided negative outcomes needed to generate 'monetaryValueForBreakeven'.
        const avoidedOutcomesForBreakeven = monetaryValueForBreakeven / outcomeMonetaryValuePerUnit;

        // Calculate the percentage points reduction required to achieve 'avoidedOutcomesForBreakeven'.
        breakevenReductionRequired = (avoidedOutcomesForBreakeven / numberOfBeneficiaries) * 100;

        // Adjust for trigger and baseline cap
        // If the calculated breakeven is below the trigger, the actual minimum reduction to get *any* payment is the trigger itself.
        // So, the investor needs to hit at least the trigger, AND generate enough value to breakeven.
        breakevenReductionRequired = Math.max(breakevenReductionRequired, paymentTriggerThreshold);

        // If the required breakeven reduction exceeds the maximum possible (baseline rate), it's unachievable.
        if (breakevenReductionRequired > baselineOutcomeRate) {
            breakevenReductionRequired = Infinity; // Indicate that breakeven is not possible given the baseline
        }
    }

    return {
        totalInterventionCost: totalInterventionCost,
        achievedOutcomeReductionAbsolute: achievedOutcomeReductionAbsolute,
        avoidedNegativeOutcomes: avoidedNegativeOutcomes,
        totalMonetaryValueGenerated: totalMonetaryValueGenerated,
        paymentToInvestors: paymentToInvestors,
        investorReturnOnInvestment: investorReturnOnInvestment,
        netSocialBenefit: netSocialBenefit,
        breakevenReductionRequired: breakevenReductionRequired
    };
}
