function calculate({ currentAnnualFunding, requiredAnnualFunding, totalTargetPopulation, avgCostPerBeneficiaryFullCoverage, costOfUnmetNeedsPerPerson, localEconomicMultiplier, administrativeEfficiencyRatio }) {
    // Ensure inputs are non-negative and handle potential undefined/null values
    currentAnnualFunding = Math.max(0, currentAnnualFunding || 0);
    requiredAnnualFunding = Math.max(0, requiredAnnualFunding || 0);
    totalTargetPopulation = Math.max(0, totalTargetPopulation || 0);
    avgCostPerBeneficiaryFullCoverage = Math.max(0, avgCostPerBeneficiaryFullCoverage || 0);
    costOfUnmetNeedsPerPerson = Math.max(0, costOfUnmetNeedsPerPerson || 0);
    localEconomicMultiplier = Math.max(1, localEconomicMultiplier || 1); // Multiplier typically >= 1
    // administrativeEfficiencyRatio is used for context but not directly in the primary outputs here.

    // 1. Calculate Funding Gap Amount
    const fundingGapAmount = requiredAnnualFunding - currentAnnualFunding;

    // 2. Calculate Funding Gap Percentage
    let fundingGapPercentage = 0;
    if (requiredAnnualFunding > 0) {
        fundingGapPercentage = (fundingGapAmount / requiredAnnualFunding) * 100;
    }
    // Ensure percentage is capped at 100% or properly represents overfunding
    fundingGapPercentage = Math.min(100, Math.max(-10000, fundingGapPercentage)); // Cap for reasonable display if vastly overfunded

    // 3. Estimate Unserved Population
    let unservedPopulationEstimate = 0;
    if (fundingGapAmount > 0 && avgCostPerBeneficiaryFullCoverage > 0) {
        unservedPopulationEstimate = fundingGapAmount / avgCostPerBeneficiaryFullCoverage;
        // Cap unserved population at the total target population
        unservedPopulationEstimate = Math.min(unservedPopulationEstimate, totalTargetPopulation);
    }
    // Ensure unserved population is not negative (if gap is negative, meaning overfunded or fully funded)
    unservedPopulationEstimate = Math.max(0, unservedPopulationEstimate);

    // 4. Calculate Direct Socioeconomic Loss
    const directSocioeconomicLoss = unservedPopulationEstimate * costOfUnmetNeedsPerPerson;

    // 5. Calculate Indirect Socioeconomic Loss (using the multiplier effect)
    // The multiplier typically includes the direct spend, so (multiplier - 1) gives the additional indirect/induced effect.
    const indirectSocioeconomicLoss = directSocioeconomicLoss * (localEconomicMultiplier - 1);

    // 6. Calculate Total Socioeconomic Impact
    const totalSocioeconomicImpact = directSocioeconomicLoss + indirectSocioeconomicLoss;

    return {
        fundingGapAmount: fundingGapAmount,
        fundingGapPercentage: fundingGapPercentage,
        unservedPopulationEstimate: Math.round(unservedPopulationEstimate), // Round people to whole numbers
        directSocioeconomicLoss: directSocioeconomicLoss,
        indirectSocioeconomicLoss: indirectSocioeconomicLoss,
        totalSocioeconomicImpact: totalSocioeconomicImpact
    };
}
