function calculate({ currentSoilPH, desiredSoilPH, soilTexture, targetArea, amendmentType, amendmentPurity }) {
  let amendmentAmountLbs = 0;

  // Input validation and normalization
  currentSoilPH = parseFloat(currentSoilPH);
  desiredSoilPH = parseFloat(desiredSoilPH);
  targetArea = parseFloat(targetArea);
  amendmentPurity = parseFloat(amendmentPurity);

  if (isNaN(currentSoilPH) || isNaN(desiredSoilPH) || isNaN(targetArea) || isNaN(amendmentPurity) || targetArea <= 0 || amendmentPurity <= 0) {
    return { amountLbs: 0, amountKg: 0 };
  }

  amendmentPurity = Math.max(1, Math.min(100, amendmentPurity)); // Cap purity between 1 and 100

  const PH_DIFF = desiredSoilPH - currentSoilPH;
  const AREA_FACTOR = targetArea / 1000; // Calculations are based per 1000 sq ft

  // Factors in lbs per 1000 sq ft per 1 pH unit change (simplified for demonstration)
  const LIME_FACTOR_SAND = 25; // lbs / 1000 sq ft / pH unit
  const LIME_FACTOR_LOAM = 50;
  const LIME_FACTOR_CLAY = 80;

  const SULFUR_FACTOR_SAND = 7.5; // lbs / 1000 sq ft / pH unit
  const SULFUR_FACTOR_LOAM = 15;
  const SULFUR_FACTOR_CLAY = 25;

  let baseFactor = 0;

  // Determine base factor based on amendment type and soil texture
  if (amendmentType.toLowerCase() === 'lime') {
    if (PH_DIFF <= 0) { // Lime is for raising pH, if target is lower or same, no lime needed.
      return { amountLbs: 0, amountKg: 0 };
    }
    switch (soilTexture.toLowerCase()) {
      case 'sand':
        baseFactor = LIME_FACTOR_SAND;
        break;
      case 'loam':
        baseFactor = LIME_FACTOR_LOAM;
        break;
      case 'clay':
        baseFactor = LIME_FACTOR_CLAY;
        break;
      default:
        baseFactor = LIME_FACTOR_LOAM; // Default to loam if unknown/invalid texture
    }
  } else if (amendmentType.toLowerCase() === 'sulfur') {
    if (PH_DIFF >= 0) { // Sulfur is for lowering pH, if target is higher or same, no sulfur needed.
      return { amountLbs: 0, amountKg: 0 };
    }
    switch (soilTexture.toLowerCase()) {
      case 'sand':
        baseFactor = SULFUR_FACTOR_SAND;
        break;
      case 'loam':
        baseFactor = SULFUR_FACTOR_LOAM;
        break;
      case 'clay':
        baseFactor = SULFUR_FACTOR_CLAY;
        break;
      default:
        baseFactor = SULFUR_FACTOR_LOAM; // Default to loam if unknown/invalid texture
    }
  } else {
    return { amountLbs: 0, amountKg: 0 }; // Unknown amendment type
  }

  // Calculate amendment amount
  // Amount = (Absolute pH difference) * (Base factor per 1000 sq ft per pH unit) * (Area / 1000) / (Purity / 100)
  amendmentAmountLbs = Math.abs(PH_DIFF) * baseFactor * AREA_FACTOR / (amendmentPurity / 100);

  const KG_PER_LB = 0.453592;
  const amendmentAmountKg = amendmentAmountLbs * KG_PER_LB;

  return {
    amountLbs: amendmentAmountLbs,
    amountKg: amendmentAmountKg
  };
}
