const bedAreaM2 = inputs.bed_length * inputs.bed_width;
const incorporationDepthM = inputs.incorporation_depth_cm / 100;

// The volume of existing soil that needs to be amended
const soilVolumeToAmendM3 = bedAreaM2 * incorporationDepthM;

// Cap target_volume_percentage based on amendment type for realistic recommendations
let adjustedTargetPercentage = inputs.target_volume_percentage;
const minPercentage = 1; // Minimum practical amendment percentage
const maxCompostPercentage = 25; // Max recommended compost by volume
const maxPerlitePercentage = 40; // Max recommended perlite by volume for drainage
const maxSandPercentage = 60; // Max recommended sand by volume for drainage/lightening

adjustedTargetPercentage = Math.max(minPercentage, adjustedTargetPercentage); // Ensure at least min percentage

const amendmentTypeLower = inputs.amendment_type.toLowerCase();

if (amendmentTypeLower === 'compost') {
    adjustedTargetPercentage = Math.min(adjustedTargetPercentage, maxCompostPercentage);
} else if (amendmentTypeLower === 'perlite') {
    adjustedTargetPercentage = Math.min(adjustedTargetPercentage, maxPerlitePercentage);
} else if (amendmentTypeLower === 'sand') {
    adjustedTargetPercentage = Math.min(adjustedTargetPercentage, maxSandPercentage);
} else {
    // Default or general cap for unknown types (e.g., assume similar to perlite)
    adjustedTargetPercentage = Math.min(adjustedTargetPercentage, maxPerlitePercentage);
}

// Calculate the required volume of amendment based on the adjusted percentage
const requiredVolumeM3 = soilVolumeToAmendM3 * (adjustedTargetPercentage / 100);

// Ensure a minimum practical volume, e.g., for very small beds (10 Liters = 0.01 m3)
const minPracticalVolumeM3 = 0.01;
const finalRequiredVolumeM3 = Math.max(requiredVolumeM3, minPracticalVolumeM3);

// Calculate weight, ensuring density is positive and realistic
const amendmentDensityKgM3 = Math.max(1, inputs.amendment_bulk_density_kg_m3); // Ensure density is at least 1
const requiredWeightKg = finalRequiredVolumeM3 * amendmentDensityKgM3;

// Calculate estimated 50L bags (1 L = 0.001 m3, so 50L = 0.05 m3)
const litersPerBag = 50;
const m3PerBag = litersPerBag / 1000;
const estimatedBags50L = finalRequiredVolumeM3 / m3PerBag;
const roundedBags = Math.ceil(estimatedBags50L); // Always round up to ensure enough is purchased

return {
    required_volume_m3: finalRequiredVolumeM3,
    required_weight_kg: requiredWeightKg,
    estimated_bags_50l: roundedBags
};
