const effectiveCost = initialCost * (1 - federalTaxCredit / 100);

const annualProductionKwh = systemSizeKw * (systemPerformanceRatio / 100) * averageDailySunlightHours * 365;

const firstYearSavings = annualProductionKwh * electricityCostPerKwh;

let cumulativeSavings = 0;
let paybackPeriodYears = 0;
let currentElectricityPrice = electricityCostPerKwh;
let currentAnnualProduction = annualProductionKwh;
const maxYears = 50;

for (let year = 1; year <= maxYears; year++) {
    const savingsThisYear = currentAnnualProduction * currentElectricityPrice;
    cumulativeSavings += savingsThisYear;

    if (cumulativeSavings >= effectiveCost) {
        paybackPeriodYears = year;
        break;
    }

    currentElectricityPrice *= (1 + annualElectricityInflation / 100);
    currentAnnualProduction *= (1 - annualSystemDegradation / 100);
}

if (paybackPeriodYears === 0) {
    paybackPeriodYears = maxYears; // Capped at maxYears if no payback within the simulation period
}

return {
    paybackPeriodYears: paybackPeriodYears,
    firstYearSavings: firstYearSavings
};
