function calculate({ installationCost, annualEnergyProduction, currentElectricityRate, annualElectricityInflation, upfrontIncentives, annualDegradationRate, annualMaintenanceCost }) {
    const initialNetCost = installationCost - upfrontIncentives;
    let paybackPeriod = null; // Initialize to null, set to a number if found
    let cumulativeSavings = 0;
    let currentEnergyProduction = annualEnergyProduction;
    let currentElectricityRatePerKWh = currentElectricityRate;

    // Convert percentages to decimals
    const inflationFactor = 1 + (annualElectricityInflation / 100);
    const degradationFactor = 1 - (annualDegradationRate / 100);

    const MAX_CALC_YEARS = 50; // Cap calculation for payback period

    if (initialNetCost <= 0) {
        paybackPeriod = 0; // Immediate payback if cost is zero or negative
    } else {
        for (let year = 1; year <= MAX_CALC_YEARS; year++) {
            const annualEnergySavingsValue = currentEnergyProduction * currentElectricityRatePerKWh;
            const netAnnualBenefit = annualEnergySavingsValue - annualMaintenanceCost;

            // If annual benefit is non-positive and we haven't paid back, it's unlikely to ever pay back.
            if (netAnnualBenefit <= 0 && cumulativeSavings < initialNetCost) {
                break; // Exit loop, payback will remain null
            }

            cumulativeSavings += netAnnualBenefit;

            if (paybackPeriod === null && cumulativeSavings >= initialNetCost) {
                const previousCumulativeSavings = cumulativeSavings - netAnnualBenefit;
                const remainingCost = initialNetCost - previousCumulativeSavings;
                paybackPeriod = (year - 1) + (remainingCost / netAnnualBenefit);
            }

            // Update for next year
            currentEnergyProduction *= degradationFactor;
            currentElectricityRatePerKWh *= inflationFactor;
        }
    }

    // Calculate total savings over 25 years (typical panel lifespan)
    let totalSavings25Years = 0;
    // Reset values for a fresh 25-year calculation independent of payback loop
    let currentEnergyProductionFor25Years = annualEnergyProduction;
    let currentElectricityRatePerKWhFor25Years = currentElectricityRate;
    let cumulativeBenefitFor25Years = 0;

    for (let y = 1; y <= 25; y++) {
        const annualEnergySavingsValue = currentEnergyProductionFor25Years * currentElectricityRatePerKWhFor25Years;
        const netAnnualBenefit = annualEnergySavingsValue - annualMaintenanceCost;
        cumulativeBenefitFor25Years += netAnnualBenefit;

        // Update for next year in 25-year calculation
        currentEnergyProductionFor25Years *= degradationFactor;
        currentElectricityRatePerKWhFor25Years *= inflationFactor;
    }
    totalSavings25Years = cumulativeBenefitFor25Years - initialNetCost; // Net savings over 25 years

    return {
        paybackPeriod: paybackPeriod === null ? null : parseFloat(paybackPeriod.toFixed(1)),
        totalSavings25Years: parseFloat(totalSavings25Years.toFixed(0)),
        netInitialCost: parseFloat(initialNetCost.toFixed(0))
    };
}
