function calculate({ daily_energy_demand_kwh, site_peak_sun_hours, base_system_losses_percent, module_power_wp, optimal_tilt_angle_deg, array_tilt_angle_deg, optimal_azimuth_angle_deg, array_azimuth_angle_deg }) {

  // Convert percentage losses to a base performance ratio (e.g., inverter, wiring, temp)
  const base_performance_ratio = (100 - base_system_losses_percent) / 100;

  // Calculate deviation from optimal tilt angle
  const tilt_deviation = Math.abs(array_tilt_angle_deg - optimal_tilt_angle_deg);
  // A simplified linear penalty for tilt deviation, capped to ensure a minimum performance
  const tilt_performance_multiplier = Math.max(0.85, 1 - (tilt_deviation * 0.003)); // ~0.3% loss per degree, capped at 85% performance

  // Calculate deviation from optimal azimuth angle
  const azimuth_deviation = Math.abs(array_azimuth_angle_deg - optimal_azimuth_angle_deg);
  // A cosine function to model azimuth impact, capped to ensure a minimum performance
  // Convert degrees to radians for Math.cos()
  const azimuth_performance_multiplier = Math.max(0.75, Math.cos(azimuth_deviation * Math.PI / 180)); // Cos(0)=1, Cos(90)=0. Cap at 75% performance

  // Combine orientation impacts
  const combined_orientation_factor = tilt_performance_multiplier * azimuth_performance_multiplier;

  // Calculate the final effective performance ratio including all losses
  const final_performance_ratio = base_performance_ratio * combined_orientation_factor;

  // Calculate the actual energy that needs to be generated by the array to meet daily demand
  const energy_needed_from_array_kwh = daily_energy_demand_kwh / final_performance_ratio;

  // Calculate the required total array power in Watts-peak (Wp)
  // Energy (kWh) = Power (kW) * Hours (h)
  // So, Power (kW) = Energy (kWh) / Hours (h)
  const required_array_power_wp = (energy_needed_from_array_kwh * 1000) / site_peak_sun_hours;

  // Calculate the number of PV modules needed, rounded up to ensure demand is met
  const num_pv_modules = Math.ceil(required_array_power_wp / module_power_wp);

  // Calculate the actual total array power based on the whole number of modules
  const total_array_power_kwp = (num_pv_modules * module_power_wp) / 1000;

  return {
    total_array_power_kwp: total_array_power_kwp,
    num_pv_modules: num_pv_modules
  };
}
