function calculateRisk(inputs) {
    const { latitude, altitude, exposureDuration, activityLevel, protonFlux, shieldingEffectiveness, age, preExistingConditions } = inputs;

    // --- Base Scores and Factors --- 
    let baseRisk = 5; // Baseline inherent risk

    // --- Location Risk --- 
    let locationRisk = 0;
    // Latitude factor: higher risk at poles due to weaker geomagnetic shielding
    if (Math.abs(latitude) >= 70) { // Extreme polar regions
        locationRisk += 15;
    } else if (Math.abs(latitude) >= 50) { // High latitudes
        locationRisk += 8;
    } else if (Math.abs(latitude) >= 30) { // Mid latitudes
        locationRisk += 3;
    }
    // Altitude factor: less atmospheric shielding at higher altitudes
    // Capped at 30km for practical purposes, relevant for high-altitude flight
    locationRisk += Math.min(20, (altitude / 500) * 2); // 2 points per 500m, capped at 20 points (for 5000m altitude and above)

    // --- Exposure & Activity Risk --- 
    let exposureActivityRisk = 0;
    // Duration factor: cumulative exposure
    exposureActivityRisk += Math.min(30, (exposureDuration / 12) * 5); // 5 points per 12 hours, capped at 30 points (for 72 hours and above)

    // Activity Level factor
    switch (activityLevel) {
        case "Sedentary/Indoors (shielded)":
            exposureActivityRisk += 0;
            break;
        case "Light Outdoor Activity (minimal shielding)":
            exposureActivityRisk += 10;
            break;
        case "Moderate Outdoor Activity (some exposure)":
            exposureActivityRisk += 20;
            break;
        case "High Altitude Travel/Flight":
            exposureActivityRisk += 35; // Significant exposure due to altitude and duration often
            break;
        case "Extreme Outdoor/Polar/Space Exposure":
            exposureActivityRisk += 60; // Highest exposure scenario
            break;
    }

    // --- Solar Radiation Intensity Risk --- 
    let solarRadiationRisk = 0;
    if (protonFlux > 100000) { // Extreme event (>10^5 pfu)
        solarRadiationRisk += 70;
    } else if (protonFlux > 10000) { // Severe event (>10^4 pfu)
        solarRadiationRisk += 50;
    } else if (protonFlux > 1000) { // Moderate event (>10^3 pfu)
        solarRadiationRisk += 30;
    } else if (protonFlux > 100) { // Minor event (>10^2 pfu)
        solarRadiationRisk += 15;
    } else if (protonFlux > 10) { // Small event (>10^1 pfu)
        solarRadiationRisk += 5;
    }
    // Note: protonFlux 0-10 pfu is considered background, no additional risk here.

    // --- Shielding Effectiveness Mitigation --- 
    let shieldingMitigation = 0;
    if (shieldingEffectiveness > 0) {
        // Shielding primarily reduces exposure and radiation risk from external sources
        let effectiveReduction = (shieldingEffectiveness / 100);
        // Apply reduction to components that shielding can directly impact
        shieldingMitigation = -(locationRisk * 0.5 + exposureActivityRisk * 0.7 + solarRadiationRisk * 0.9) * effectiveReduction;
        shieldingMitigation = Math.max(shieldingMitigation, -100); // Cap max reduction at 100 points
    }

    // --- Individual Vulnerability Risk --- 
    let individualVulnerabilityRisk = 0;
    // Age factor
    if (age < 18) { // Children generally more sensitive
        individualVulnerabilityRisk += (18 - age) * 0.8; // Max 14.4 for newborn
    } else if (age >= 60) { // Elderly generally more sensitive
        individualVulnerabilityRisk += (age - 60) * 0.6; // Max 36 for 120-year-old
    }
    // Pre-existing conditions factor
    switch (preExistingConditions) {
        case "None":
            individualVulnerabilityRisk += 0;
            break;
        case "Mild Cardiovascular/Respiratory":
            individualVulnerabilityRisk += 10;
            break;
        case "Moderate Cardiovascular/Respiratory/Immune":
            individualVulnerabilityRisk += 25;
            break;
        case "Severe Cardiovascular/Respiratory/Immune/Radiation Sensitivity":
            individualVulnerabilityRisk += 50;
            break;
    }

    // --- Summing all components --- 
    let totalRiskScore = baseRisk + locationRisk + exposureActivityRisk + solarRadiationRisk + individualVulnerabilityRisk + shieldingMitigation;

    // --- Final Health Risk Index Scaling --- 
    // Max theoretical score: Base(5) + Location(15+20=35) + Exposure(30+60=90) + Solar(70) + Individual(14.4+36+50=100.4) - minShielding(0) = ~300
    // Scale the total score to a 0-100 index for intuitive understanding.
    let healthRiskIndex = Math.max(0, Math.min(100, totalRiskScore / 3)); // Normalize by dividing by approx max potential score

    // --- Recommendation Logic --- 
    let recommendation = "Monitor space weather forecasts. Maintain awareness.";
    if (healthRiskIndex >= 75) {
        recommendation = "Severe Risk: Consider immediate protective measures, seek shelter, or postpone high-risk activities. Consult medical advice if you experience symptoms.";
    } else if (healthRiskIndex >= 50) {
        recommendation = "High Risk: Take significant precautions, limit outdoor exposure, and implement shielding. Be prepared for potential impacts and consult official advisories.";
    } else if (healthRiskIndex >= 25) {
        recommendation = "Moderate Risk: Exercise caution, especially if sensitive. Implement basic protective strategies and stay informed about space weather updates.";
    }

    return {
        healthRiskIndex: parseFloat(healthRiskIndex.toFixed(1)),
        recommendation: recommendation
    };
}
