function calculate({ totalStudentPopulation, percentIEP, budgetSpecialEd, mildNeedsRatio, moderateNeedsRatio, severeNeedsRatio, avgCostMild, avgCostModerate, avgCostSevere }) {

  // Input validation and normalization
  totalStudentPopulation = Math.max(0, totalStudentPopulation || 0);
  percentIEP = Math.max(0, Math.min(100, percentIEP || 0));
  budgetSpecialEd = Math.max(0, budgetSpecialEd || 0);

  let totalRatio = (mildNeedsRatio || 0) + (moderateNeedsRatio || 0) + (severeNeedsRatio || 0);

  // If ratios don't sum to 100 (or close), normalize them proportionally.
  // This ensures student counts are distributed correctly based on user's relative proportions.
  let normalizedMildRatio = mildNeedsRatio;
  let normalizedModerateRatio = moderateNeedsRatio;
  let normalizedSevereRatio = severeNeedsRatio;

  if (totalRatio > 0 && Math.abs(totalRatio - 100) > 0.1) { // Allow for slight floating point inaccuracies
    normalizedMildRatio = (mildNeedsRatio / totalRatio) * 100;
    normalizedModerateRatio = (moderateNeedsRatio / totalRatio) * 100;
    normalizedSevereRatio = (severeNeedsRatio / totalRatio) * 100;
  } else if (totalRatio === 0) { // If all ratios are zero, set them to zero.
    normalizedMildRatio = 0;
    normalizedModerateRatio = 0;
    normalizedSevereRatio = 0;
  }

  avgCostMild = Math.max(0, avgCostMild || 0);
  avgCostModerate = Math.max(0, avgCostModerate || 0);
  avgCostSevere = Math.max(0, avgCostSevere || 0);

  // Step 1: Calculate total IEP students
  const totalIEPStudents = totalStudentPopulation * (percentIEP / 100);

  // Step 2: Distribute IEP students by needs category
  const mildStudentsCount = totalIEPStudents * (normalizedMildRatio / 100);
  const moderateStudentsCount = totalIEPStudents * (normalizedModerateRatio / 100);
  const severeStudentsCount = totalIEPStudents * (normalizedSevereRatio / 100);

  // Step 3: Calculate the recommended optimal budget
  const recommendedBudget = 
    (mildStudentsCount * avgCostMild) + 
    (moderateStudentsCount * avgCostModerate) + 
    (severeStudentsCount * avgCostSevere);

  // Step 4: Determine budget gap or surplus
  const budgetGapSurplus = budgetSpecialEd - recommendedBudget;

  // Step 5: Calculate recommended FTE staff based on industry benchmarks (example ratios)
  // These ratios can be customized based on specific district/state guidelines
  const FTE_RATIO_MILD = 1 / 12; // 1 FTE for every 12 mild needs students
  const FTE_RATIO_MODERATE = 1 / 8; // 1 FTE for every 8 moderate needs students
  const FTE_RATIO_SEVERE = 1 / 5; // 1 FTE for every 5 severe needs students

  const recommendedStaffFTE = 
    (mildStudentsCount * FTE_RATIO_MILD) +
    (moderateStudentsCount * FTE_RATIO_MODERATE) +
    (severeStudentsCount * FTE_RATIO_SEVERE);

  // Step 6: Calculate average cost per IEP student based on recommended budget
  const avgCostPerIEP = totalIEPStudents > 0 ? recommendedBudget / totalIEPStudents : 0;

  return {
    totalIEPStudents: Math.round(totalIEPStudents),
    mildStudentsCount: Math.round(mildStudentsCount),
    moderateStudentsCount: Math.round(moderateStudentsCount),
    severeStudentsCount: Math.round(severeStudentsCount),
    recommendedBudget: recommendedBudget,
    budgetGapSurplus: budgetGapSurplus,
    recommendedStaffFTE: recommendedStaffFTE,
    avgCostPerIEP: avgCostPerIEP
  };
}
