function calculate() {
    const totalNetProfitLoss = parseFloat(inputs.totalNetProfitLoss);
    const initialBankroll = parseFloat(inputs.initialBankroll);
    const numberPeriods = parseFloat(inputs.numberPeriods);
    const stdDevOfPeriodicReturnsPercent = parseFloat(inputs.stdDevOfPeriodicReturnsPercent);
    const riskFreeRateAnnualPercent = parseFloat(inputs.riskFreeRateAnnualPercent);
    const annualizationFactor = parseFloat(inputs.annualizationFactor);

    if (initialBankroll <= 0) {
        throw new Error("Initial Bankroll must be a positive number.");
    }
    if (numberPeriods <= 0) {
        throw new Error("Number of Periods must be a positive number.");
    }
    if (stdDevOfPeriodicReturnsPercent < 0) {
        throw new Error("Standard Deviation of Periodic Returns cannot be negative.");
    }
    if (annualizationFactor <= 0) {
        throw new Error("Annualization Factor must be a positive number.");
    }

    const totalReturnDecimal = totalNetProfitLoss / initialBankroll;
    const stdDevPeriodicDecimal = stdDevOfPeriodicReturnsPercent / 100;
    const riskFreeRateDecimal = riskFreeRateAnnualPercent / 100;

    // Annualized Return (compounded)
    let annualizedReturnDecimal;
    // Ensure (1 + totalReturnDecimal) is not negative for Math.pow
    if (1 + totalReturnDecimal <= 0) {
        // If total return is -100% or more, resulting in 0 or negative 1+return, compounding becomes problematic.
        // Assuming an absolute floor for return at -100%
        annualizedReturnDecimal = -1;
    } else {
        annualizedReturnDecimal = Math.pow(1 + totalReturnDecimal, annualizationFactor / numberPeriods) - 1;
    }

    // Annualized Volatility
    const annualizedVolatilityDecimal = stdDevPeriodicDecimal * Math.sqrt(annualizationFactor);

    let sharpeRatio = 0;
    if (annualizedVolatilityDecimal === 0) {
        // If volatility is zero, Sharpe Ratio is infinite (if return > risk-free rate) or zero/negative infinite
        sharpeRatio = (annualizedReturnDecimal - riskFreeRateDecimal) > 0 ? Infinity : ((annualizedReturnDecimal - riskFreeRateDecimal) < 0 ? -Infinity : 0);
    } else {
        sharpeRatio = (annualizedReturnDecimal - riskFreeRateDecimal) / annualizedVolatilityDecimal;
    }

    return {
        totalReturnPercent: totalReturnDecimal * 100,
        annualizedReturnPercent: annualizedReturnDecimal * 100,
        annualizedVolatilityPercent: annualizedVolatilityDecimal * 100,
        sharpeRatio: sharpeRatio
    };
}
