const cmPerUnit = inputs.avgSellingPricePerUnit - inputs.avgVariableCostPerUnit;
let breakevenUnits = Infinity;
let breakevenRevenue = Infinity;

if (cmPerUnit > 0) {
    breakevenUnits = inputs.monthlyFixedCosts / cmPerUnit;
    breakevenRevenue = breakevenUnits * inputs.avgSellingPricePerUnit;
} else if (inputs.monthlyFixedCosts > 0) {
    // If contribution margin is non-positive but fixed costs exist, breakeven is impossible
    breakevenUnits = Infinity; // Represent as impossible
    breakevenRevenue = Infinity;
} else {
    // If both fixed costs and contribution margin are 0 or negative (or CM is positive, FC is 0)
    // This case means breakeven is 0 units if no fixed costs and CM is positive, or 0 if all are 0.
    // Given the context of a startup, assume fixed costs > 0 is common.
    breakevenUnits = 0;
    breakevenRevenue = 0;
}

const totalVariableCostsAtTarget = inputs.targetMonthlySalesUnits * inputs.avgVariableCostPerUnit;
const totalRevenueAtTarget = inputs.targetMonthlySalesUnits * inputs.avgSellingPricePerUnit;

const monthlyProfitBeforeTax = totalRevenueAtTarget - totalVariableCostsAtTarget - inputs.monthlyFixedCosts;
const monthlyProfitAfterTax = monthlyProfitBeforeTax * (1 - (inputs.taxRate / 100));

let timeToRecoverInvestmentMonths = Infinity;
if (inputs.initialInvestment > 0 && monthlyProfitAfterTax > 0) {
    timeToRecoverInvestmentMonths = inputs.initialInvestment / monthlyProfitAfterTax;
} else if (inputs.initialInvestment === 0) {
    timeToRecoverInvestmentMonths = 0;
}

return {
    breakevenUnits: Math.max(0, breakevenUnits),
    breakevenRevenue: Math.max(0, breakevenRevenue),
    monthlyProfitBeforeTax: monthlyProfitBeforeTax,
    monthlyProfitAfterTax: monthlyProfitAfterTax,
    timeToRecoverInvestmentMonths: Math.max(0, timeToRecoverInvestmentMonths)
};
