function calculateDilution({ your_current_shares, total_shares_pre_round_actual, pre_money_valuation, new_capital_raised, target_post_money_option_pool_percentage, existing_option_pool_shares }) {
    // Convert percentage to decimal
    const targetOptionPoolDecimal = target_post_money_option_pool_percentage / 100;

    // 1. Calculate Initial Fully Diluted Shares Pre-Round (including existing option pool)
    const initial_fully_diluted_shares = total_shares_pre_round_actual + existing_option_pool_shares;

    // Handle edge cases for division by zero
    if (initial_fully_diluted_shares <= 0) {
        // If no shares pre-round, and no new capital, assume no company or calculation not applicable
        if (new_capital_raised === 0) {
            return {
                your_new_ownership_pct: 0,
                total_dilution_from_round_pct: 0,
                fully_diluted_post_money_valuation: 0,
                new_shares_issued_to_investors: 0,
                new_shares_added_to_option_pool: 0,
                total_shares_post_money_fully_diluted: 0
            };
        }
        // If new capital but no pre-existing shares, this is an unusual scenario. Assume new shares determine value.
        // We'll proceed with pps calculation but need to be careful with inputs.
    }

    // If pre_money_valuation is 0, but new_capital_raised is > 0 and initial_fully_diluted_shares > 0
    // this implies an infinite price per share if new_capital_raised > 0 and pre_money_valuation = 0.
    // Or it means the company had no value pre-money, but is getting investment.
    // For a calculator, we need to make a pragmatic assumption or indicate error.
    // Let's assume a floor for PPS to avoid NaN or Infinity if pre_money_valuation is extremely low or zero.
    const effective_pre_money_valuation = Math.max(pre_money_valuation, 0.01); // Prevent division by zero if pre-money is 0, allow a tiny value.

    // 2. Calculate Price Per Share (PPS) for the new investment
    // This PPS is determined by the pre-money valuation divided by the fully diluted shares pre-round.
    // If initial_fully_diluted_shares is 0, pps would be Infinity or NaN. Handled above.
    const pps = effective_pre_money_valuation / initial_fully_diluted_shares;

    // 3. Calculate Shares Issued to New Investors
    // If pps is 0 (due to initial_fully_diluted_shares being effectively infinite or very large compared to valuation),
    // or pre_money_valuation is 0 and initial_fully_diluted_shares > 0, this can lead to issues.
    // If pps is extremely small, new_shares_issued_to_investors can be very large.
    const new_shares_issued_to_investors = pps > 0 ? new_capital_raised / pps : 0; // If pps is effectively zero, no shares issued for new capital.

    // 4. Calculate shares needed for the *new* option pool to meet its target post-money percentage.
    // This calculation ensures the option pool reaches the target percentage of the *final* fully diluted cap table.

    // Shares that exist *before* the option pool is adjusted for this round, but *after* new investment shares.
    const shares_before_option_pool_adjustment = initial_fully_diluted_shares + new_shares_issued_to_investors;

    let new_shares_added_to_option_pool_calc;
    if (targetOptionPoolDecimal >= 1) { // If target is 100% or more, everyone gets diluted to 0 except for option pool (unrealistic)
        new_shares_added_to_option_pool_calc = Infinity;
    } else {
        // The formula for new shares 'y' needed to hit target 'T' based on base shares 'B' and existing pool 'E':
        // y = (T * B - E) / (1 - T)
        new_shares_added_to_option_pool_calc = (targetOptionPoolDecimal * shares_before_option_pool_adjustment - existing_option_pool_shares) / (1 - targetOptionPoolDecimal);
    }

    // Ensure new_shares_added_to_option_pool is not negative (we don't remove shares from the pool if it's already larger than target)
    const new_shares_added_to_option_pool = Math.max(0, new_shares_added_to_option_pool_calc);

    // 5. Calculate Total Shares Post-Money Fully Diluted
    const total_shares_post_money_fully_diluted = initial_fully_diluted_shares + new_shares_issued_to_investors + new_shares_added_to_option_pool;

    // 6. Calculate Your Initial Ownership Percentage (Pre-Round)
    const initial_ownership_pct = initial_fully_diluted_shares > 0 ? (your_current_shares / initial_fully_diluted_shares) * 100 : 0;

    // 7. Calculate Your New Ownership Percentage (Post-Round)
    const your_new_ownership_pct = total_shares_post_money_fully_diluted > 0
        ? (your_current_shares / total_shares_post_money_fully_diluted) * 100
        : 0;

    // 8. Calculate Total Dilution From Round
    const total_dilution_from_round_pct = initial_ownership_pct - your_new_ownership_pct;

    // 9. Fully Diluted Post-Money Valuation (reflects the value of the company after all dilution, at the price investors paid)
    const fully_diluted_post_money_valuation = total_shares_post_money_fully_diluted * pps;

    return {
        your_new_ownership_pct: your_new_ownership_pct,
        total_dilution_from_round_pct: total_dilution_from_round_pct,
        fully_diluted_post_money_valuation: fully_diluted_post_money_valuation,
        new_shares_issued_to_investors: new_shares_issued_to_investors,
        new_shares_added_to_option_pool: new_shares_added_to_option_pool,
        total_shares_post_money_fully_diluted: total_shares_post_money_fully_diluted
    };
}
