const investorReputationScore = parseFloat(inputs.investorReputationScore);
const investorSanctionsHistory = inputs.investorSanctionsHistory.toLowerCase() === 'yes';
const sourceGeographicRiskIndex = parseFloat(inputs.sourceGeographicRiskIndex);
const sourceIndustryOverlapRisk = parseFloat(inputs.sourceIndustryOverlapRisk);
const fundingAmountUSD = parseFloat(inputs.fundingAmountUSD);
const dueDiligenceEffortLevel = parseFloat(inputs.dueDiligenceEffortLevel);
const transparencyIndex = parseFloat(inputs.transparencyIndex);
const founderEthicalStance = parseFloat(inputs.founderEthicalStance);

// Input validation and clamping
const clamp = (val, min, max) => Math.min(Math.max(val, min), max);

const repScore = clamp(investorReputationScore, 1, 10);
const geoRisk = clamp(sourceGeographicRiskIndex, 1, 5);
const indOverlap = clamp(sourceIndustryOverlapRisk, 1, 5);
const ddLevel = clamp(dueDiligenceEffortLevel, 1, 5);
const transIndex = clamp(transparencyIndex, 1, 5);
const founderEthic = clamp(founderEthicalStance, 1, 5);

let ethicalRisk = 0;
let reputationalRisk = 0;
let financialStabilityRisk = 0;

// 1. Calculate Base Ethical Risk
// Inverse of investor reputation (higher score = lower risk), founder ethics (higher = lower risk), transparency (higher = lower risk)
ethicalRisk += (10 - repScore) * 3; // Max 27
ethicalRisk += (5 - founderEthic) * 5; // Max 20
ethicalRisk += (5 - transIndex) * 4; // Max 16
ethicalRisk += investorSanctionsHistory ? 30 : 0; // High impact for sanctions
ethicalRisk = clamp(ethicalRisk, 0, 100);

// 2. Calculate Base Reputational Risk
// Inverse of investor reputation, geographic risk, industry overlap
reputationalRisk += (10 - repScore) * 2.5; // Max 22.5
reputationalRisk += geoRisk * 6; // Max 30
reputationalRisk += indOverlap * 5; // Max 25
reputationalRisk += investorSanctionsHistory ? 25 : 0; // Significant impact for sanctions
reputationalRisk = clamp(reputationalRisk, 0, 100);

// 3. Calculate Base Financial Stability Risk (from source perspective)
// Sanctions history is a major red flag for financial stability/legitimacy
// High funding from a risky source could indicate less scrutiny or desperate measures
financialStabilityRisk += investorSanctionsHistory ? 40 : 0;
financialStabilityRisk += (geoRisk + indOverlap) * 3; // Max 30
financialStabilityRisk += (fundingAmountUSD > 1000000 && (geoRisk > 3 || indOverlap > 3)) ? 20 : 0; // Large funding from risky sources
financialStabilityRisk = clamp(financialStabilityRisk, 0, 100);

// 4. Aggregate Overall Risk
let overallRiskScore = (ethicalRisk * 0.4 + reputationalRisk * 0.4 + financialStabilityRisk * 0.2);

// 5. Adjust for Due Diligence Effort Level (mitigating factor)
// Higher due diligence level reduces perceived risk.
// ddLevel 1 (minimal) = 0% reduction, ddLevel 5 (extensive) = 20% reduction
const ddReductionFactor = 1 - ((ddLevel - 1) / 4 * 0.2);
overallRiskScore = overallRiskScore * ddReductionFactor;

overallRiskScore = Math.round(clamp(overallRiskScore, 0, 100));

const getRiskRating = (score) => {
  if (score >= 75) return 'Very High';
  if (score >= 50) return 'High';
  if (score >= 25) return 'Medium';
  return 'Low';
};

return {
  overallRiskScore: overallRiskScore,
  ethicalRiskRating: getRiskRating(ethicalRisk),
  reputationalRiskRating: getRiskRating(reputationalRisk),
  financialStabilityRisk: getRiskRating(financialStabilityRisk)
};
