function (inputs) {
    const initialFunding = inputs.initialFunding || 0;
    const jobCreationPerMillion = inputs.jobCreationPerMillion || 0;
    const avgDirectJobSalary = inputs.avgDirectJobSalary || 0;
    const localSpendingMultiplier = inputs.localSpendingMultiplier || 0;
    const projectDurationYears = inputs.projectDurationYears || 0;
    const stateLocalTaxRate = inputs.stateLocalTaxRate || 0;
    const adminOverheadRate = inputs.adminOverheadRate || 0;
    const indirectJobFactor = inputs.indirectJobFactor || 0;
    const avgIndirectJobSalary = inputs.avgIndirectJobSalary || 0;

    if (initialFunding <= 0 || projectDurationYears <= 0) {
        return {
            totalDirectJobs: 0,
            totalIndirectJobs: 0,
            totalJobs: 0,
            totalNewWages: 0,
            grossEconomicOutput: 0,
            totalTaxRevenue: 0,
            netEconomicImpact: 0
        };
    }

    const effectiveProgramSpend = Math.max(0, initialFunding * (1 - adminOverheadRate / 100));

    const totalDirectJobs = (effectiveProgramSpend / 1_000_000) * jobCreationPerMillion;
    const totalIndirectJobs = totalDirectJobs * indirectJobFactor;
    const totalJobs = totalDirectJobs + totalIndirectJobs;

    const directWagesAnnual = totalDirectJobs * avgDirectJobSalary;
    const indirectWagesAnnual = totalIndirectJobs * avgIndirectJobSalary;
    const totalNewWages = (directWagesAnnual + indirectWagesAnnual) * projectDurationYears;

    const grossEconomicOutput = effectiveProgramSpend * localSpendingMultiplier;

    const totalTaxRevenue = totalNewWages * (stateLocalTaxRate / 100);

    const netEconomicImpact = grossEconomicOutput - initialFunding;

    return {
        totalDirectJobs: Math.round(totalDirectJobs),
        totalIndirectJobs: Math.round(totalIndirectJobs),
        totalJobs: Math.round(totalJobs),
        totalNewWages: Math.round(totalNewWages),
        grossEconomicOutput: Math.round(grossEconomicOutput),
        totalTaxRevenue: Math.round(totalTaxRevenue),
        netEconomicImpact: Math.round(netEconomicImpact)
    };
}
