function calculate({ currentAnnualHealthcareBudget, federalFundingPercentage, proposedFederalFundingCutPercentage, stateMitigationCapacityPercentage, totalStatePopulation, vulnerablePopulationRatio, currentPrimaryCareAccessScore, currentSpecialtyCareAccessScore, averageCostPerPatientVisit, preventionServiceFundingAllocationPercentage }) { 
  // 1. Calculate the initial federal funding component
  const federalFundingAmount = currentAnnualHealthcareBudget * (federalFundingPercentage / 100);

  // 2. Calculate the raw federal funding cut
  const rawFederalCutAmount = federalFundingAmount * (proposedFederalFundingCutPercentage / 100);

  // 3. Calculate the mitigated funding cut
  let mitigatedCutAmount = rawFederalCutAmount * (1 - (stateMitigationCapacityPercentage / 100));

  // Ensure the net funding reduction doesn't exceed the original federal funding amount or become negative
  let netFundingReduction = Math.max(0, Math.min(mitigatedCutAmount, federalFundingAmount));

  // Edge case: If no budget or no cut, then no impact
  if (currentAnnualHealthcareBudget <= 0 || netFundingReduction <= 0) {
    return {
      netFundingReduction: 0,
      equivalentServiceUnitsLost: 0,
      projectedPatientsLosingAccess: 0,
      projectedVulnerablePatientsImpacted: 0,
      primaryCareAccessImpactScore: parseFloat(currentPrimaryCareAccessScore.toFixed(1)),
      specialtyCareAccessImpactScore: parseFloat(currentSpecialtyCareAccessScore.toFixed(1)),
      preventionServiceReductionPercentage: 0,
      potentialIncreaseInERVisits: 0
    };
  }

  // 4. Calculate equivalent service units lost
  const equivalentServiceUnitsLost = averageCostPerPatientVisit > 0 ? Math.round(netFundingReduction / averageCostPerPatientVisit) : 0;

  // 5. Project patients losing access (heuristic: 10% of service units lost results in unique patients losing significant access).
  const projectedPatientsLosingAccess = Math.round(equivalentServiceUnitsLost * 0.1);

  // Cap projected patients at total population
  const actualProjectedPatientsLosingAccess = Math.min(projectedPatientsLosingAccess, totalStatePopulation);

  // 6. Project vulnerable patients impacted
  const projectedVulnerablePatientsImpacted = Math.round(actualProjectedPatientsLosingAccess * vulnerablePopulationRatio);

  // 7. Impact on access scores
  // Heuristic: for every $1M net funding reduction, Primary Care score drops by 0.1 and Specialty by 0.08.
  const scoreDropFactor = netFundingReduction / 1000000; // per million dollars

  let primaryCareAccessImpactScore = currentPrimaryCareAccessScore - (scoreDropFactor * 0.1);
  let specialtyCareAccessImpactScore = currentSpecialtyCareAccessScore - (scoreDropFactor * 0.08);

  // Cap scores between 1 and 10
  primaryCareAccessImpactScore = Math.max(1, Math.min(10, primaryCareAccessImpactScore));
  specialtyCareAccessImpactScore = Math.max(1, Math.min(10, specialtyCareAccessImpactScore));

  // 8. Preventative service reduction
  // Heuristic: Assume 15% of the net funding reduction directly impacts preventative services if prevention funding is available.
  const preventionFundingComponent = currentAnnualHealthcareBudget * (preventionServiceFundingAllocationPercentage / 100);

  let preventionServiceReductionPercentage = 0;
  if (preventionFundingComponent > 0) {
    const preventionSpecificCut = Math.min(netFundingReduction * 0.15, preventionFundingComponent);
    preventionServiceReductionPercentage = (preventionSpecificCut / preventionFundingComponent) * 100;
  }
  // Cap at 100%
  preventionServiceReductionPercentage = Math.min(100, preventionServiceReductionPercentage);


  // 9. Potential increase in ER visits
  // Heuristic: 5% of patients losing access will turn to ER for non-urgent care.
  const potentialIncreaseInERVisits = Math.round(actualProjectedPatientsLosingAccess * 0.05);

  return {
    netFundingReduction: Math.round(netFundingReduction),
    equivalentServiceUnitsLost: equivalentServiceUnitsLost,
    projectedPatientsLosingAccess: actualProjectedPatientsLosingAccess,
    projectedVulnerablePatientsImpacted: projectedVulnerablePatientsImpacted,
    primaryCareAccessImpactScore: parseFloat(primaryCareAccessImpactScore.toFixed(1)),
    specialtyCareAccessImpactScore: parseFloat(specialtyCareAccessImpactScore.toFixed(1)),
    preventionServiceReductionPercentage: parseFloat(preventionServiceReductionPercentage.toFixed(1)),
    potentialIncreaseInERVisits: potentialIncreaseInERVisits
  };
}
