function calculate() {
    const estimated_dwelling_repair_cost = parseFloat(inputs.estimated_dwelling_repair_cost);
    const estimated_personal_property_damage_cost = parseFloat(inputs.estimated_personal_property_damage_cost);
    const policy_dwelling_limit = parseFloat(inputs.policy_dwelling_limit);
    const policy_personal_property_limit = parseFloat(inputs.policy_personal_property_limit);
    const deductible_amount = parseFloat(inputs.deductible_amount);
    const depreciation_rate_dwelling = parseFloat(inputs.depreciation_rate_dwelling);
    const depreciation_rate_personal_property = parseFloat(inputs.depreciation_rate_personal_property);
    const has_rcv_policy = inputs.has_rcv_policy.toLowerCase() === 'yes';
    const other_unreimbursed_expenses = parseFloat(inputs.other_unreimbursed_expenses);

    // Input validation (basic checks for non-negative values)
    if (isNaN(estimated_dwelling_repair_cost) || estimated_dwelling_repair_cost < 0) return { error: 'Invalid dwelling repair cost.' };
    if (isNaN(estimated_personal_property_damage_cost) || estimated_personal_property_damage_cost < 0) return { error: 'Invalid personal property damage cost.' };
    if (isNaN(policy_dwelling_limit) || policy_dwelling_limit < 0) return { error: 'Invalid dwelling limit.' };
    if (isNaN(policy_personal_property_limit) || policy_personal_property_limit < 0) return { error: 'Invalid personal property limit.' };
    if (isNaN(deductible_amount) || deductible_amount < 0) return { error: 'Invalid deductible amount.' };
    if (isNaN(depreciation_rate_dwelling) || depreciation_rate_dwelling < 0 || depreciation_rate_dwelling > 1) return { error: 'Invalid dwelling depreciation rate (0-1).' };
    if (isNaN(depreciation_rate_personal_property) || depreciation_rate_personal_property < 0 || depreciation_rate_personal_property > 1) return { error: 'Invalid personal property depreciation rate (0-1).' };
    if (isNaN(other_unreimbursed_expenses) || other_unreimbursed_expenses < 0) return { error: 'Invalid other unreimbursed expenses.' };


    // 1. Determine Dwelling Value for Claim (RCV vs. ACV)
    let dwelling_value_considered;
    if (has_rcv_policy) {
        dwelling_value_considered = estimated_dwelling_repair_cost;
    } else {
        // Actual Cash Value (ACV) = Replacement Cost - Depreciation
        dwelling_value_considered = estimated_dwelling_repair_cost * (1 - depreciation_rate_dwelling);
        dwelling_value_considered = Math.max(0, dwelling_value_considered); // Ensure no negative values
    }

    // 2. Determine Personal Property Value for Claim (RCV vs. ACV)
    let personal_property_value_considered;
    if (has_rcv_policy) {
        personal_property_value_considered = estimated_personal_property_damage_cost;
    } else {
        // Actual Cash Value (ACV) = Replacement Cost - Depreciation
        personal_property_value_considered = estimated_personal_property_damage_cost * (1 - depreciation_rate_personal_property);
        personal_property_value_considered = Math.max(0, personal_property_value_considered); // Ensure no negative values
    }

    // 3. Apply Policy Limits Individually
    const covered_dwelling_amount = Math.min(dwelling_value_considered, policy_dwelling_limit);
    const covered_personal_property_amount = Math.min(personal_property_value_considered, policy_personal_property_limit);

    // 4. Calculate Total Gross Covered Loss (before deductible)
    const total_gross_covered_loss = covered_dwelling_amount + covered_personal_property_amount;

    // 5. Apply Deductible
    // The deductible is applied to the total covered loss. If the total loss is less than the deductible, the payout is zero.
    const estimated_payout = Math.max(0, total_gross_covered_loss - deductible_amount);

    // 6. Calculate Total Out-of-Pocket Cost
    // This includes the deductible (up to the total covered loss) and other unreimbursed expenses.
    const actual_deductible_paid = Math.min(deductible_amount, total_gross_covered_loss); // User pays deductible up to the covered loss
    const total_out_of_pocket = actual_deductible_paid + other_unreimbursed_expenses;

    // 7. Calculate Potential Uncovered Damage/Loss
    // This is the total estimated damage minus the actual insurance payout and the deductible covered by the homeowner.
    const total_estimated_damage = estimated_dwelling_repair_cost + estimated_personal_property_damage_cost;
    const potential_uncovered_damage = Math.max(0, total_estimated_damage - estimated_payout - actual_deductible_paid);

    // Edge Case: If total_gross_covered_loss is 0, then actual_deductible_paid should also be 0 for uncovered_damage calculation consistency.
    // Re-evaluating: potential_uncovered_damage represents damage NOT paid by insurer OR homeowner's deductible.
    // So, it's (Total Damage) - (Insurer Payout) - (Deductible Met By Covered Loss).
    // This is correct as calculated.

    return {
        dwelling_value_considered: dwelling_value_considered,
        personal_property_value_considered: personal_property_value_considered,
        covered_dwelling_amount: covered_dwelling_amount,
        covered_personal_property_amount: covered_personal_property_amount,
        total_gross_covered_loss: total_gross_covered_loss,
        estimated_payout: estimated_payout,
        total_out_of_pocket: total_out_of_pocket,
        potential_uncovered_damage: potential_uncovered_damage
    };
}
