function calculate({ initialCostStandard, initialCostResilient, avgDamageStandard, stormFrequency, insurancePremiumStandard, insurancePremiumResilient, avgTemporaryLivingCost, projectLifespan, discountRate }) {

    const discountRateDecimal = discountRate / 100;

    // Input validation and default handling
    initialCostStandard = Math.max(0, initialCostStandard || 0);
    initialCostResilient = Math.max(0, initialCostResilient || 0);
    avgDamageStandard = Math.max(0, avgDamageStandard || 0);
    stormFrequency = Math.max(0, stormFrequency || 0);
    insurancePremiumStandard = Math.max(0, insurancePremiumStandard || 0);
    insurancePremiumResilient = Math.max(0, insurancePremiumResilient || 0);
    avgTemporaryLivingCost = Math.max(0, avgTemporaryLivingCost || 0);
    projectLifespan = Math.max(1, projectLifespan || 1); // Lifespan must be at least 1 year

    // 1. Net Initial Investment
    const netInitialInvestment = initialCostResilient - initialCostStandard;

    // 2. Annual Avoided Costs
    const annualDamageAvoided = avgDamageStandard * stormFrequency;
    const annualInsuranceSavings = insurancePremiumStandard - insurancePremiumResilient;
    const annualLivingCostAvoided = avgTemporaryLivingCost * stormFrequency;

    // 3. Total Annual Savings
    const totalAnnualSavings = annualDamageAvoided + annualInsuranceSavings + annualLivingCostAvoided;

    // 4. Payback Period
    let paybackPeriod = Infinity;
    if (totalAnnualSavings > 0) {
        paybackPeriod = netInitialInvestment / totalAnnualSavings;
        if (paybackPeriod < 0) {
            paybackPeriod = 0; // If resilient is cheaper and saves money annually, payback is immediate
        } else if (paybackPeriod > projectLifespan) {
            paybackPeriod = Infinity; // Will not pay back within project lifespan
        }
    } else if (netInitialInvestment <= 0) {
         paybackPeriod = 0; // If resilient is cheaper or same cost and does not incur losses
    }

    // 5. Net Present Value (NPV) and ROI
    let totalNPV = -netInitialInvestment;
    let sumOfDiscountedSavings = 0;

    for (let t = 1; t <= projectLifespan; t++) {
        const discountedCashFlow = totalAnnualSavings / Math.pow(1 + discountRateDecimal, t);
        sumOfDiscountedSavings += discountedCashFlow;
        totalNPV += discountedCashFlow;
    }

    let roiPercentage = -Infinity; // Default for cases where netInitialInvestment is positive but no savings

    if (netInitialInvestment === 0) {
        if (sumOfDiscountedSavings > 0) {
            roiPercentage = (sumOfDiscountedSavings / initialCostStandard) * 100; // If no extra cost, ROI based on savings vs. standard cost
            if (initialCostStandard === 0) roiPercentage = Infinity; // Avoid division by zero
        } else {
            roiPercentage = 0;
        }
    } else if (netInitialInvestment < 0) { // If resilient is cheaper
        roiPercentage = ((Math.abs(netInitialInvestment) + sumOfDiscountedSavings) / Math.abs(netInitialInvestment)) * 100;
        if (netInitialInvestment === 0) roiPercentage = Infinity; // Handle case where both costs are zero
    } else { // netInitialInvestment > 0
        roiPercentage = (totalNPV / netInitialInvestment) * 100;
    }

    // Edge case: If ROI is negative and net initial investment is positive, it's a loss.
    // If savings are negative overall, ROI should reflect that.
    if (netInitialInvestment > 0 && totalNPV < 0) {
        // Keep the calculated negative ROI
    } else if (netInitialInvestment > 0 && sumOfDiscountedSavings <= 0) {
        roiPercentage = -100; // If initial cost > 0 but no savings, 100% loss relative to investment
    } 

    return {
        netInitialInvestment: netInitialInvestment,
        annualDamageAvoided: annualDamageAvoided,
        annualInsuranceSavings: annualInsuranceSavings,
        annualLivingCostAvoided: annualLivingCostAvoided,
        totalAnnualSavings: totalAnnualSavings,
        paybackPeriod: paybackPeriod === Infinity ? 'Never' : (paybackPeriod < 0 ? 0 : paybackPeriod),
        totalNPV: totalNPV,
        roiPercentage: roiPercentage
    };
}
