const C = Math.min(1, Math.max(0, inputs.runoffCoefficient));
const A = Math.max(0, inputs.drainageArea);
const i = Math.max(0, inputs.rainfallIntensity);
const td = Math.max(0, inputs.detentionTime);
const Q_out = Math.max(0, inputs.allowableReleaseRate);

// Calculate Peak Runoff Rate (cfs)
// The Rational Method's unit consistency: A (acres) * i (in/hr) * C (dimensionless) = Q (cfs) (approximately)
const peakRunoffRate = C * i * A;

// Calculate required detention volume (cubic feet)
// Volume is calculated based on the difference between peak inflow and allowable outflow,
// sustained over the specified detention time.
let effectivePeakToDetain = 0;
if (peakRunoffRate > Q_out) {
    effectivePeakToDetain = peakRunoffRate - Q_out;
}

// Volume = (Effective Flow Rate in cfs) * (Detention Time in seconds)
// Convert detention time from hours to seconds (td * 3600)
const requiredDetentionVolumeCubicFeet = effectivePeakToDetain * td * 3600;

// Convert to Acre-Feet (1 acre-foot = 43560 cubic feet)
const requiredDetentionVolumeAcreFeet = requiredDetentionVolumeCubicFeet / 43560;

return {
    peakRunoffRate: peakRunoffRate,
    requiredDetentionVolumeCubicFeet: requiredDetentionVolumeCubicFeet,
    requiredDetentionVolumeAcreFeet: requiredDetentionVolumeAcreFeet
};
