function calculate({ project_type, project_scale_sqkm, material_cost_per_sqm, labor_cost_per_hour_skilled, estimated_labor_hours_per_sqkm, logistics_complexity_factor, geopolitical_risk_multiplier, initial_project_duration_months, contingency_percentage }) {

  // Input validation and default values
  project_scale_sqkm = parseFloat(project_scale_sqkm) || 0;
  material_cost_per_sqm = parseFloat(material_cost_per_sqm) || 0;
  labor_cost_per_hour_skilled = parseFloat(labor_cost_per_hour_skilled) || 0;
  estimated_labor_hours_per_sqkm = parseFloat(estimated_labor_hours_per_sqkm) || 0;
  logistics_complexity_factor = parseFloat(logistics_complexity_factor) || 1.0; // Min 1.0
  geopolitical_risk_multiplier = parseFloat(geopolitical_risk_multiplier) || 1.0; // Min 1.0
  initial_project_duration_months = parseFloat(initial_project_duration_months) || 0;
  contingency_percentage = parseFloat(contingency_percentage) || 0;

  // Ensure factors are within reasonable bounds
  logistics_complexity_factor = Math.max(1.0, Math.min(logistics_complexity_factor, 3.0));
  geopolitical_risk_multiplier = Math.max(1.0, Math.min(geopolitical_risk_multiplier, 3.0));
  contingency_percentage = Math.max(0, contingency_percentage);

  if (project_scale_sqkm <= 0) {
    return {
      total_estimated_cost: 0,
      risk_adjusted_cost: 0,
      estimated_project_timeline: 0,
      cost_per_sqkm: 0
    };
  }

  // 1. Calculate Base Material and Labor Costs
  let base_material_cost = project_scale_sqkm * 1000000 * material_cost_per_sqm; // Convert sq.km to sq.m
  let base_labor_cost = project_scale_sqkm * estimated_labor_hours_per_sqkm * labor_cost_per_hour_skilled;

  // 2. Apply Project Type Specific Adjustments (example multipliers, can be more complex)
  let type_material_multiplier = 1.0;
  let type_labor_multiplier = 1.0;

  const projectTypeLower = (project_type || '').toLowerCase();
  if (projectTypeLower.includes('naval base')) {
    type_material_multiplier = 1.5; 
    type_labor_multiplier = 1.6; 
  } else if (projectTypeLower.includes('airfield')) {
    type_material_multiplier = 1.3; 
    type_labor_multiplier = 1.4; 
  } else if (projectTypeLower.includes('logistics hub')) {
    type_material_multiplier = 1.2; 
    type_labor_multiplier = 1.2; 
  } else if (projectTypeLower.includes('forward operating base') || projectTypeLower.includes('fob')) {
    type_material_multiplier = 1.1; 
    type_labor_multiplier = 1.1; 
  } else if (projectTypeLower.includes('data center') || projectTypeLower.includes('secure facility')) {
    type_material_multiplier = 1.7;
    type_labor_multiplier = 1.5;
  }

  base_material_cost *= type_material_multiplier;
  base_labor_cost *= type_labor_multiplier;

  // 3. Apply Logistics Complexity
  let logistics_adjusted_material_cost = base_material_cost * logistics_complexity_factor;
  let logistics_adjusted_labor_cost = base_labor_cost * logistics_complexity_factor;

  // 4. Calculate Subtotal and add Contingency
  let subtotal_cost = logistics_adjusted_material_cost + logistics_adjusted_labor_cost;
  let total_estimated_cost = subtotal_cost * (1 + contingency_percentage / 100);

  // 5. Apply Geopolitical Risk Multiplier
  let risk_adjusted_cost = total_estimated_cost * geopolitical_risk_multiplier;

  // 6. Estimate Project Timeline
  // Base timeline adjusted by complexity and risk
  let estimated_project_timeline = initial_project_duration_months * 
                                   (1 + (logistics_complexity_factor - 1) * 0.2) * 
                                   (1 + (geopolitical_risk_multiplier - 1) * 0.15);
  // Ensure minimum timeline
  estimated_project_timeline = Math.max(initial_project_duration_months, estimated_project_timeline);

  // 7. Calculate Cost per Square Kilometer
  let cost_per_sqkm = risk_adjusted_cost / project_scale_sqkm;

  return {
    total_estimated_cost: total_estimated_cost,
    risk_adjusted_cost: risk_adjusted_cost,
    estimated_project_timeline: estimated_project_timeline,
    cost_per_sqkm: cost_per_sqkm
  };
}
