function calculate({ initialDiversificationCost, annualDiversificationOperatingCost, currentGeopoliticalRiskExposure, riskReductionPercentage, currentSupplyChainDisruptionCost, disruptionReductionPercentage, expectedAnnualSupplyCostSavings, projectHorizonYears, discountRate }) {
  const initialInvestment = initialDiversificationCost;
  const annualOperatingCost = annualDiversificationOperatingCost;

  // Step 1: Calculate annual benefits from risk mitigation and cost savings
  const annualGeopoliticalRiskMitigation = currentGeopoliticalRiskExposure * (riskReductionPercentage / 100);
  const annualSupplyChainDisruptionMitigation = currentSupplyChainDisruptionCost * (disruptionReductionPercentage / 100);
  const annualRiskMitigationBenefit = annualGeopoliticalRiskMitigation + annualSupplyChainDisruptionMitigation;
  const totalAnnualBenefit = annualRiskMitigationBenefit + expectedAnnualSupplyCostSavings;

  // Step 2: Calculate net annual cash flow
  const netAnnualCashFlowOperating = totalAnnualBenefit - annualOperatingCost;

  // Step 3: Calculate Payback Period
  let paybackPeriodYears;
  if (netAnnualCashFlowOperating <= 0) {
    paybackPeriodYears = Infinity; // Project never pays back or loses money annually
  } else if (initialInvestment <= 0) {
    paybackPeriodYears = 0; // No initial investment means immediate payback if cash flow is positive
  } else {
    paybackPeriodYears = initialInvestment / netAnnualCashFlowOperating;
  }

  // Step 4: Calculate Total ROI over the project horizon
  let totalDiversificationROI;
  if (initialInvestment === 0) {
    totalDiversificationROI = netAnnualCashFlowOperating > 0 ? Infinity : 0; // Infinite ROI if no cost and positive cash flow
  } else {
    const totalBenefitOverHorizon = netAnnualCashFlowOperating * projectHorizonYears;
    totalDiversificationROI = ((totalBenefitOverHorizon - initialInvestment) / initialInvestment) * 100;
  }

  // Step 5: Calculate Net Present Value (NPV)
  let netPresentValue = -initialInvestment;
  const rate = discountRate / 100;

  if (rate <= 0) { // If discount rate is 0 or negative, sum up cash flows directly (or treat as 0% for practical purposes)
    netPresentValue += netAnnualCashFlowOperating * projectHorizonYears;
  } else {
    for (let i = 1; i <= projectHorizonYears; i++) {
      netPresentValue += netAnnualCashFlowOperating / Math.pow((1 + rate), i);
    }
  }

  return {
    totalDiversificationROI: isFinite(totalDiversificationROI) ? totalDiversificationROI : (totalDiversificationROI === Infinity ? 'Infinite' : 'N/A'),
    paybackPeriodYears: isFinite(paybackPeriodYears) ? paybackPeriodYears : (paybackPeriodYears === Infinity ? 'Never' : 'N/A'),
    annualRiskMitigationBenefit: annualRiskMitigationBenefit,
    netPresentValue: netPresentValue
  };
}
