const materialProps = {
    "Steel A36": { yieldStrength: 250, density: 7850, costPerKg: 1.50 }, // MPa, kg/m^3, $/kg
    "Aluminum 6061-T6": { yieldStrength: 276, density: 2700, costPerKg: 3.00 },
    "Titanium Grade 5": { yieldStrength: 830, density: 4430, costPerKg: 30.00 },
    "Carbon Fiber Composite (Approx)": { yieldStrength: 1000, density: 1600, costPerKg: 50.00 },
    "Concrete (Reinforced, Approx Tension)": { yieldStrength: 30, density: 2400, costPerKg: 0.10 }
};

let finalYieldStrength;
let finalDensity;
let finalCostPerKg;

// Determine which material properties to use (custom or predefined)
if (customYieldStrength && customDensity && customCostPerKg) {
    finalYieldStrength = customYieldStrength;
    finalDensity = customDensity;
    finalCostPerKg = customCostPerKg;
} else {
    const selectedMaterial = materialProps[materialType];
    if (selectedMaterial) {
        finalYieldStrength = selectedMaterial.yieldStrength;
        finalDensity = selectedMaterial.density;
        finalCostPerKg = selectedMaterial.costPerKg;
    } else {
        // Fallback to Steel A36 if materialType is unknown and no custom values are provided
        const defaultMaterial = materialProps["Steel A36"];
        finalYieldStrength = defaultMaterial.yieldStrength;
        finalDensity = defaultMaterial.density;
        finalCostPerKg = defaultMaterial.costPerKg;
        // In a real UI, you might display a warning here.
    }
}

// Ensure material properties are valid numbers
if (isNaN(finalYieldStrength) || finalYieldStrength <= 0) throw new Error("Invalid Yield Strength. Please provide a positive number.");
if (isNaN(finalDensity) || finalDensity <= 0) throw new Error("Invalid Density. Please provide a positive number.");
if (isNaN(finalCostPerKg) || finalCostPerKg < 0) throw new Error("Invalid Cost per Kg. Please provide a non-negative number.");

// Calculations
const designLoadN = appliedLoad * 1000 * safetyFactor; // Convert kN to N, apply safety factor
const yieldStrengthPa = finalYieldStrength * 1_000_000; // Convert MPa to Pa (N/m^2)

let requiredCrossSectionalArea = designLoadN / yieldStrengthPa; // m^2

// Cap minimum area to a practical value (e.g., 1 cm^2 = 0.0001 m^2)
const minPracticalArea = 0.0001;
requiredCrossSectionalArea = Math.max(requiredCrossSectionalArea, minPracticalArea);

const equivalentSquareSide = Math.sqrt(requiredCrossSectionalArea); // m

const totalVolume = requiredCrossSectionalArea * elementLength; // m^3
const totalElementMass = totalVolume * finalDensity; // kg
const totalMaterialCost = totalElementMass * finalCostPerKg; // $

const materialStrengthToWeightRatio = finalYieldStrength / finalDensity; // MPa / (kg/m^3) which is N*m/kg

return {
    requiredCrossSectionalArea,
    equivalentSquareSide,
    totalElementMass,
    totalMaterialCost,
    materialStrengthToWeightRatio
};
