function calculate() {
  const distance_source_m = inputs.distance_source_m;
  const peak_particle_velocity_mm_s = inputs.peak_particle_velocity_mm_s;
  const building_type = inputs.building_type;
  const material_resilience = inputs.material_resilience;
  const foundation_condition = inputs.foundation_condition;
  const building_age_years = inputs.building_age_years;

  // Input validation and default values
  if (isNaN(distance_source_m) || distance_source_m <= 0) return { error: "Distance to source must be a positive number." };
  if (isNaN(peak_particle_velocity_mm_s) || peak_particle_velocity_mm_s < 0) return { error: "Peak Particle Velocity must be a non-negative number." };
  if (isNaN(material_resilience) || material_resilience < 0 || material_resilience > 1) return { error: "Material Resilience must be between 0 and 1." };
  if (isNaN(foundation_condition) || foundation_condition < 0 || foundation_condition > 1) return { error: "Foundation Condition must be between 0 and 1." };
  if (isNaN(building_age_years) || building_age_years < 0) return { error: "Building Age must be a non-negative number." };

  // Factors for calculation
  // 1. Vibration Intensity Factor: Relative to a common damage threshold (e.g., 7.5 mm/s for minor cosmetic damage).
  // Capped to prevent extreme values from very high PPV.
  const vibration_intensity_factor = Math.min(3.0, peak_particle_velocity_mm_s / 7.5);

  // 2. Distance Decay Factor: Vibration intensity typically decreases with distance. 
  // Using a simplified inverse relationship for demonstration.
  const distance_decay_factor = 1 / (1 + distance_source_m / 25);

  // 3. Building Type Factor: Different building types have varying susceptibility.
  let building_factor = 1.0;
  switch (building_type) {
    case "Residential (Wood/Light Frame)":
      building_factor = 0.8; // More flexible, less susceptible to minor cracks
      break;
    case "Residential (Masonry)":
      building_factor = 1.4; // More rigid, more susceptible to cracking
      break;
    case "Commercial (Steel/Concrete)":
      building_factor = 0.7; // Generally robust
      break;
    case "Historic/Sensitive":
      building_factor = 1.8; // Often fragile, high sensitivity
      break;
    default:
      building_factor = 1.0; // Default or unknown type
  }

  // 4. Material Resilience Factor: Lower resilience means higher damage susceptibility.
  const resilience_factor = (1 - material_resilience) * 1.5; // Scale so lower resilience increases risk more significantly

  // 5. Foundation Condition Factor: Poor foundation condition increases vulnerability.
  const foundation_factor = (1 - foundation_condition) * 1.2; // Scale so poorer condition increases risk

  // 6. Building Age Factor: Older buildings may be more susceptible due to material degradation, pre-existing conditions.
  const age_factor = Math.min(2.0, 1 + (building_age_years / 60)); // Cap age effect

  // Combine factors to get a raw risk score
  let raw_risk_score = 
    vibration_intensity_factor * 
    distance_decay_factor * 
    building_factor * 
    resilience_factor * 
    foundation_factor * 
    age_factor * 
    10; // Base scaling constant to bring it into a reasonable range

  // Final damage risk score, capped between 0 and 100
  const damage_risk_score = Math.min(100, Math.max(0, raw_risk_score * 5)); // Further scaling to fill 0-100 range

  // Determine damage risk category
  let damage_risk_category;
  if (damage_risk_score < 15) {
    damage_risk_category = "Very Low Risk";
  } else if (damage_risk_score < 35) {
    damage_risk_category = "Low Risk";
  } else if (damage_risk_score < 60) {
    damage_risk_category = "Moderate Risk";
  } else if (damage_risk_score < 85) {
    damage_risk_category = "High Risk";
  } else {
    damage_risk_category = "Severe Risk";
  }

  return {
    damage_risk_score: damage_risk_score,
    damage_risk_category: damage_risk_category
  };
}
