function calculate(inputs) {
    // Constants (simplified, based on general FAFSA principles, not actual year-specific tables)
    // These values are illustrative and would be updated annually for a production tool.
    const PARENT_INCOME_PROTECTION_BASE = 19500; // For one parent, scaled by family size
    const PARENT_INCOME_PROTECTION_PER_ADDITIONAL = 6000;
    const STUDENT_INCOME_PROTECTION = 7600; // Approx 2023-24 value
    const PARENT_ASSET_PROTECTION_BASE = 26000; // For dependent, scales roughly with family size/age
    const PARENT_ASSET_CONTRIBUTION_RATE = 0.0564; // Approx FAFSA rate
    const STUDENT_ASSET_CONTRIBUTION_RATE = 0.20; // Approx FAFSA rate
    const PARENT_INCOME_CONTRIBUTION_RATE_TIERS = [
        { threshold: 30000, rate: 0.22 },
        { threshold: 60000, rate: 0.30 },
        { threshold: 90000, rate: 0.38 },
        { threshold: Infinity, rate: 0.47 } // Final tier catches all above
    ];
    const STUDENT_INCOME_CONTRIBUTION_RATE = 0.50;

    const MAX_PELL_GRANT = 7395; // Approx 2023-24 value for EFC=0
    const PELL_EFC_THRESHOLD = 7000; // EFC value above which Pell typically phases out, simplified.

    // Federal Student Loan Caps (simplified for dependent undergraduates, 1st year)
    const LOAN_CAPS = {
        '1stYear': { subsidized: 3500, unsubsidized: 2000 },
        '2ndYear': { subsidized: 4500, unsubsidized: 2000 },
        '3rdYear+': { subsidized: 5500, unsubsidized: 2000 }
    };

    // Merit Scholarship Potential (simplified scale)
    const MERIT_GPA_TIERS = [
        { gpa: 4.0, potential: 15000 },
        { gpa: 3.8, potential: 10000 },
        { gpa: 3.5, potential: 6000 },
        { gpa: 3.0, potential: 2000 },
        { gpa: 2.5, potential: 500 },
        { gpa: 0, potential: 0 } // Base case
    ];
    const MERIT_SAT_FACTOR = 0.02; // Additional $ per point above 1000 SAT
    const MERIT_ACT_FACTOR = 150; // Additional $ per point above 20 ACT

    const INSTITUTIONAL_GRANT_FACTOR = 0.25; // Estimate 25% of remaining need might be covered by institutional grants

    const {
        parentAgi,
        studentAgi,
        parentAssets,
        studentAssets,
        familySize,
        collegeStudents,
        isDependent,
        gpa,
        satActScore,
        costOfAttendance
    } = inputs;

    let expectedFamilyContribution = 0;
    let financialNeed = 0;
    let maxFederalPellGrant = 0;
    let maxFederalStudentLoan = 0;
    let meritScholarshipPotential = 0;
    let totalAidEligibility = 0;

    const isStudentDependent = (isDependent === "Yes");
    const actualCollegeStudents = Math.max(1, collegeStudents); // Ensure at least 1 for division
    const studentGpa = parseFloat(gpa) || 0;
    const studentSatActScore = parseInt(satActScore) || 0; // Handle optional SAT/ACT

    // --- 1. Expected Family Contribution (EFC) Calculation (Simplified FAFSA-like logic) ---

    let parentContributionTotal = 0;
    if (isStudentDependent) {
        // Parent Income Protection Allowance (IPA)
        const parentIPA = PARENT_INCOME_PROTECTION_BASE + (Math.max(0, familySize - 1) * PARENT_INCOME_PROTECTION_PER_ADDITIONAL);
        const parentDisposableIncome = Math.max(0, parentAgi - parentIPA);

        // Parent Income Contribution (Simplified Tiered Calculation)
        let parentIncomeContribution = 0;
        if (parentDisposableIncome > 0) {
            let currentIncome = parentDisposableIncome;
            let accumulatedIncomeFromPreviousTiers = 0;
            for (const tier of PARENT_INCOME_CONTRIBUTION_RATE_TIERS) {
                if (currentIncome <= 0) break;
                const incomeInThisTier = Math.min(currentIncome, tier.threshold === Infinity ? currentIncome : (tier.threshold - accumulatedIncomeFromPreviousTiers));
                parentIncomeContribution += incomeInThisTier * tier.rate;
                currentIncome -= incomeInThisTier;
                accumulatedIncomeFromPreviousTiers += incomeInThisTier;
            }
        }

        // Parent Asset Protection Allowance (APA)
        const parentAPA = PARENT_ASSET_PROTECTION_BASE + (Math.max(0, familySize - 2) * 5000); // Simplified scaling
        const parentNetAssets = Math.max(0, parentAssets - parentAPA);
        const parentAssetContribution = parentNetAssets * PARENT_ASSET_CONTRIBUTION_RATE;

        parentContributionTotal = parentIncomeContribution + parentAssetContribution;
        // Divide parental contribution by number of college students (this lowers per-student EFC)
        parentContributionTotal /= actualCollegeStudents;
    }

    // Student Income Contribution (SIC)
    const studentDisposableIncome = Math.max(0, studentAgi - STUDENT_INCOME_PROTECTION);
    const studentIncomeContribution = studentDisposableIncome * STUDENT_INCOME_CONTRIBUTION_RATE;

    // Student Asset Contribution (SAC)
    const studentAssetContribution = studentAssets * STUDENT_ASSET_CONTRIBUTION_RATE;

    expectedFamilyContribution = parentContributionTotal + studentIncomeContribution + studentAssetContribution;
    expectedFamilyContribution = Math.max(0, Math.round(expectedFamilyContribution));

    // --- 2. Financial Need Calculation ---
    financialNeed = Math.max(0, costOfAttendance - expectedFamilyContribution);

    // --- 3. Federal Pell Grant ---
    if (expectedFamilyContribution <= 0) {
        maxFederalPellGrant = MAX_PELL_GRANT;
    } else if (expectedFamilyContribution < PELL_EFC_THRESHOLD) {
        maxFederalPellGrant = MAX_PELL_GRANT * (1 - (expectedFamilyContribution / PELL_EFC_THRESHOLD));
    } else {
        maxFederalPellGrant = 0;
    }
    maxFederalPellGrant = Math.round(Math.max(0, maxFederalPellGrant));

    // --- 4. Federal Student Loan Eligibility ---
    // Assuming 1st-year undergraduate. In a real system, student's year would be an input.
    let currentLoanCaps = LOAN_CAPS['1stYear'];
    if (!isStudentDependent) {
        currentLoanCaps = { subsidized: 0, unsubsidized: 6000 }; // Example for independent, first year
    }

    let potentialSubsidizedLoan = Math.min(currentLoanCaps.subsidized, financialNeed);
    let potentialUnsubsidizedLoan = currentLoanCaps.unsubsidized;

    // Subsidized loan cannot exceed remaining need after other grant aid (like Pell)
    let remainingNeedForSubsidized = Math.max(0, financialNeed - maxFederalPellGrant);
    potentialSubsidizedLoan = Math.min(potentialSubsidizedLoan, remainingNeedForSubsidized);

    // Unsubsidized loan can cover up to COA minus other aid (EFC + Pell + Subsidized Loan)
    let maxUnsubsidizedBasedOnCOA = Math.max(0, costOfAttendance - expectedFamilyContribution - maxFederalPellGrant - potentialSubsidizedLoan);
    potentialUnsubsidizedLoan = Math.min(potentialUnsubsidizedLoan, maxUnsubsidizedBasedOnCOA);

    maxFederalStudentLoan = potentialSubsidizedLoan + potentialUnsubsidizedLoan;
    maxFederalStudentLoan = Math.round(Math.max(0, maxFederalStudentLoan));

    // --- 5. Merit Scholarship Potential ---
    let meritFromGpa = 0;
    for (const tier of MERIT_GPA_TIERS) {
        if (studentGpa >= tier.gpa) {
            meritFromGpa = tier.potential;
            break;
        }
    }

    let meritFromSatAct = 0;
    if (studentSatActScore) {
        if (studentSatActScore >= 400 && studentSatActScore <= 1600) { // Assume SAT
            meritFromSatAct = Math.max(0, (studentSatActScore - 1000)) * MERIT_SAT_FACTOR;
        } else if (studentSatActScore >= 1 && studentSatActScore <= 36) { // Assume ACT
            meritFromSatAct = Math.max(0, (studentSatActScore - 20)) * MERIT_ACT_FACTOR;
        }
    }
    meritScholarshipPotential = Math.round(meritFromGpa + meritFromSatAct);
    // Cap merit scholarship at a reasonable percentage of COA, e.g., 50%
    meritScholarshipPotential = Math.min(meritScholarshipPotential, costOfAttendance * 0.5);

    // --- 6. Total Estimated Aid Eligibility ---
    // Includes Pell, Federal Loans, Merit, and a simplified estimate for institutional need-based grants
    let institutionalGrantPotential = Math.max(0, financialNeed - maxFederalPellGrant - potentialSubsidizedLoan); // Need not covered by Pell or subsidized loans
    institutionalGrantPotential = Math.round(institutionalGrantPotential * INSTITUTIONAL_GRANT_FACTOR);
    institutionalGrantPotential = Math.min(institutionalGrantPotential, financialNeed); // Institutional grants should not exceed need

    totalAidEligibility = maxFederalPellGrant + potentialSubsidizedLoan + potentialUnsubsidizedLoan + meritScholarshipPotential + institutionalGrantPotential;
    totalAidEligibility = Math.min(totalAidEligibility, costOfAttendance); // Total aid should not exceed COA
    totalAidEligibility = Math.round(Math.max(0, totalAidEligibility));

    return {
        expectedFamilyContribution: expectedFamilyContribution,
        financialNeed: financialNeed,
        maxFederalPellGrant: maxFederalPellGrant,
        maxFederalStudentLoan: maxFederalStudentLoan,
        meritScholarshipPotential: meritScholarshipPotential,
        totalAidEligibility: totalAidEligibility
    };
}
