function calculateImpact(inputs) {
  const { participantCount, eventDurationHours, mediaOutletsContacted, socialMediaEngagementLevel, issueScope, targetAudienceInfluence, advocacyTacticsBeyondEvent, communitySupportLevel } = inputs;

  // --- Constants and Multipliers ---

  // Base values
  const BASE_REACH_PER_PARTICIPANT = 20; // Each participant directly influences 20 people (friends, family, direct observers)
  const MEDIA_OUTLET_REACH_FACTOR = 5000; // Base reach added per media outlet contacted
  const BASE_MEDIA_SCORE_FACTOR = 0.05; // Base score per participant * hour * outlet
  const BASE_POLICY_SCORE_FACTOR = 0.02; // Base score per participant * hour

  // Social Media Engagement Multipliers (for Reach and Media Impact)
  const SOCIAL_MEDIA_MULTIPLIERS = {
    low: 0.5, // Reduces base social amplification
    medium: 1.5,
    high: 4,
    viral: 10
  };

  // Issue Scope Multipliers
  const ISSUE_SCOPE_REACH_MULTIPLIERS = { // Broader issues generally have higher potential reach
    school: 0.8,
    municipal_state: 1.0,
    national_federal: 1.5,
    global: 2.0
  };
  const ISSUE_SCOPE_MEDIA_MULTIPLIERS = { // Broader issues attract more national/international media
    school: 0.6,
    municipal_state: 1.0,
    national_federal: 1.8,
    global: 2.5
  };
  const ISSUE_SCOPE_POLICY_MULTIPLIERS = { // Policy influence often easier on local issues
    school: 2.0,
    municipal_state: 1.5,
    national_federal: 0.8,
    global: 0.5
  };

  // Target Audience Influence Multipliers
  const TARGET_AUDIENCE_REACH_MULTIPLIERS = { // General public targets naturally have higher reach
    school_admin: 0.7,
    local_officials: 0.9,
    federal_officials: 1.0,
    general_public: 1.5
  };
  const TARGET_AUDIENCE_POLICY_MULTIPLIERS = { // Directing at decision-makers increases policy potential
    school_admin: 1.8,
    local_officials: 1.5,
    federal_officials: 1.2,
    general_public: 0.7
  };
  const TARGET_AUDIENCE_MEDIA_MULTIPLIERS = { // General public targets often need more media amplification
    school_admin: 0.8,
    local_officials: 1.0,
    federal_officials: 1.2,
    general_public: 1.5
  };

  // Advocacy Tactics Beyond Event Multipliers (Primarily for Policy Influence)
  const ADVOCACY_TACTICS_POLICY_MULTIPLIERS = {
    none: 0.5,
    petitions_letters: 1.2,
    direct_lobbying: 2.0,
    sustained_campaign: 3.0
  };

  // Community Support Level Multipliers (for all outputs)
  const COMMUNITY_SUPPORT_MULTIPLIERS = {
    low: 0.7,
    moderate: 1.2,
    strong: 1.8
  };

  // --- Input Validation and Edge Cases ---
  if (participantCount <= 0 || eventDurationHours <= 0) {
    return {
      potentialReach: 0,
      mediaImpactScore: 0,
      policyInfluenceScore: 0
    };
  }

  // Cap inputs to prevent unrealistic extremes
  const cappedParticipants = Math.min(participantCount, 50000); // Max 50k participants
  const cappedDuration = Math.min(eventDurationHours, 24);    // Max 24 hours for a single event
  const cappedMediaOutlets = Math.min(mediaOutletsContacted, 100); // Max 100 outlets

  // --- Potential Audience Reach Calculation ---
  let potentialReach = cappedParticipants * BASE_REACH_PER_PARTICIPANT;
  potentialReach += cappedMediaOutlets * MEDIA_OUTLET_REACH_FACTOR;
  potentialReach *= 1 + (SOCIAL_MEDIA_MULTIPLIERS[socialMediaEngagementLevel] || 1) * 0.5; // Social media boost
  potentialReach *= (ISSUE_SCOPE_REACH_MULTIPLIERS[issueScope] || 1);
  potentialReach *= (TARGET_AUDIENCE_REACH_MULTIPLIERS[targetAudienceInfluence] || 1);
  potentialReach *= (COMMUNITY_SUPPORT_MULTIPLIERS[communitySupportLevel] || 1);

  // Apply a soft cap for very large numbers
  potentialReach = Math.min(potentialReach, 100_000_000); // Max 100 million for reach

  // --- Media & Narrative Impact Score Calculation (0-100) ---
  let mediaImpactScore = cappedParticipants * cappedDuration * cappedMediaOutlets * BASE_MEDIA_SCORE_FACTOR;
  mediaImpactScore *= (SOCIAL_MEDIA_MULTIPLIERS[socialMediaEngagementLevel] || 1);
  mediaImpactScore *= (ISSUE_SCOPE_MEDIA_MULTIPLIERS[issueScope] || 1);
  mediaImpactScore *= (TARGET_AUDIENCE_MEDIA_MULTIPLIERS[targetAudienceInfluence] || 1);
  mediaImpactScore *= (COMMUNITY_SUPPORT_MULTIPLIERS[communitySupportLevel] || 1);

  // Clamp score between 0 and 100
  mediaImpactScore = Math.max(0, Math.min(mediaImpactScore, 100));

  // Adjust for very low values to ensure some minimum if inputs are valid
  if (cappedParticipants > 0 && mediaImpactScore < 5) mediaImpactScore = Math.max(mediaImpactScore, 0.5);

  // --- Policy Influence Potential Score Calculation (0-100) ---
  let policyInfluenceScore = cappedParticipants * cappedDuration * BASE_POLICY_SCORE_FACTOR;
  policyInfluenceScore *= (ISSUE_SCOPE_POLICY_MULTIPLIERS[issueScope] || 1);
  policyInfluenceScore *= (TARGET_AUDIENCE_POLICY_MULTIPLIERS[targetAudienceInfluence] || 1);
  policyInfluenceScore *= (ADVOCACY_TACTICS_POLICY_MULTIPLIERS[advocacyTacticsBeyondEvent] || 1);
  policyInfluenceScore *= (COMMUNITY_SUPPORT_MULTIPLIERS[communitySupportLevel] || 1);

  // Clamp score between 0 and 100
  policyInfluenceScore = Math.max(0, Math.min(policyInfluenceScore, 100));

  // Adjust for very low values
  if (cappedParticipants > 0 && policyInfluenceScore < 5) policyInfluenceScore = Math.max(policyInfluenceScore, 0.2);

  return {
    potentialReach: Math.round(potentialReach),
    mediaImpactScore: parseFloat(mediaImpactScore.toFixed(1)),
    policyInfluenceScore: parseFloat(policyInfluenceScore.toFixed(1))
  };
}
