function calculateLearningPath(inputs) {
  let { currentKnowledgeScore, targetCompetencyScore, weeklyStudyHours, learningStyleMatch, resourceAvailabilityScore, topicComplexityFactor, targetCompletionMonths, motivationLevel } = inputs;

  // Input validation and clamping
  currentKnowledgeScore = Math.max(0, Math.min(100, currentKnowledgeScore));
  targetCompetencyScore = Math.max(0, Math.min(100, targetCompetencyScore));
  weeklyStudyHours = Math.max(0, Math.min(60, weeklyStudyHours)); // Max 60 hours/week is reasonable
  learningStyleMatch = Math.max(0, Math.min(100, learningStyleMatch));
  resourceAvailabilityScore = Math.max(0, Math.min(100, resourceAvailabilityScore));
  topicComplexityFactor = Math.max(1, Math.min(10, topicComplexityFactor));
  targetCompletionMonths = Math.max(1, Math.min(120, targetCompletionMonths)); // Max 10 years
  motivationLevel = Math.max(0, Math.min(100, motivationLevel));

  const results = {
    estimatedTotalLearningHours: 0,
    estimatedCompletionDate: 'N/A',
    learningPathEfficiencyScore: 0,
    recommendedWeeklyPace: 0
  };

  // 1. Calculate Competency Gap
  const competencyGap = targetCompetencyScore - currentKnowledgeScore;

  if (competencyGap <= 0) {
    results.estimatedCompletionDate = 'Already proficient';
    results.learningPathEfficiencyScore = 100; // Already achieved, so highly efficient in terms of remaining effort.
    return results;
  }

  // 2. Define Base Learning Rate
  // This is a foundational constant: under neutral conditions, how many competency points a student gains per hour.
  // A higher value means faster learning for the same effort.
  const baseLearningRatePerPointPerHour = 0.5; // e.g., 0.5 competency points gained per hour in ideal conditions for an average topic.

  // 3. Adjust Learning Rate based on Student/Resource Factors
  // These factors modify the base learning rate. Values > 1 increase efficiency, < 1 decrease it.

  // Learning Style Match: 0% match -> 0.5x, 50% match -> 1x, 100% match -> 1.5x
  const learningStyleFactor = 0.5 + (learningStyleMatch / 100);

  // Resource Availability: 0% -> 0.6x, 50% -> 1x, 100% -> 1.4x
  const resourceFactor = 0.6 + (resourceAvailabilityScore / 100) * 0.8;

  // Motivation Level: 0% -> 0.7x, 50% -> 1x, 100% -> 1.3x
  const motivationFactor = 0.7 + (motivationLevel / 100) * 0.6;

  // Topic Complexity: 1 (easiest) -> 1x, 5 (average) -> 0.7x, 10 (hardest) -> 0.4x
  // This factor is a divisor, so higher complexity means lower effective learning rate.
  const complexityDivisor = 0.7 + (topicComplexityFactor / 10) * 0.7; // Range: 0.77 (for complexity 1) to 1.4 (for complexity 10)

  // Combine all factors to get the effective learning rate.
  // The complexity divisor is applied to the product of other enhancing factors.
  let effectiveLearningRate = (baseLearningRatePerPointPerHour * learningStyleFactor * resourceFactor * motivationFactor) / complexityDivisor;

  // Ensure effectiveLearningRate is positive and reasonable
  effectiveLearningRate = Math.max(0.01, effectiveLearningRate); // Minimum effective learning rate to avoid division by zero or infinite hours

  // 4. Calculate Total Estimated Learning Hours
  let estimatedTotalLearningHours = competencyGap / effectiveLearningRate;

  // Apply minimum and maximum hours to make it realistic
  estimatedTotalLearningHours = Math.max(1, estimatedTotalLearningHours); // Minimum of 1 hour for any gap
  estimatedTotalLearningHours = Math.min(10000, estimatedTotalLearningHours); // Cap at 10,000 hours for very ambitious targets

  results.estimatedTotalLearningHours = Math.round(estimatedTotalLearningHours);

  // 5. Calculate Learning Path Efficiency Score
  // This score reflects how efficient the path is relative to ideal conditions.
  // Normalize based on the baseline rate.
  let learningPathEfficiencyScore = (effectiveLearningRate / baseLearningRatePerPointPerHour) * 100;
  results.learningPathEfficiencyScore = Math.round(Math.max(0, Math.min(100, learningPathEfficiencyScore)));

  // 6. Calculate Estimated Completion Date
  if (weeklyStudyHours > 0) {
    const estimatedWeeks = estimatedTotalLearningHours / weeklyStudyHours;
    const completionDate = new Date();
    completionDate.setDate(completionDate.getDate() + Math.round(estimatedWeeks * 7));
    results.estimatedCompletionDate = completionDate.toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });
  } else {
    results.estimatedCompletionDate = 'Requires study hours';
  }

  // 7. Calculate Recommended Weekly Pace
  if (targetCompletionMonths > 0) {
    const totalWeeksInTargetPeriod = targetCompletionMonths * 4.33; // Average weeks per month
    let recommendedPace = estimatedTotalLearningHours / totalWeeksInTargetPeriod;

    // Ensure recommended pace is realistic for a human
    recommendedPace = Math.max(0.1, recommendedPace); // Minimum pace to show progress
    recommendedPace = Math.min(60, recommendedPace); // Cap at 60 hours/week to prevent burnout suggestions

    results.recommendedWeeklyPace = parseFloat(recommendedPace.toFixed(1));
  } else {
    results.recommendedWeeklyPace = 0;
  }

  return results;
}
