function calculate({ initialLoanBalance, annualInterestRate, currentAnnualIncome, annualIncomeGrowthRate, careerChangeYear, careerChangeIncomeBoost, targetRepaymentYears, additionalMonthlyPayment }) {

  const monthlyInterestRate = (annualInterestRate / 100) / 12;
  const targetTotalPayments = targetRepaymentYears * 12;

  let baseMonthlyPayment = 0;
  if (monthlyInterestRate > 0) {
    baseMonthlyPayment = initialLoanBalance * monthlyInterestRate * Math.pow(1 + monthlyInterestRate, targetTotalPayments) / (Math.pow(1 + monthlyInterestRate, targetTotalPayments) - 1);
  } else {
    baseMonthlyPayment = initialLoanBalance / targetTotalPayments;
  }

  let balance = initialLoanBalance;
  let totalPaid = 0;
  let totalInterest = 0;
  let months = 0;
  let currentSimulatedAnnualIncome = currentAnnualIncome;

  const maxMonths = 360; // Cap at 30 years to prevent infinite loops
  const extraPayment = additionalMonthlyPayment || 0;
  const incomeGrowthFactor = 1 + (annualIncomeGrowthRate / 100);
  const careerBoostFactor = 1 + ((careerChangeIncomeBoost || 0) / 100);
  const ccy = careerChangeYear || Infinity; // Treat as never happening if not set

  while (balance > 0 && months < maxMonths) {
    const currentYear = Math.floor(months / 12);

    // Project income growth annually
    if (months % 12 === 0 && currentYear > 0) {
      currentSimulatedAnnualIncome *= incomeGrowthFactor;
      // Apply career change boost if applicable
      if (currentYear === ccy && ccy !== Infinity) {
        currentSimulatedAnnualIncome *= careerBoostFactor;
      }
    }

    const interestThisMonth = balance * monthlyInterestRate;
    let currentMonthlyPayment = baseMonthlyPayment + extraPayment;

    // Cap payment to remaining balance + interest if it's the final payment
    if (currentMonthlyPayment > balance + interestThisMonth) {
      currentMonthlyPayment = balance + interestThisMonth;
    }

    const principalThisMonth = currentMonthlyPayment - interestThisMonth;

    balance -= principalThisMonth;
    totalPaid += currentMonthlyPayment;
    totalInterest += interestThisMonth;
    months++;
  }

  const effectiveRepaymentYears = months / 12;

  return {
    optimizedMonthlyPayment: baseMonthlyPayment,
    totalAmountPaid: totalPaid,
    totalInterestPaid: totalInterest,
    effectiveRepaymentYears: effectiveRepaymentYears
  };
}
