function (inputs) {
    const totalPrincipal = inputs.totalPrincipal || 0;
    const annualRate = (inputs.averageInterestRate || 0) / 100;
    const repaymentPlanType = inputs.repaymentPlanType || 'standard';
    const annualIncome = inputs.annualIncome || 0;
    const householdSize = inputs.householdSize || 1;
    const extraMonthlyPayment = inputs.extraMonthlyPayment || 0;

    if (totalPrincipal <= 0 || annualRate < 0) {
        return {
            calculatedMonthlyPayment: 0,
            totalInterestPaid: 0,
            totalAmountPaid: 0,
            repaymentPeriodMonths: 0,
            repaymentPeriodYears: 0,
            loanBalanceAfterMaxTerm: 0
        };
    }

    const monthlyRate = annualRate / 12;
    let baseCalculatedPayment = 0;
    const maxSimulationMonths = 360; // 30 years

    // Federal Poverty Level (FPL) 2023 values for 48 contiguous states & D.C.
    // Source: https://aspe.hhs.gov/topics/poverty-economic-mobility/poverty-guidelines
    // Approximate for 1-8 people, assuming an increment of $5140 per person after the first
    const getFederalPovertyLevel = (size) => {
        if (size <= 0) return 0;
        const baseFPL = 14580; // FPL for 1 person in 2023
        const increment = 5140; // Increment for each additional person
        return baseFPL + Math.max(0, size - 1) * increment;
    };

    if (repaymentPlanType === 'standard') {
        const termMonths = 120; // 10 years
        if (monthlyRate === 0) {
             baseCalculatedPayment = totalPrincipal / termMonths;
        } else {
             baseCalculatedPayment = totalPrincipal * monthlyRate / (1 - Math.pow(1 + monthlyRate, -termMonths));
        }
    } else if (repaymentPlanType === 'extended') {
        const termMonths = 300; // 25 years
        if (monthlyRate === 0) {
            baseCalculatedPayment = totalPrincipal / termMonths;
        } else {
            baseCalculatedPayment = totalPrincipal * monthlyRate / (1 - Math.pow(1 + monthlyRate, -termMonths));
        }
    } else if (repaymentPlanType === 'income-driven') {
        // Assuming SAVE/REPAYE-like plan: 10% of discretionary income above 150% FPL
        const relevantFPL = getFederalPovertyLevel(householdSize);
        const discretionaryIncomeThreshold = relevantFPL * 1.5;
        const discretionaryIncome = Math.max(0, annualIncome - discretionaryIncomeThreshold);

        baseCalculatedPayment = Math.max(0, discretionaryIncome * 0.10 / 12); // 10% of discretionary income

        // Edge case: if IDR payment is calculated as 0, and no extra payment, then it's effectively 0, 
        // but for simulation, we need a small payment to trigger interest accrual and show non-payoff.
        // Or, handle as a specific output indicating balance growth.
        // Let's ensure a minimum to prevent division by zero in some theoretical models, but here, the simulation handles it.
    }

    let currentBalance = totalPrincipal;
    let totalInterest = 0;
    let totalPaid = 0;
    let months = 0;
    const effectiveMonthlyPayment = Math.max(0, baseCalculatedPayment + extraMonthlyPayment);

    while (currentBalance > 0 && months < maxSimulationMonths) {
        const interestAccrued = currentBalance * monthlyRate;
        let paymentForThisMonth = effectiveMonthlyPayment;

        // If the calculated payment (including extra) is less than the interest accrued, 
        // the balance will increase (negative amortization). Payment is still made.
        // If the balance plus accrued interest is less than effectiveMonthlyPayment (final payment scenario),
        // only pay what's remaining.
        if (currentBalance + interestAccrued < effectiveMonthlyPayment) {
            paymentForThisMonth = currentBalance + interestAccrued;
        }

        const principalPaid = paymentForThisMonth - interestAccrued;
        currentBalance -= principalPaid;
        totalInterest += interestAccrued;
        totalPaid += paymentForThisMonth;
        months++;

        // Ensure balance doesn't go negative due to float precision on last payment
        if (currentBalance <= 0.01) {
            currentBalance = 0;
            break;
        }
    }

    const repaymentPeriodYears = months / 12;
    const loanBalanceAfterMaxTerm = currentBalance > 0 ? currentBalance : 0;

    return {
        calculatedMonthlyPayment: baseCalculatedPayment,
        totalInterestPaid: totalInterest,
        totalAmountPaid: totalPaid,
        repaymentPeriodMonths: months,
        repaymentPeriodYears: repaymentPeriodYears,
        loanBalanceAfterMaxTerm: loanBalanceAfterMaxTerm
    };
}
