function calculateFPL(familySize) {
  const FPL_BASE_1_PERSON = 15060; // 2024 Federal Poverty Level for 1 person (contiguous US)
  const FPL_INCREMENT_PER_PERSON = 5380; // Increment for each additional person
  return FPL_BASE_1_PERSON + (Math.max(0, familySize - 1) * FPL_INCREMENT_PER_PERSON);
}

const IDR_DISCRETIONARY_PERCENTAGE = 0.10; // Models PAYE/REPAYE (pre-July 2024 SAVE undergraduate loans)
const FORGIVENESS_TAX_RATE = 0.22; // Assumed marginal tax rate for forgiveness

let currentLoanBalance = loan_balance_total;
let totalPaid = 0;
let currentAGI = current_agi;
let initialMonthlyPayment = 0;

for (let year = 0; year < idr_plan_term_years; year++) {
  const currentFPL = calculateFPL(family_size);
  const discretionaryIncome = Math.max(0, currentAGI - (1.5 * currentFPL));
  let annualIDRPayment = discretionaryIncome * IDR_DISCRETIONARY_PERCENTAGE;
  let monthlyIDRPaymentForYear = annualIDRPayment / 12;

  // Store the initial payment for the first year
  if (year === 0) {
    initialMonthlyPayment = monthlyIDRPaymentForYear;
  }

  for (let month = 0; month < 12; month++) {
    const monthlyInterest = currentLoanBalance * (average_interest_rate / 12);

    // Cap payment to ensure it doesn't exceed the balance plus accrued interest
    // and ensure it doesn't go below 0.
    let paymentThisMonth = Math.min(monthlyIDRPaymentForYear, currentLoanBalance + monthlyInterest + 0.01);
    paymentThisMonth = Math.max(0, paymentThisMonth); // Ensure payment is not negative

    totalPaid += paymentThisMonth;
    currentLoanBalance += monthlyInterest - paymentThisMonth;
    currentLoanBalance = Math.max(0, currentLoanBalance); // Loan balance cannot be negative

    if (currentLoanBalance <= 0) {
      // Loan paid off early
      return {
        initial_idr_payment: initialMonthlyPayment,
        total_paid_idr: totalPaid,
        forgiven_amount: 0,
        estimated_tax_on_forgiveness: 0,
        net_cost_with_forgiveness: totalPaid
      };
    }
  }
  currentAGI *= (1 + agi_annual_growth_rate);
}

const forgivenAmount = currentLoanBalance;
const estimatedTaxOnForgiveness = forgivenAmount * FORGIVENESS_TAX_RATE;
const netCostWithForgiveness = totalPaid + estimatedTaxOnForgiveness;

return {
  initial_idr_payment: initialMonthlyPayment,
  total_paid_idr: totalPaid,
  forgiven_amount: forgivenAmount,
  estimated_tax_on_forgiveness: estimatedTaxOnForgiveness,
  net_cost_with_forgiveness: netCostWithForgiveness
};
