const principal = inputs.principal;
const annualInterestRate = inputs.annualInterestRate;
const currentRemainingTermYears = inputs.currentRemainingTermYears;
const additionalMonthlyPayment = inputs.additionalMonthlyPayment || 0;

const monthlyInterestRate = annualInterestRate / 100 / 12;
const currentTermMonths = currentRemainingTermYears * 12;

let currentMonthlyPayment;
if (monthlyInterestRate === 0) {
    if (currentTermMonths === 0) {
        currentMonthlyPayment = principal;
    } else {
        currentMonthlyPayment = principal / currentTermMonths;
    }
} else {
    currentMonthlyPayment = principal * (monthlyInterestRate * Math.pow(1 + monthlyInterestRate, currentTermMonths)) / (Math.pow(1 + monthlyInterestRate, currentTermMonths) - 1);
}

const currentTotalPaid = currentMonthlyPayment * currentTermMonths;
const currentTotalInterest = currentTotalPaid - principal;

const optimizedMonthlyPayment = currentMonthlyPayment + additionalMonthlyPayment;

let optimizedTermMonths;
if (monthlyInterestRate === 0) {
    if (optimizedMonthlyPayment === 0) {
        optimizedTermMonths = 999999; // Effectively infinite
    } else {
        optimizedTermMonths = principal / optimizedMonthlyPayment;
    }
} else {
    if (optimizedMonthlyPayment <= principal * monthlyInterestRate) {
        optimizedTermMonths = 999999; // Effectively infinite if payment doesn't cover interest
    } else {
        optimizedTermMonths = -Math.log(1 - (principal * monthlyInterestRate) / optimizedMonthlyPayment) / Math.log(1 + monthlyInterestRate);
    }
}
optimizedTermMonths = Math.ceil(optimizedTermMonths); // Round up to ensure full payment

const optimizedRemainingTermYears = optimizedTermMonths / 12;
const optimizedTotalPaid = optimizedMonthlyPayment * optimizedTermMonths;
const optimizedTotalInterest = optimizedTotalPaid - principal;

const totalInterestSaved = currentTotalInterest - optimizedTotalInterest;
const termReductionYears = currentRemainingTermYears - optimizedRemainingTermYears;

return {
    currentMonthlyPayment: currentMonthlyPayment,
    currentTotalPaid: currentTotalPaid,
    currentTotalInterest: currentTotalInterest,
    optimizedMonthlyPayment: optimizedMonthlyPayment,
    optimizedRemainingTermYears: optimizedRemainingTermYears,
    optimizedTotalPaid: optimizedTotalPaid,
    totalInterestSaved: totalInterestSaved,
    termReductionYears: termReductionYears
};
