const calculateMonthlyPayment = (principal, annualRate, months) => {
    if (months <= 0) return 0; // Handle zero or negative term
    if (annualRate === 0) {
        return principal / months; // Simple division for 0% interest
    }
    const monthlyRate = (annualRate / 100) / 12;
    const numerator = principal * monthlyRate * Math.pow(1 + monthlyRate, months);
    const denominator = Math.pow(1 + monthlyRate, months) - 1;
    return numerator / denominator;
};

const oldMonthlyPayment = calculateMonthlyPayment(currentLoanBalance, currentInterestRate, currentLoanTerm);
const newMonthlyPayment = calculateMonthlyPayment(currentLoanBalance, newInterestRate, newLoanTerm);

const monthlyPaymentSavings = oldMonthlyPayment - newMonthlyPayment;

const totalPrincipalOld = currentLoanBalance;
const totalPaymentsOld = oldMonthlyPayment * currentLoanTerm;
const totalInterestPaidOld = totalPaymentsOld - totalPrincipalOld;

const totalPrincipalNew = currentLoanBalance; // Assuming fees are an extra cost, not rolled into principal for monthly calc
const totalPaymentsNew = newMonthlyPayment * newLoanTerm;
const totalInterestPaidNew = totalPaymentsNew - totalPrincipalNew;

// Total savings considers the difference in interest paid over the respective loan terms, minus any upfront fees.
const totalInterestSaved = totalInterestPaidOld - totalInterestPaidNew - refinanceFees;

let breakEvenPoint = 0;
// Calculate break-even point for refinance fees, capping at a reasonable maximum if savings are tiny or negative.
if (monthlyPaymentSavings > 0) {
    breakEvenPoint = refinanceFees / monthlyPaymentSavings;
    if (breakEvenPoint > 999) breakEvenPoint = 999; // Cap for display purposes
} else {
    breakEvenPoint = NaN; // Indicates no break-even if no monthly savings or negative savings
}

return {
    oldMonthlyPayment: oldMonthlyPayment,
    newMonthlyPayment: newMonthlyPayment,
    monthlyPaymentSavings: monthlyPaymentSavings,
    totalInterestSaved: totalInterestSaved,
    breakEvenPoint: breakEvenPoint
};
