const totalLoanAmount = inputs.totalLoanAmount;
const annualInterestRate = inputs.annualInterestRate;
const standardRepaymentTermYears = inputs.standardRepaymentTermYears;
const annualIncome = inputs.annualIncome;
const discretionaryIncomePercentage = inputs.discretionaryIncomePercentage;
const extraMonthlyPayment = inputs.extraMonthlyPayment;

const monthlyInterestRate = (annualInterestRate / 100) / 12;
const standardTotalPayments = standardRepaymentTermYears * 12;

let standardMonthlyPayment = 0;
let standardTotalInterest = 0;

// --- Calculate Standard Repayment --- 
if (totalLoanAmount <= 0 || standardRepaymentTermYears <= 0) {
    standardMonthlyPayment = 0;
    standardTotalInterest = 0;
} else if (annualInterestRate === 0) {
    standardMonthlyPayment = totalLoanAmount / standardTotalPayments;
    standardTotalInterest = 0;
} else {
    const i = monthlyInterestRate;
    const n = standardTotalPayments;
    standardMonthlyPayment = totalLoanAmount * i * Math.pow(1 + i, n) / (Math.pow(1 + i, n) - 1);
    standardTotalInterest = (standardMonthlyPayment * n) - totalLoanAmount;
}

// --- Calculate Aggressive Repayment (with extra payment) --- 
let aggressiveMonthlyPayment = standardMonthlyPayment + extraMonthlyPayment;
let aggressiveTotalPayments = standardTotalPayments; 
let aggressiveRepaymentTermYears = standardRepaymentTermYears;
let aggressiveTotalInterest = standardTotalInterest;

if (extraMonthlyPayment > 0 && aggressiveMonthlyPayment > 0) {
    const minPaymentToCoverInterest = totalLoanAmount * monthlyInterestRate;

    if (totalLoanAmount > 0 && annualInterestRate > 0) {
        // If aggressive monthly payment is less than what's needed to cover monthly interest, it won't pay off.
        // Cap aggressiveMonthlyPayment to at least standard to avoid infinite/negative term.
        const effectiveAggressivePayment = Math.max(aggressiveMonthlyPayment, minPaymentToCoverInterest + 0.01); // Ensure it's slightly above interest

        // Formula for n (number of payments) given P, r, M:
        // n = -log(1 - (P*r/M)) / log(1+r)
        const termCalcNumerator = Math.log(effectiveAggressivePayment / (effectiveAggressivePayment - totalLoanAmount * monthlyInterestRate));
        const termCalcDenominator = Math.log(1 + monthlyInterestRate);

        if (termCalcDenominator > 0 && isFinite(termCalcNumerator) && isFinite(termCalcDenominator) && termCalcNumerator > 0) {
            aggressiveTotalPayments = termCalcNumerator / termCalcDenominator;
            aggressiveRepaymentTermYears = aggressiveTotalPayments / 12;
            aggressiveTotalInterest = (effectiveAggressivePayment * aggressiveTotalPayments) - totalLoanAmount;
        } else {
             // If payment is too low to ever pay off or other calculation issue
            aggressiveTotalPayments = standardTotalPayments; // Revert to standard term for practical display
            aggressiveRepaymentTermYears = standardRepaymentTermYears;
            aggressiveTotalInterest = standardTotalInterest;
        }
    } else if (totalLoanAmount > 0 && annualInterestRate === 0) {
        // No interest, just principal division
        aggressiveTotalPayments = totalLoanAmount / aggressiveMonthlyPayment;
        aggressiveRepaymentTermYears = aggressiveTotalPayments / 12;
        aggressiveTotalInterest = 0;
    }
} else if (extraMonthlyPayment === 0) {
    // If no extra payment, aggressive is same as standard. This ensures values are set.
    aggressiveMonthlyPayment = standardMonthlyPayment;
    aggressiveTotalPayments = standardTotalPayments;
    aggressiveRepaymentTermYears = standardRepaymentTermYears;
    aggressiveTotalInterest = standardTotalInterest;
}

// --- Calculate Estimated IDR-like Payment --- 
let idrEstimatedMonthlyPayment = 0;
if (annualIncome > 0 && discretionaryIncomePercentage > 0) {
    idrEstimatedMonthlyPayment = (annualIncome / 12) * (discretionaryIncomePercentage / 100);
    // Cap IDR payment at standard payment, as real IDR plans often don't exceed standard payment for higher incomes.
    idrEstimatedMonthlyPayment = Math.min(idrEstimatedMonthlyPayment, standardMonthlyPayment);
}

// --- Determine Optimal Plan --- 
let optimalMonthlyPayment = standardMonthlyPayment;
let optimalTotalInterest = standardTotalInterest;
let optimalRepaymentTermYears = standardRepaymentTermYears;
let interestSavings = 0;

// If an extra payment was specified and it results in a better outcome
if (extraMonthlyPayment > 0 && aggressiveMonthlyPayment > 0 && aggressiveTotalPayments < standardTotalPayments && aggressiveTotalInterest < standardTotalInterest) {
    optimalMonthlyPayment = aggressiveMonthlyPayment;
    optimalTotalInterest = aggressiveTotalInterest;
    optimalRepaymentTermYears = aggressiveRepaymentTermYears;
    interestSavings = standardTotalInterest - optimalTotalInterest;
}

// Caps: Ensure payments are not negative, interest savings not negative
standardMonthlyPayment = Math.max(0, standardMonthlyPayment);
standardTotalInterest = Math.max(0, standardTotalInterest);
aggressiveMonthlyPayment = Math.max(0, aggressiveMonthlyPayment);
aggressiveTotalInterest = Math.max(0, aggressiveTotalInterest);
optimalMonthlyPayment = Math.max(0, optimalMonthlyPayment);
optimalTotalInterest = Math.max(0, optimalTotalInterest);
interestSavings = Math.max(0, interestSavings);

// A few more caps for term to avoid extremely short/long values for display
aggressiveRepaymentTermYears = Math.min(Math.max(0, aggressiveRepaymentTermYears), 100); 
optimalRepaymentTermYears = Math.min(Math.max(0, optimalRepaymentTermYears), 100); 

return {
    standardMonthlyPayment: standardMonthlyPayment,
    aggressiveMonthlyPayment: aggressiveMonthlyPayment,
    idrEstimatedMonthlyPayment: idrEstimatedMonthlyPayment,
    optimalMonthlyPayment: optimalMonthlyPayment,
    optimalTotalInterest: optimalTotalInterest,
    optimalRepaymentTermYears: optimalRepaymentTermYears,
    interestSavings: interestSavings
};
