const P = principal;
const annualRate = annualInterestRate / 100;
const r = annualRate / 12; // Monthly interest rate
const n_standard = loanTermYears * 12; // Total standard payments

let standardMonthlyPayment = 0;
if (r === 0) {
    if (n_standard === 0) {
        standardMonthlyPayment = P; // If term is 0, pay principal now
    } else {
        standardMonthlyPayment = P / n_standard; // No interest
    }
} else {
    standardMonthlyPayment = P * (r * Math.pow(1 + r, n_standard)) / (Math.pow(1 + r, n_standard) - 1);
}

// Calculate total interest and total paid for standard repayment
const totalAmountPaidStandard = standardMonthlyPayment * n_standard;
const totalInterestPaidStandard = totalAmountPaidStandard - P;

// Initialize variables for extra payment scenario
let effectiveMonthlyPayment = standardMonthlyPayment;
let payoffMonthsWithExtra = n_standard;
let totalInterestPaidWithExtra = totalInterestPaidStandard;
let totalAmountPaidWithExtra = totalAmountPaidStandard;
let interestSaved = 0;

const extra = extraMonthlyPayment || 0; // Ensure extra is a number, defaults to 0

if (extra > 0) {
    effectiveMonthlyPayment = standardMonthlyPayment + extra;

    if (effectiveMonthlyPayment <= P * r && r > 0) {
        // If payment is less than or equal to monthly interest on principal, it won't pay off.
        payoffMonthsWithExtra = Infinity;
        totalInterestPaidWithExtra = Infinity;
        totalAmountPaidWithExtra = Infinity;
        interestSaved = -Infinity; 
    } else if (r === 0) {
        payoffMonthsWithExtra = Math.ceil(P / effectiveMonthlyPayment);
        totalInterestPaidWithExtra = 0;
        totalAmountPaidWithExtra = P;
        interestSaved = totalInterestPaidStandard;
    } else {
        // Calculate new number of months to pay off with the effective payment
        payoffMonthsWithExtra = Math.log(effectiveMonthlyPayment / (effectiveMonthlyPayment - P * r)) / Math.log(1 + r);

        // Ensure payoffMonthsWithExtra is at least 1 and round up to the next full month
        payoffMonthsWithExtra = Math.max(1, Math.ceil(payoffMonthsWithExtra));

        totalAmountPaidWithExtra = effectiveMonthlyPayment * payoffMonthsWithExtra;
        totalInterestPaidWithExtra = totalAmountPaidWithExtra - P;
        interestSaved = totalInterestPaidStandard - totalInterestPaidWithExtra;
    }
}

return {
    standardMonthlyPayment: standardMonthlyPayment,
    totalInterestPaid: totalInterestPaidStandard,
    totalPaidWithExtra: totalAmountPaidWithExtra,
    payoffMonthsStandard: n_standard,
    payoffMonthsWithExtra: payoffMonthsWithExtra,
    interestSaved: interestSaved
};
