const loanPrincipal = inputs.loanPrincipal;
const annualInterestRate = inputs.annualInterestRate;
const repaymentPeriodYears = inputs.repaymentPeriodYears;
const originationFeePercent = inputs.originationFeePercent || 0;
const extraMonthlyPayment = inputs.extraMonthlyPayment || 0;

// Effective Principal after origination fee
const P_actual = loanPrincipal * (1 + originationFeePercent / 100);

// Monthly interest rate as a decimal
const r_monthly = annualInterestRate / 100 / 12;

// Initial total number of payments (months) based on input repayment period
const n_initial = repaymentPeriodYears * 12;

let monthlyPayment_base;
if (r_monthly === 0) {
    monthlyPayment_base = P_actual / n_initial;
} else {
    // Amortization formula for fixed monthly payment
    monthlyPayment_base = P_actual * (r_monthly * Math.pow(1 + r_monthly, n_initial)) / (Math.pow(1 + r_monthly, n_initial) - 1);
}

// Handle cases where principal is zero or invalid inputs lead to non-finite results
if (P_actual <= 0 || !isFinite(monthlyPayment_base) || isNaN(monthlyPayment_base)) {
     return {
        monthlyPayment: 0,
        totalInterestPaid: 0,
        totalCostOfLoan: 0,
        totalRepaymentMonths: 0
    };
}

// Effective monthly payment including extra payments
const effectiveMonthlyPayment = monthlyPayment_base + extraMonthlyPayment;

let totalRepaymentMonths;
let totalInterestPaid;
let totalCostOfLoan;

// Safeguard: If payment doesn't cover monthly interest, loan will never be paid off
if (r_monthly > 0 && effectiveMonthlyPayment < P_actual * r_monthly) {
    totalRepaymentMonths = Infinity; // Loan effectively never paid off
    totalInterestPaid = Infinity;
    totalCostOfLoan = Infinity;
} else if (r_monthly === 0) {
    // If interest rate is 0, total interest is 0, and cost is just principal
    totalRepaymentMonths = Math.ceil(P_actual / effectiveMonthlyPayment);
    totalInterestPaid = 0;
    totalCostOfLoan = P_actual;
} else {
    // Calculate new number of payments (n) with the effective monthly payment
    // Formula to find 'n' given P, r, M: n = -log(1 - Pr/M) / log(1+r)
    const numerator = Math.log(1 - P_actual * r_monthly / effectiveMonthlyPayment);
    const denominator = Math.log(1 + r_monthly);

    if (isNaN(numerator) || isNaN(denominator) || denominator === 0) {
        // Fallback for extreme cases (e.g., payment just covers principal, but still problematic log args)
        totalRepaymentMonths = Infinity;
        totalInterestPaid = Infinity;
        totalCostOfLoan = Infinity;
    } else {
        totalRepaymentMonths = -numerator / denominator;
        totalRepaymentMonths = Math.ceil(totalRepaymentMonths); // Round up to cover the last partial payment

        // Total amount paid = totalRepaymentMonths * effectiveMonthlyPayment
        totalCostOfLoan = totalRepaymentMonths * effectiveMonthlyPayment;
        totalInterestPaid = totalCostOfLoan - P_actual;

        // Ensure totalInterestPaid is not negative due to rounding or slight calculation discrepancies
        totalInterestPaid = Math.max(0, totalInterestPaid);
        totalCostOfLoan = P_actual + totalInterestPaid;
    }
}

return {
    monthlyPayment: monthlyPayment_base, // Display the base payment for the initial term
    totalInterestPaid: totalInterestPaid,
    totalCostOfLoan: totalCostOfLoan,
    totalRepaymentMonths: totalRepaymentMonths
};
