{
    const P = current_principal_balance;
    const annual_rate = annual_interest_rate / 100;
    const i = annual_rate / 12;

    const total_original_months = original_loan_term_years * 12;
    const actual_payments_made_months = Math.min(payments_made_months, total_original_months); // Cap payments made at original term

    // Handle case where principal is zero or negative
    if (P <= 0) {
        return {
            total_interest_paid_standard: 0,
            total_payments_standard: 0,
            time_to_repay_standard_years: 0,
            total_interest_paid_accelerated: 0,
            total_payments_accelerated: 0,
            time_to_repay_accelerated_years: 0
        };
    }

    // --- Scenario 1: Standard Repayment from current balance --- 
    let remaining_original_term_months = Math.max(0, total_original_months - actual_payments_made_months);
    let standard_monthly_payment;

    if (remaining_original_term_months <= 0) {
        // Loan term expired but balance remains. Assume payoff in one month with accrued interest.
        standard_monthly_payment = P * (1 + i);
        remaining_original_term_months = 1; // Force 1 iteration for calculation
    } else if (i === 0) { // Zero interest rate
        standard_monthly_payment = P / remaining_original_term_months;
    } else { // Standard EMI calculation
        standard_monthly_payment = P * (i * Math.pow(1 + i, remaining_original_term_months)) / (Math.pow(1 + i, remaining_original_term_months) - 1);
    }

    let balance_std = P;
    let total_interest_std = 0;
    let payments_count_std = 0;
    const max_payments_limit = 1200; // Cap at 100 years to prevent infinite loops

    while (balance_std > 0 && payments_count_std < max_payments_limit) {
        let interest_for_month = balance_std * i;
        let principal_paid_this_month = standard_monthly_payment - interest_for_month;

        // Ensure last payment clears balance correctly
        if (balance_std + interest_for_month <= standard_monthly_payment) {
            principal_paid_this_month = balance_std;
            interest_for_month = balance_std * i; // Recalculate interest for the exact remaining principal
            balance_std = 0; // Loan fully paid
        } else {
            balance_std -= principal_paid_this_month;
        }

        total_interest_std += interest_for_month;
        payments_count_std++;

        if (balance_std < 0.01) { // Account for floating point inaccuracies
            balance_std = 0;
        }
    }

    // --- Scenario 2: Accelerated Repayment --- 
    let actual_additional_monthly_payment = Math.max(0, additional_monthly_payment);
    let accelerated_monthly_payment = standard_monthly_payment + actual_additional_monthly_payment;

    // Ensure accelerated payment is at least what's needed to cover interest + some principal
    if (accelerated_monthly_payment < P * i && P > 0 && i > 0) {
        accelerated_monthly_payment = P * i + 1; // At least cover interest plus $1 to ensure progress
    }
    if (accelerated_monthly_payment <= 0 && P > 0) {
        accelerated_monthly_payment = standard_monthly_payment; // If somehow it's zero or negative, default to standard
    }

    let balance_accel = P;
    let total_interest_accel = 0;
    let payments_count_accel = 0;

    while (balance_accel > 0 && payments_count_accel < max_payments_limit) {
        let interest_for_month = balance_accel * i;
        let principal_paid_this_month = accelerated_monthly_payment - interest_for_month;

        // Ensure last payment clears balance correctly
        if (balance_accel + interest_for_month <= accelerated_monthly_payment) {
            principal_paid_this_month = balance_accel;
            interest_for_month = balance_accel * i; // Recalculate interest for the exact remaining principal
            balance_accel = 0; // Loan fully paid
        } else {
            balance_accel -= principal_paid_this_month;
        }

        total_interest_accel += interest_for_month;
        payments_count_accel++;

        if (balance_accel < 0.01) { // Account for floating point inaccuracies
            balance_accel = 0;
        }
    }

    return {
        total_interest_paid_standard: total_interest_std,
        total_payments_standard: total_interest_std + P, // Total payments = original principal + total interest
        time_to_repay_standard_years: payments_count_std / 12,
        total_interest_paid_accelerated: total_interest_accel,
        total_payments_accelerated: total_interest_accel + P,
        time_to_repay_accelerated_years: payments_count_accel / 12
    };
}
