function calculateRepayment(loanPrincipal, weightedInterestRate, annualIncome, householdSize, existingMonthlyPayment, loanType, paymentGoal, expectedIncomeGrowthRate) {
    if (loanPrincipal <= 0) {
        return {
            optimizedStrategy: "Loan Paid Off",
            optimizedMonthlyPayment: 0,
            totalInterestPaid: 0,
            totalAmountRepaid: 0,
            repaymentDurationYears: 0,
            monthlyPaymentSavings: 0,
            debtToIncomeRatio: 0
        };
    }
    const annualRate = weightedInterestRate / 100;
    const monthlyRate = annualRate / 12;

    const AGI_FACTOR = 0.9; // Simplified AGI calculation
    // Federal Poverty Level (FPL) 2023 values (approximate national averages, can vary by state/year)
    const FPL_2023_1PERSON = 14580; 
    const FPL_INCREMENT_PER_PERSON = 5140; 

    const calculateFPL = (size) => FPL_2023_1PERSON + Math.max(0, size - 1) * FPL_INCREMENT_PER_PERSON;

    // Helper to calculate PMT and totals for fixed loans
    const calculateFixedLoan = (principal, monthlyRate, months) => {
        if (principal <= 0 || monthlyRate <= 0 || months <= 0) {
            return { monthlyPayment: 0, totalInterest: 0, totalRepaid: 0, durationMonths: 0 };
        }
        const pmt = principal * monthlyRate / (1 - Math.pow(1 + monthlyRate, -months));
        const totalRepaid = pmt * months;
        const totalInterest = totalRepaid - principal;
        return { monthlyPayment: pmt, totalInterest: totalInterest, totalRepaid: totalRepaid, durationMonths: months };
    };

    let plans = [];

    // 1. Standard 10-Year Plan
    const standardPlan = calculateFixedLoan(loanPrincipal, monthlyRate, 120);
    plans.push({ name: "Standard 10-Year Repayment", ...standardPlan });

    // 2. Extended 25-Year Plan
    const extendedPlan = calculateFixedLoan(loanPrincipal, monthlyRate, 300);
    plans.push({ name: "Extended 25-Year Repayment", ...extendedPlan });

    // 3. Income-Driven Repayment (IDR) Plans - if Federal loans
    if (loanType === "Federal" || loanType === "Mixed") {

        const simulateIDRPlan = (planName, paymentPercentageDI, termYears, capToStandard = false, diMultiplier = 1.5) => {
            let currentPrincipal = loanPrincipal;
            let currentAnnualIncome = annualIncome;
            let totalPaymentsMade = 0;
            let totalInterestPaid = 0;
            let actualMonthsPaid = 0;

            const termMonths = termYears * 12;

            for (let month = 1; month <= termMonths; month++) {
                if (currentPrincipal <= 0) break; // Loan fully paid

                // Recalculate AGI annually
                if ((month - 1) % 12 === 0 && month > 1) {
                    currentAnnualIncome *= (1 + expectedIncomeGrowthRate / 100);
                }

                const currentAGI = currentAnnualIncome * AGI_FACTOR;
                const currentFPL = calculateFPL(householdSize);
                const discretionaryIncome = Math.max(0, currentAGI - (diMultiplier * currentFPL));

                let monthlyIDRPayment = Math.max(0, discretionaryIncome * (paymentPercentageDI / 100) / 12);

                if (capToStandard) {
                    monthlyIDRPayment = Math.min(monthlyIDRPayment, standardPlan.monthlyPayment);
                }

                const monthlyInterestAccrued = currentPrincipal * monthlyRate;

                // Payment application logic
                let interestPaidThisMonth = Math.min(monthlyIDRPayment, monthlyInterestAccrued);
                let principalPaidThisMonth = Math.max(0, monthlyIDRPayment - interestPaidThisMonth);

                currentPrincipal -= principalPaidThisMonth;
                totalInterestPaid += interestPaidThisMonth;
                totalPaymentsMade += monthlyIDRPayment; // This is the total borrower pays
                actualMonthsPaid = month;
            }

            // Forgiveness: If principal remains at end of term, it's forgiven.
            // Total repaid and total interest paid only include what the borrower actually paid.

            const averageMonthlyPayment = actualMonthsPaid > 0 ? (totalPaymentsMade / actualMonthsPaid) : 0;

            return {
                monthlyPayment: averageMonthlyPayment,
                totalInterest: totalInterestPaid,
                totalRepaid: totalPaymentsMade,
                durationMonths: actualMonthsPaid,
            };
        };

        // PAYE (10% discretionary, 20-year term, capped at standard payment, 150% FPL discretionary income factor)
        const payePlan = simulateIDRPlan("PAYE (Pay As You Earn)", 10, 20, true, 1.5);
        plans.push({ name: "PAYE (Pay As You Earn)", ...payePlan });

        // SAVE Plan (formerly REPAYE): 10% discretionary (for undergrad), 20 or 25-year term, NO cap, 225% FPL discretionary income factor, 100% interest subsidy
        // For simplicity, we assume 20-year term for SAVE in this model, could be 25 for grad loans.
        const simulateSAVEPlan = (termYears) => {
            let currentPrincipal = loanPrincipal;
            let currentAnnualIncome = annualIncome;
            let totalPaymentsMade = 0;
            let totalInterestPaid = 0;
            let actualMonthsPaid = 0;

            const termMonths = termYears * 12;

            for (let month = 1; month <= termMonths; month++) {
                if (currentPrincipal <= 0) break; 

                if ((month - 1) % 12 === 0 && month > 1) {
                    currentAnnualIncome *= (1 + expectedIncomeGrowthRate / 100);
                }

                const currentAGI = currentAnnualIncome * AGI_FACTOR;
                const currentFPL = calculateFPL(householdSize);
                const discretionaryIncome = Math.max(0, currentAGI - (2.25 * currentFPL)); // 225% FPL for SAVE

                let monthlySAVEPayment = Math.max(0, discretionaryIncome * (0.10) / 12); // 10% of DI
                if (monthlySAVEPayment < 0.01 && loanPrincipal > 0) monthlySAVEPayment = 0; // If payment effectively zero, set to zero

                const monthlyInterestAccrued = currentPrincipal * monthlyRate;

                // SAVE interest subsidy: Any accrued interest not covered by the payment is subsidized by the government.
                // This means principal will never increase due to unpaid interest.
                let interestPaidThisMonth = Math.min(monthlySAVEPayment, monthlyInterestAccrued);
                let principalPaidThisMonth = Math.max(0, monthlySAVEPayment - interestPaidThisMonth);

                currentPrincipal -= principalPaidThisMonth; // Principal only reduces, never increases.
                totalInterestPaid += interestPaidThisMonth;
                totalPaymentsMade += monthlySAVEPayment;
                actualMonthsPaid = month;
            }

            const averageMonthlyPayment = actualMonthsPaid > 0 ? (totalPaymentsMade / actualMonthsPaid) : 0;
            return {
                monthlyPayment: averageMonthlyPayment,
                totalInterest: totalInterestPaid,
                totalRepaid: totalPaymentsMade,
                durationMonths: actualMonthsPaid,
            };
        };
        const savePlan = simulateSAVEPlan(20); // Assume 20 years for undergrad, general purpose. Could be 25 for grad loans.
        plans.push({ name: "SAVE Plan (formerly REPAYE)", ...savePlan });
    }

    // Filter out plans with infinite values if any calculation resulted in that
    plans = plans.filter(p => p.totalRepaid !== Infinity && p.monthlyPayment !== Infinity && !isNaN(p.totalRepaid));
    if (plans.length === 0) {
         return {
            optimizedStrategy: "N/A",
            optimizedMonthlyPayment: 0,
            totalInterestPaid: 0,
            totalAmountRepaid: 0,
            repaymentDurationYears: 0,
            monthlyPaymentSavings: 0,
            debtToIncomeRatio: 0
        };
    }

    // Determine the best plan based on paymentGoal
    let optimizedPlan = plans[0]; // Initialize with the first valid plan

    if (paymentGoal === "Minimize Total Cost") {
        optimizedPlan = plans.reduce((minPlan, currentPlan) => (currentPlan.totalRepaid < minPlan.totalRepaid) ? currentPlan : minPlan);
    } else if (paymentGoal === "Lowest Initial Monthly Payment" || paymentGoal === "Maximize Income-Driven Flexibility") {
        optimizedPlan = plans.reduce((minPlan, currentPlan) => (currentPlan.monthlyPayment < minPlan.monthlyPayment) ? currentPlan : minPlan);
    } else if (paymentGoal === "Fastest Repayment") {
        // Prioritize shortest duration. If durations are equal, then minimize cost.
        optimizedPlan = plans.reduce((fastestPlan, currentPlan) => {
            if (currentPlan.durationMonths < fastestPlan.durationMonths) return currentPlan;
            if (currentPlan.durationMonths === fastestPlan.durationMonths && currentPlan.totalRepaid < fastestPlan.totalRepaid) return currentPlan;
            return fastestPlan;
        });
    } else {
        // Default to Minimize Total Cost
        optimizedPlan = plans.reduce((minPlan, currentPlan) => (currentPlan.totalRepaid < minPlan.totalRepaid) ? currentPlan : minPlan);
    }

    const comparisonPayment = existingMonthlyPayment > 0 ? existingMonthlyPayment : standardPlan.monthlyPayment;
    const monthlyPaymentSavings = comparisonPayment - optimizedPlan.monthlyPayment;

    // Ensure annualIncome is not zero to avoid division by zero for DTI
    const debtToIncomeRatio = annualIncome > 0 ? ((optimizedPlan.monthlyPayment * 12) / annualIncome * 100) : 0; 

    return {
        optimizedStrategy: optimizedPlan.name,
        optimizedMonthlyPayment: optimizedPlan.monthlyPayment,
        totalInterestPaid: optimizedPlan.totalInterest,
        totalAmountRepaid: optimizedPlan.totalRepaid,
        repaymentDurationYears: optimizedPlan.durationMonths / 12,
        monthlyPaymentSavings: monthlyPaymentSavings,
        debtToIncomeRatio: debtToIncomeRatio
    };
}
return calculateRepayment(loanPrincipal, weightedInterestRate, annualIncome, householdSize, existingMonthlyPayment, loanType, paymentGoal, expectedIncomeGrowthRate);
